import{_ as e,c as i,o as t,b as a}from"./chunks/framework.4MfQJnRv.js";const o=JSON.parse('{"title":"Privacy Policy","description":"Privacy Policy","frontmatter":{"footer":true,"title":"Privacy Policy","description":"Privacy Policy","date":"2025-09-20T00:00:00.000Z","draft":false,"sidebar":false},"headers":[{"level":2,"title":"Overview","slug":"overview","link":"#overview","children":[]},{"level":2,"title":"Data Collection","slug":"data-collection","link":"#data-collection","children":[]},{"level":2,"title":"Cookies and Tracking","slug":"cookies-and-tracking","link":"#cookies-and-tracking","children":[]},{"level":2,"title":"Third-Party Services","slug":"third-party-services","link":"#third-party-services","children":[]},{"level":2,"title":"Server Logs","slug":"server-logs","link":"#server-logs","children":[]},{"level":2,"title":"Local Processing","slug":"local-processing","link":"#local-processing","children":[]},{"level":2,"title":"Data Retention","slug":"data-retention","link":"#data-retention","children":[]},{"level":2,"title":"Your Rights","slug":"your-rights","link":"#your-rights","children":[]},{"level":2,"title":"Security Measures","slug":"security-measures","link":"#security-measures","children":[]},{"level":2,"title":"Legal Compliance","slug":"legal-compliance","link":"#legal-compliance","children":[]},{"level":2,"title":"Changes to This Policy","slug":"changes-to-this-policy","link":"#changes-to-this-policy","children":[]},{"level":2,"title":"Technical Implementation","slug":"technical-implementation","link":"#technical-implementation","children":[]},{"level":2,"title":"Contact Information","slug":"contact-information","link":"#contact-information","children":[]}],"relativePath":"about/author/thanhlv/privacy-policy.md","filePath":"about/author/thanhlv/privacy-policy.md","lastUpdated":1758360146000}');const r=e({name:"about/author/thanhlv/privacy-policy.md"},[["render",function(e,o,r,l,n,s){return t(),i("div",null,o[0]||(o[0]=[a('<h1 id="privacy-policy" tabindex="-1">Privacy Policy <a class="header-anchor" href="#privacy-policy" aria-label="Permalink to &quot;Privacy Policy&quot;">​</a></h1><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>This Privacy Policy describes how this website and extension handles user information and data. Our fundamental principle is simple: we do not collect, store, process, or transmit any personal data to external servers or third parties.</p><h2 id="data-collection" tabindex="-1">Data Collection <a class="header-anchor" href="#data-collection" aria-label="Permalink to &quot;Data Collection&quot;">​</a></h2><p><strong>We collect NO personal data whatsoever.</strong> This includes but is not limited to:</p><ul><li>Names, email addresses, or contact information</li><li>IP addresses or location data</li><li>Device information or browser fingerprints</li><li>Usage patterns or behavioral data</li><li>Any form identification or registration data</li></ul><h2 id="cookies-and-tracking" tabindex="-1">Cookies and Tracking <a class="header-anchor" href="#cookies-and-tracking" aria-label="Permalink to &quot;Cookies and Tracking&quot;">​</a></h2><p><strong>We use NO tracking technologies:</strong></p><ul><li>No cookies are set by this website</li><li>No web beacons or tracking pixels are embedded</li><li>No third-party analytics services (Google Analytics, etc.) are implemented</li><li>No social media tracking scripts are loaded</li><li>No advertising networks or tracking partners are involved</li></ul><h2 id="third-party-services" tabindex="-1">Third-Party Services <a class="header-anchor" href="#third-party-services" aria-label="Permalink to &quot;Third-Party Services&quot;">​</a></h2><p><strong>We do not integrate with external services that collect data:</strong></p><ul><li>No content delivery networks (CDNs) that track users</li><li>No external fonts or resources that could track visitors</li><li>No embedded social media widgets</li><li>No comment systems that require registration</li><li>No newsletter subscriptions or email collection forms</li></ul><h2 id="server-logs" tabindex="-1">Server Logs <a class="header-anchor" href="#server-logs" aria-label="Permalink to &quot;Server Logs&quot;">​</a></h2><p><strong>Our server infrastructure maintains minimal technical logs only for:</strong></p><ul><li>Basic security monitoring (failed access attempts)</li><li>Technical error debugging</li><li>Server performance monitoring</li></ul><p>These logs contain no personally identifiable information and are automatically purged regularly.</p><h2 id="local-processing" tabindex="-1">Local Processing <a class="header-anchor" href="#local-processing" aria-label="Permalink to &quot;Local Processing&quot;">​</a></h2><p><strong>All website functionality operates locally in your browser:</strong></p><ul><li>Page rendering happens client-side</li><li>Any interactive features process data locally</li><li>No form submissions send data to our servers</li><li>Search functionality (if any) works entirely offline</li><li>User preferences are stored only in local browser storage</li></ul><h2 id="data-retention" tabindex="-1">Data Retention <a class="header-anchor" href="#data-retention" aria-label="Permalink to &quot;Data Retention&quot;">​</a></h2><p><strong>Since we collect no data, there is nothing to retain or delete:</strong></p><ul><li>No user accounts or profiles exist</li><li>No personal information is stored</li><li>No usage history is maintained</li><li>No data backup or archival systems contain user information</li></ul><h2 id="your-rights" tabindex="-1">Your Rights <a class="header-anchor" href="#your-rights" aria-label="Permalink to &quot;Your Rights&quot;">​</a></h2><p><strong>Since no personal data is collected, traditional data protection rights do not apply, but we want to be transparent:</strong></p><ul><li>Right to know: This policy explains our no-data-collection approach</li><li>Right to access: There is no data to access</li><li>Right to deletion: There is no data to delete</li><li>Right to portability: There is no data to port</li><li>Right to correction: There is no data to correct</li></ul><h2 id="security-measures" tabindex="-1">Security Measures <a class="header-anchor" href="#security-measures" aria-label="Permalink to &quot;Security Measures&quot;">​</a></h2><p><strong>Despite collecting no data, we maintain security best practices:</strong></p><ul><li>Regular security updates to server software</li><li>Encrypted connections (HTTPS) for all pages</li><li>Secure hosting infrastructure</li><li>Regular security audits and monitoring</li></ul><h2 id="legal-compliance" tabindex="-1">Legal Compliance <a class="header-anchor" href="#legal-compliance" aria-label="Permalink to &quot;Legal Compliance&quot;">​</a></h2><p><strong>This privacy policy complies with major privacy regulations:</strong></p><ul><li><strong>GDPR (General Data Protection Regulation)</strong>: No personal data processing occurs</li><li><strong>CCPA (California Consumer Privacy Act)</strong>: No personal information is collected or sold</li><li><strong>Other regional privacy laws</strong>: Our no-data approach exceeds most regulatory requirements</li></ul><h2 id="changes-to-this-policy" tabindex="-1">Changes to This Policy <a class="header-anchor" href="#changes-to-this-policy" aria-label="Permalink to &quot;Changes to This Policy&quot;">​</a></h2><p><strong>Any changes to this privacy policy will be:</strong></p><ul><li>Posted on this page with updated date</li><li>Highlighted if they affect our core no-data commitment</li><li>Made only to clarify existing practices, not to introduce data collection</li></ul><h2 id="technical-implementation" tabindex="-1">Technical Implementation <a class="header-anchor" href="#technical-implementation" aria-label="Permalink to &quot;Technical Implementation&quot;">​</a></h2><p><strong>How we ensure no data collection:</strong></p><ul><li>Static website hosting with no server-side processing</li><li>No database connections or data storage systems</li><li>No form processing endpoints</li><li>No API calls that transmit user data</li><li>Regular audits of all website code and dependencies</li></ul><h2 id="contact-information" tabindex="-1">Contact Information <a class="header-anchor" href="#contact-information" aria-label="Permalink to &quot;Contact Information&quot;">​</a></h2><p>If you have any questions, concerns, or requests regarding this privacy policy or our data practices, please contact us through the available channels on this website.</p><p><strong>Effective Date:</strong> September 20, 2025<br><strong>Last Updated:</strong> September 20, 2025</p><hr><p><em>This privacy policy reflects our commitment to user privacy through a zero-data-collection approach. We believe the best way to protect your privacy is to never collect your data in the first place.</em></p>',42)]))}]]);export{o as __pageData,r as default};
