import{_ as n,j as c,o as h,v as i}from"./chunks/framework.9xwLhOZU.js";const t=JSON.parse('{"title":"Concurrency và parallelism. Giải thích Concurrency và parallelism của OS và business khác gì nhau ?.","description":"Về mặt lý thuyết, thuật toán và kiến trúc triển khai, có rất nhiều tài liệu trên sách và blog đã đề cập đến hai loại cấu trúc dữ liệu này","frontmatter":{"footer":true,"title":"Concurrency và parallelism. Giải thích Concurrency và parallelism của OS và business khác gì nhau ?.","description":"Về mặt lý thuyết, thuật toán và kiến trúc triển khai, có rất nhiều tài liệu trên sách và blog đã đề cập đến hai loại cấu trúc dữ liệu này","authors":["lethanh"],"date":"2024-01-10T00:00:00.000Z","image":"https://static-cdn.thanhlv.com/blog/images/2024-01-10-concurrency-va-parallelism-giai-thich-concurrency-va-parallelism-cua-os-va-business-khac-gi-nhau/parallelism_1.png","outline":"deep"},"headers":[{"level":2,"title":"Concurrency và Parallelism","slug":"concurrency-va-parallelism","link":"#concurrency-va-parallelism","children":[{"level":3,"title":"Concurrency","slug":"concurrency","link":"#concurrency","children":[{"level":4,"title":"Ví dụ concurrency","slug":"vi-du-concurrency","link":"#vi-du-concurrency","children":[]},{"level":4,"title":"Ví dụ minh họa ngoài đời thực","slug":"vi-du-minh-hoa-ngoai-đoi-thuc","link":"#vi-du-minh-hoa-ngoai-đoi-thuc","children":[]}]},{"level":3,"title":"Parallelism","slug":"parallelism","link":"#parallelism","children":[]}]},{"level":2,"title":"Concurrency kết hợp với parallelism","slug":"concurrency-ket-hop-voi-parallelism","link":"#concurrency-ket-hop-voi-parallelism","children":[{"level":4,"title":"Ví dụ về 2 cô thư ký:","slug":"vi-du-ve-2-co-thu-ky","link":"#vi-du-ve-2-co-thu-ky","children":[]},{"level":3,"title":"Vậy Concurrency và parallelism của OS và Business khác gì nhau","slug":"vay-concurrency-va-parallelism-cua-os-va-business-khac-gi-nhau","link":"#vay-concurrency-va-parallelism-cua-os-va-business-khac-gi-nhau","children":[{"level":4,"title":"Concurrency","slug":"concurrency-1","link":"#concurrency-1","children":[]},{"level":4,"title":"Parallelism","slug":"parallelism-1","link":"#parallelism-1","children":[]}]}]},{"level":2,"title":"Tổng kết","slug":"tong-ket","link":"#tong-ket","children":[]}],"relativePath":"blog/2024-01-10-concurrency-va-parallelism-giai-thich-concurrency-va-parallelism-cua-os-va-business-khac-gi-nhau.md","filePath":"blog/2024-01-10-concurrency-va-parallelism-giai-thich-concurrency-va-parallelism-cua-os-va-business-khac-gi-nhau.md","lastUpdated":1743178056000}'),l={name:"blog/2024-01-10-concurrency-va-parallelism-giai-thich-concurrency-va-parallelism-cua-os-va-business-khac-gi-nhau.md"},a=[i('<h1 id="2024-01-10-concurrency-va-parallelism-giai-thich-concurrency-va-parallelism-cua-os-va-business-khac-gi-nhau" tabindex="-1">Concurrency và parallelism. Giải thích Concurrency và parallelism của OS và business khác gì nhau ? <a class="header-anchor" href="#2024-01-10-concurrency-va-parallelism-giai-thich-concurrency-va-parallelism-cua-os-va-business-khac-gi-nhau" aria-label="Permalink to &quot;Concurrency và parallelism. Giải thích Concurrency và parallelism của OS và business khác gì nhau ? {#2024-01-10-concurrency-va-parallelism-giai-thich-concurrency-va-parallelism-cua-os-va-business-khac-gi-nhau}&quot;">​</a></h1><p>Concurrency và parallelism là hai khái niệm quan trọng trong lĩnh vực lập trình và công nghệ thông tin. Mặc dù nhiều người đã nghe về chúng và có thể đã làm việc với chúng, nhưng không phải ai cũng hiểu rõ về ý nghĩa cụ thể của Concurrency và Parallelism.</p><p>Trong bài viết này, tôi sẽ giải thích ý nghĩa của Concurrency và Parallelism trong hệ điều hành nói chung và trong lĩnh vực kinh doanh.</p><p>Tuy nhiên, cần lưu ý rằng ý nghĩa của chúng có thể thay đổi tùy thuộc vào ngữ cảnh cụ thể, nhưng bản chất ý tưởng vẫn giữ nguyên.</p><p>Hãy cùng bắt đầu!</p><nav class="table-of-contents"><ul><li><a href="#concurrency-va-parallelism">Concurrency và Parallelism</a><ul><li><a href="#concurrency">Concurrency</a><ul><li><a href="#vi-du-concurrency">Ví dụ concurrency</a></li><li><a href="#vi-du-minh-hoa-ngoai-đoi-thuc">Ví dụ minh họa ngoài đời thực</a></li></ul></li><li><a href="#parallelism">Parallelism</a></li></ul></li><li><a href="#concurrency-ket-hop-voi-parallelism">Concurrency kết hợp với parallelism</a><ul><li><a href="#vi-du-ve-2-co-thu-ky">Ví dụ về 2 cô thư ký:</a></li><li><a href="#vay-concurrency-va-parallelism-cua-os-va-business-khac-gi-nhau">Vậy Concurrency và parallelism của OS và Business khác gì nhau</a><ul><li><a href="#concurrency-1">Concurrency</a></li><li><a href="#parallelism-1">Parallelism</a></li></ul></li></ul></li><li><a href="#tong-ket">Tổng kết</a></li></ul></nav><h2 id="concurrency-va-parallelism" tabindex="-1">Concurrency và Parallelism <a class="header-anchor" href="#concurrency-va-parallelism" aria-label="Permalink to &quot;Concurrency và Parallelism {#concurrency-va-parallelism}&quot;">​</a></h2><h3 id="concurrency" tabindex="-1">Concurrency <a class="header-anchor" href="#concurrency" aria-label="Permalink to &quot;Concurrency&quot;">​</a></h3><p>Concurrency, hay <strong>Đồng thời</strong> khi dịch sang tiếng Việt, là một khái niệm liên quan đến việc xử lý nhiều tác vụ cùng một lúc trên hệ điều hành.</p><p>Trong quá khứ, khi công nghệ chưa phát triển, chúng ta chỉ có thể chạy một ứng dụng tại một thời điểm. Để chạy ứng dụng khác, người dùng phải tắt ứng dụng hiện tại và mở ứng dụng mới.</p><p>Khi mở các ứng dụng, chúng sẽ chạy trên một Core CPU (trong thời kỳ trước đây chỉ có một Core). Mỗi ứng dụng khi được mở sẽ được CPU xử lý các logic liên quan đến ứng dụng đó, và CPU sẽ tập trung chỉ vào việc xử lý cho ứng dụng đó mà thôi.</p><p><img src="https://static-cdn.thanhlv.com/blog/images/2024-01-10-concurrency-va-parallelism-giai-thich-concurrency-va-parallelism-cua-os-va-business-khac-gi-nhau/concurrency.png" alt="Concurrency"></p><p>Sau đó, với sự phát triển của công nghệ, tại một thời điểm chúng ta có khả năng chạy nhiều ứng dụng trên cùng một máy tính.</p><p>Tuy nhiên, ở giai đoạn này, mặc dù có thể chạy nhiều ứng dụng, máy tính vẫn chỉ sử dụng một Core CPU để xử lý. Một Core đơn sẽ đảm nhận việc xử lý logic cho nhiều ứng dụng khác nhau.</p><p>??? Nhưng tại sao nó lại làm được ?</p><p>Tính năng <strong>Concurrency</strong> giúp CPU thực hiện công việc này. Thay vì một Core của CPU chỉ xử lý một ứng dụng duy nhất, tính năng Concurrency cho phép Core chuyển đổi giữa việc xử lý các ứng dụng khác nhau liên tục. <img src="https://static-cdn.thanhlv.com/blog/images/2024-01-10-concurrency-va-parallelism-giai-thich-concurrency-va-parallelism-cua-os-va-business-khac-gi-nhau/concurrency_2.png" alt="Concurrency2"></p><h4 id="vi-du-concurrency" tabindex="-1">Ví dụ concurrency <a class="header-anchor" href="#vi-du-concurrency" aria-label="Permalink to &quot;Ví dụ concurrency&quot;">​</a></h4><ol><li>Giây thứ 1 : Bắt đầu, Core xử lý logic cho thread A1</li><li>Giây thứ 2 : Core Switching đến xử lý logic cho thread B1</li><li>Giây thứ 3 : Core Switching đến xử lý logic cho thread A2</li><li>Giây thứ 4: Core Switching đến xử lý logic cho thread B2</li><li>Giây thứ 5: Core Switching đến xử lý logic cho thread A3</li><li>Giây thứ 6: Core Switching đến xử lý logic cho thread B3</li><li>Giây thứ 7: Core Switching đến xử lý logic cho thread A1</li><li>Giây thứ 8: Core Switching đến xử lý logic cho thread A2</li><li>Giây thứ 9: Core Switching đến xử lý logic cho thread B1</li></ol><p>Quá trình chuyển đổi này được gọi là <strong>Switching context</strong>. <br> Mỗi khi chuyển đến một context thread khác để xử lý logic, sẽ có một khoảng thời gian nhỏ được tiêu tốn. <br> Độ trễ của quá trình chuyển đổi phụ thuộc vào nhiều yếu tố, thường dao động từ 50 đến 100 nanoseconds (1 giây = 1 tỷ nanoseconds).</p><p>Quá trình chuyển đổi giữa các thread và sau đó xử lý logic cho các thread đó diễn ra rất nhanh, với độ trễ cho việc chuyển đổi và xử lý một phần logic cho thread thường nằm trong khoảng từ 500 đến 1000 nanoseconds. (Lưu ý rằng không phải lúc nào cũng xử lý hết logic mà chỉ xử lý một phần trước khi chuyển sang thread khác).</p><p>Với phương thức làm việc thông qua quá trình chuyển đổi context, chúng ta có cảm giác như CPU đang thực hiện các công việc song song. Do đó, nó được gọi là <strong>Concurrency</strong> (Đồng thời).</p><h4 id="vi-du-minh-hoa-ngoai-đoi-thuc" tabindex="-1">Ví dụ minh họa ngoài đời thực <a class="header-anchor" href="#vi-du-minh-hoa-ngoai-đoi-thuc" aria-label="Permalink to &quot;Ví dụ minh họa ngoài đời thực&quot;">​</a></h4><p>Ví dụ minh họa trong thực tế:</p><p>Thông tin ví dụ:</p><p>Một cô thư ký giám đốc có nhiều nhiệm vụ, bao gồm việc nghe điện thoại cho giám đốc, kiểm tra các cuộc hẹn, nhắn tin cho khách hàng, và nhiều công việc khác. <img src="https://static-cdn.thanhlv.com/blog/images/2024-01-10-concurrency-va-parallelism-giai-thich-concurrency-va-parallelism-cua-os-va-business-khac-gi-nhau/concurrency_3.png" alt="Concurrency3"></p><ol><li>Giây thứ 1: Cô thư ký bắt đầu nghe điện thoại của khách hàng.</li><li>Giây thứ 2: Cô thư ký tạm dừng nghe điện thoại để kiểm tra cuộc hẹn.</li><li>Giây thứ 3: Cô thư ký dừng lại kiểm tra cuộc hẹn để nhắn tin cho khách hàng.</li><li>Giây thứ 4: Cô thư ký tạm dừng nhắn tin để tiếp tục nghe điện thoại.</li><li>Giây thứ 5: Cô thư ký kết thúc việc nghe điện thoại và tiếp tục kiểm tra cuộc hẹn.</li><li>Giây thứ 6: Cô thư ký tạm dừng kiểm tra cuộc hẹn để nhắn tin cho khách hàng.</li><li>Giây thứ 100: Cô thư ký kết thúc công việc nghe điện thoại.</li><li>Giây thứ 101: Cô thư ký tiếp tục kiểm tra cuộc hẹn.</li><li>Giây thứ 102: Cô thư ký tạm dừng kiểm tra cuộc hẹn và tiếp tục nhắn tin cho khách hàng.</li><li>Giây thứ 103: Cô thư ký kết thúc kiểm tra cuộc hẹn.</li><li>Giây thứ 104: Cô thư ký tiếp tục nhắn tin cho khách hàng.</li><li>...và cứ như vậy.</li></ol><p>Chỉ có một cô thư ký, và cô ấy sẽ thực hiện mỗi công việc một chút ít cho đến khi hoàn thành tất cả các nhiệm vụ. Nếu cô ấy phải đối mặt với nhiều công việc cùng một lúc, thì thời gian xử lý mỗi công việc sẽ kéo dài. Ngược lại, nếu cô ấy chỉ có một công việc duy nhất, thì thời gian xử lý công việc đó sẽ nhanh hơn.</p><p>Các cánh tay ảo được minh họa như việc cô thư ký cần tạm dừng công việc hiện tại để chuyển sang thực hiện một công việc khác tạm thời.</p><p>Ví dụ: Trong trường hợp công việc nhắn tin, nếu cô ấy đang thực hiện đồng thời (Concurrency) 10 công việc khác nhau, thì để hoàn thành công việc nhắn tin có thể mất 10 giây.</p><p>Tuy nhiên, nếu chỉ tập trung vào công việc nhắn tin đó một cách độc lập, cô ấy có thể chỉ mất 2 giây.</p><p>Concurrency giống như việc có một core hoặc một cô thư ký, và cần chuyển đổi giữa các công việc liên tục để đồng thời thực hiện nhiều công việc cùng một lúc.</p><h3 id="parallelism" tabindex="-1">Parallelism <a class="header-anchor" href="#parallelism" aria-label="Permalink to &quot;Parallelism&quot;">​</a></h3><p>Parallelism, hay <strong>Song Song</strong> khi dịch sang tiếng Việt, là một khái niệm liên quan đến việc xử lý nhiều công việc đồng thời trên hệ điều hành.</p><p>Trong quá khứ, chúng ta chỉ có một Core hoặc một cô thư ký duy nhất, và mỗi Core hoặc cô thư ký đó sẽ phải thực hiện nhiều công việc đồng thời.</p><p>Với sự phát triển của công nghệ, người ta có khả năng tích hợp nhiều Core hơn hoặc nhiều Thread(<a href="./2024-01-09-cpu-core-and-thread-khac-gi-nhau.html"><strong>Logical core</strong></a>) trên một Core, thậm chí tích hợp nhiều task trên một CPU hoặc kết hợp nhiều CPU trên một mainboard. Khi đó, khái niệm Parallelism đã xuất hiện.</p><p>Thay vì một thread trong một core phải xử lý toàn bộ công việc, khi có nhiều thread trong một core hoặc nhiều core hơn, các công việc sẽ được phân chia và chia nhẹ ra cho các thread, core khác nhau để cùng xử lý.</p><p><img src="https://static-cdn.thanhlv.com/blog/images/2024-01-10-concurrency-va-parallelism-giai-thich-concurrency-va-parallelism-cua-os-va-business-khac-gi-nhau/parallelism_1.png" alt="Parallelism_1"></p><p>Như trong hình ảnh ở trên, khi chúng ta có 2 công việc, mỗi công việc sẽ được giao cho một thread hoặc core để xử lý. Điều này giúp chúng ta có khả năng xử lý hai công việc song song.</p><p>Áp dụng lại ví dụ với cô thư ký:</p><ol><li>Giây thứ 1 : Thư ký 1 : Nghe điện thoại</li><li>Giây thứ 1 : Thư ký 2 : kiểm tra lịch hẹn</li><li>Giây thứ 2 : Thư ký 1 : Nghe điện thoại kết thúc</li><li>Giây thứ 2: Thư ký 2 : kiểm tra cuộc hẹn kết thúc</li><li>...</li></ol><p>Tương tự như vậy, các cô thư ký có thể song song xử lý nhiều công việc khác nhau, giống như việc chia công việc cho các thread,core để xử lý song song.</p><p>Parallelism đơn giản vậy thôi. Trước đây, chỉ có một thread trong một core xử lý một công việc tại một thời điểm. Nhưng bây giờ, chúng ta có thể sử dụng nhiều thread trong một core hoặc nhiều core để xử lý các công việc song song.</p><h2 id="concurrency-ket-hop-voi-parallelism" tabindex="-1">Concurrency kết hợp với parallelism <a class="header-anchor" href="#concurrency-ket-hop-voi-parallelism" aria-label="Permalink to &quot;Concurrency kết hợp với parallelism&quot;">​</a></h2><p><img src="https://static-cdn.thanhlv.com/blog/images/2024-01-10-concurrency-va-parallelism-giai-thich-concurrency-va-parallelism-cua-os-va-business-khac-gi-nhau/concurrency_combined_with_parallelism.png" alt="Parallelism_1"></p><p>Sẽ trông giống như trên hình vậy. Trong các thread trong core hoặc trong các core, công việc vẫn được xử lý đồng thời và các nhiệm vụ sẽ được chia sẻ giữa các thread hoặc core khác nhau để cùng thực hiện.</p><ul><li>Mỗi thư ký sẽ xử lý đồng thời nhiều công việc.</li><li>Nhiều cô thư ký để xử lý song song nhiều công việc khác nhau.</li></ul><h4 id="vi-du-ve-2-co-thu-ky" tabindex="-1">Ví dụ về 2 cô thư ký: <a class="header-anchor" href="#vi-du-ve-2-co-thu-ky" aria-label="Permalink to &quot;Ví dụ về 2 cô thư ký:&quot;">​</a></h4><p><img src="https://static-cdn.thanhlv.com/blog/images/2024-01-10-concurrency-va-parallelism-giai-thich-concurrency-va-parallelism-cua-os-va-business-khac-gi-nhau/concurrency_combined_with_parallelism.png" alt="Parallelism_1"></p><p>Có 2 cô thư ký và 4 công việc: Kiểm tra lịch hẹn, nghe điện thoại, nhắn tin cho khách hàng, và pha cafe cho sếp.</p><p>Cô thư ký 1 sẽ đảm nghiệm kiểm tra lịch hẹn và nghe điện thoại.</p><p>Cô thư ký 2 sẽ đảm nghiệm nhắn tin cho khách hàng và pha cafe cho sếp.</p><p>Flow minh họa:</p><ol><li>Giây thứ 1 : Thư ký 1 kiểm tra lịch hẹn</li><li>Giây thứ 1 : Thư ký 2 nhắn tin cho khách hàng</li><li>Giây thứ 2: Thư ký 1 tạm dừng kiểm tra lịch để chuyển sang nghe điện thoại</li><li>Giây thứ 2: Thư ký 2 tạm dừng nhắn tin cho khách hàng để đi pha cafe</li><li>Giây thứ 3: Thư ký 1 tạm dừng nghe điện thoại để kiểm tra lịch</li><li>Giây thứ 3: Thư ký 2 tạm từng đi pha cafe để nhắn tin cho khách hàng</li><li>Giây thứ 4: Thư ký 1 tạm dừng kiểm tra lịch để nghe điện thoại</li><li>Giây thứ 4: Thư ký 2 tạm từng nhắn tin cho khách hàng để pha cafe</li><li>Giây thứ 5: Thư ký 1 nghe điện thoại kết thúc.</li><li>Giây thứ 5: Thư ký 2 pha cafe kết thúc</li><li>Giây thứ 6: Thư ký 1 kiểm tra lịch hẹn</li><li>Giây thứ 6: Thư ký 2 nhắn tin cho khách hàng</li><li>Giây thứ 7: Thư ký 1 tiếp tục kiểm tra lịch hẹn</li><li>Giây thứ 8: Thư ký 2 tiếp tục nhắn tin cho khách hàng</li><li>Giây thứ 9: Thư ký 1 kiêm tra lịch hẹn kết thúc</li><li>Giây thứ 10: Thư ký 2 tiếp tục nhắn tin cho khách hàng</li><li>Giây thứ 11: Thư ký 2 tiếp tục nhắn tin cho khách hàng</li><li>Giây thứ 12: Thư ký 2 thúc công việc</li></ol><p>Đúng vậy, để tăng tốc độ xử lý, các công việc có thể được chia đều cho cả hai cô thư ký, mỗi cô ôm trách nhiệm về hai công việc. Điều này giúp cả hai cô thư ký có thể xử lý đồng thời hai công việc, từ đó tăng khả năng hoàn thành nhiệm vụ một cách hiệu quả.</p><h3 id="vay-concurrency-va-parallelism-cua-os-va-business-khac-gi-nhau" tabindex="-1">Vậy Concurrency và parallelism của OS và Business khác gì nhau <a class="header-anchor" href="#vay-concurrency-va-parallelism-cua-os-va-business-khac-gi-nhau" aria-label="Permalink to &quot;Vậy Concurrency và parallelism của OS và Business khác gì nhau&quot;">​</a></h3><ul><li><p><strong>OS</strong> : Như mình nói ở phía trên Concurrency và parallelism trong OS nói đến cách thức CPU xử lý logic để có thể xử lý đồng thời hoặc Song Song các công việc.</p></li><li><p><strong>Business</strong> : Với Business thì sẽ cũng mang ý tưởng xử lý giống OS, nhưng nó sẽ mang vào ứng dụng phần mềm, chúng ta sẽ xử lý các task trong phần mềm 1 cách đồng thời hoặc song song.</p></li></ul><h4 id="concurrency-1" tabindex="-1">Concurrency <a class="header-anchor" href="#concurrency-1" aria-label="Permalink to &quot;Concurrency&quot;">​</a></h4><p>Concurrency nói đến các logic cần phải xử lý đồng thời nhưng cần phải bảo đảm được tính đúng đắn của dữ liệu.</p><ul><li>Ví dụ trong vấn đề order chỗ ngồi. 2 khách hàng lên ứng dụng cùng đặt một chỗ ngồi. Khi đó chúng ta cần phải cho ứng dụng xử lý đồng thời 2 order của người dùng cho chỗ ngồi, nhưng vẫn sẽ cần kiểm tra nếu 2 người đó cùng đặt 1 vị trí thì sẽ báo lỗi đây chính là vấn đề Concurrency Business cần xử lý.</li><li>Tính đồng thời vẫn được sử dụng để tăng hiệu suất. Chúng ta không thể bắt user 2 chờ user 1 đặt xong thì mới cho user 2 đặt. <ul><li>Có 1 số cách để giải quyết ví dụ: <ul><li>Chọn thời điểm kiểm tra trùng ghế có thể tại thời điểm thanh toán. Tại thời điểm thanh toán thì vị trí chỗ ngồi sẽ được lock khi có một giao dịch đang xảy ra.</li></ul></li></ul></li><li>Một ví dụ khác trong việc đặt lịch hẹn với bác sĩ online. Nhiều người cùng vào ứng dụng để đặt lịch hẹn. Ứng dụng sẽ đồng thời xử lý tất cả yêu cầu của người dùng cho việc đặt lịch hẹn với bác sĩ. Tuy nhiên, nếu có 2 người dùng lên lịch đặt trùng giờ. Chúng ta sẽ cần báo cho 1 hoặc cả 2 khách hàng biết.</li></ul><p>Concurrency trong Business nói đến vấn đề xử lý đồng thời các task , order, tuy nhiên vẫn cần đảm bảo tính đúng đắn của logic dữ liệu.</p><h4 id="parallelism-1" tabindex="-1">Parallelism <a class="header-anchor" href="#parallelism-1" aria-label="Permalink to &quot;Parallelism&quot;">​</a></h4><p>Parallelism theo mình tìm hiểu thì nó nói đến vấn đề chạy song song nhiều task vụ business độc lập dữ liệu với nhau. Hoặc sẽ liên quan đến nhau ở một task cha to hơn để xác định là thành công hay thất bại. Còn các task nhỏ sẽ chạy song song để tăng hiệu năng.</p><p>Ví dụ: Ví dụ trong trong việc lên lịch một cuộc họp có nhiều người vào thứ 2.</p><p>Người quản trị sẽ gửi tin nhắn song song cùng lúc đến nhiều người để hỏi là “Thứ 2 giám đốc muốn họp cổ đông, bạn có thể đến hôm đó hay không “ và chờ sự phản hồi có đi được hay không.</p><ul><li>Người quản trị nhận lại được tất cả các response từ các người có quyền tham dự một cách song song, trong khi chờ response, quản trị viên có thể lên kế hoạch về buổi họp…..</li><li>Sau khi nhận được hết response người quản trị gửi lại thông tin đến giám đốc sau đó giám đốc đánh giá để cho thể họp vào thứ 2 hay không.</li><li>Việc gửi tin nhắn và chờ response có thể làm song song, bạn không cần chờ người 1 trả lời xong mới đến người 2. Việc lên kế hoạch cho buổi họp cũng có thể làm song song mà không cần phải chờ kết quả là có thật sự hợp hay không.</li></ul><p>Parallelism sẽ khác với Concurrency trong business đó là mặc dù 2 công việc vẫn cần xử lý đồng thời, nhưng Concurrency sẽ cần kiểm tra 1 số logic để đảm bảo không bị conflict còn parallelism thì sẽ không cần.</p><p>Như vậy mình đã giải thích cho mọi người về Concurrency và parallelism. Xử lý Concurrency và parallelism của OS và business khác gì nhau.</p><p>Thanks.</p><h2 id="tong-ket" tabindex="-1">Tổng kết <a class="header-anchor" href="#tong-ket" aria-label="Permalink to &quot;Tổng kết&quot;">​</a></h2><ol><li>Concurrency, hay <strong>Đồng thời</strong> khi dịch sang tiếng Việt, là một khái niệm liên quan đến việc xử lý nhiều tác vụ cùng một lúc trên hệ điều hành kể cả bạn chỉ có duy nhất một core.</li><li>Tính năng <strong>Concurrency</strong> giúp một core của CPU có thể thực hiện nhiều công việc. Thay vì một Core của CPU chỉ xử lý một ứng dụng duy nhất, tính năng Concurrency cho phép Core chuyển đổi giữa việc xử lý các ứng dụng khác nhau liên tục.</li><li>Parallelism, hay <strong>Song Song</strong> khi dịch sang tiếng Việt, là một khái niệm liên quan đến việc xử lý nhiều công việc đồng thời trên hệ điều hành.</li><li>Thay vì một thread trong một core phải xử lý toàn bộ công việc, khi có nhiều thread trong một core hoặc nhiều core hơn, các công việc sẽ được phân chia và chia nhẹ ra cho các thread, core khác nhau để cùng xử lý.</li></ol>',70)];const r=n(l,[["render",function(n,i,t,l,r,g){return h(),c("div",null,a)}]]);export{t as __pageData,r as default};
