import{_ as t,j as n,o as h,v as i}from"./chunks/framework.9xwLhOZU.js";const c=JSON.parse('{"title":"Sự khác biệt giữa Design Pattern và Software Architecture.","description":"Design Pattern tập trung vào giải pháp tái sử dụng cho các vấn đề thiết kế cụ thể ở mức độ function, class và project. Software Architecture định nghĩa cấu trúc tổng thể của hệ thống, từ mức độ project đến solution và system.","frontmatter":{"footer":true,"title":"Sự khác biệt giữa Design Pattern và Software Architecture.","description":"Design Pattern tập trung vào giải pháp tái sử dụng cho các vấn đề thiết kế cụ thể ở mức độ function, class và project. Software Architecture định nghĩa cấu trúc tổng thể của hệ thống, từ mức độ project đến solution và system.","authors":["lethanh"],"date":"2024-02-29T00:00:00.000Z","outline":"deep","image":"/assets/1.tgNQEE1P.jpg","draft":false},"headers":[{"level":2,"title":"Level và Scope trong phát triển phần mềm","slug":"level-va-scope-trong-phat-trien-phan-mem","link":"#level-va-scope-trong-phat-trien-phan-mem","children":[]},{"level":2,"title":"So sánh sự khác biệt giữa Design Pattern và Software Architecture trong Level và Scope","slug":"so-sanh-su-khac-biet-giua-design-pattern-va-software-architecture-trong-level-va-scope","link":"#so-sanh-su-khac-biet-giua-design-pattern-va-software-architecture-trong-level-va-scope","children":[{"level":3,"title":"1. Design Pattern","slug":"_1-design-pattern","link":"#_1-design-pattern","children":[]},{"level":3,"title":"2. Software Architecture","slug":"_2-software-architecture","link":"#_2-software-architecture","children":[]}]},{"level":2,"title":"Điểm giống nhau giữa Design Pattern và Software Architecture","slug":"điem-giong-nhau-giua-design-pattern-va-software-architecture","link":"#điem-giong-nhau-giua-design-pattern-va-software-architecture","children":[]},{"level":2,"title":"Bảng so sánh khác nhau giữa Design Pattern và Software Architecture","slug":"bang-so-sanh-khac-nhau-giua-design-pattern-va-software-architecture","link":"#bang-so-sanh-khac-nhau-giua-design-pattern-va-software-architecture","children":[{"level":3,"title":"Sự Khác Biệt Chính","slug":"su-khac-biet-chinh","link":"#su-khac-biet-chinh","children":[]}]},{"level":2,"title":"Tổng kết","slug":"tong-ket","link":"#tong-ket","children":[]}],"relativePath":"blog/2024-02-29-su-khac-biet-giua-design-patternsva-architechture.md","filePath":"blog/2024-02-29-su-khac-biet-giua-design-patternsva-architechture.md","lastUpdated":1738743510000}'),e={name:"blog/2024-02-29-su-khac-biet-giua-design-patternsva-architechture.md"},a=[i('<h1 id="su-khac-biet-giua-design-pattern-va-software-architecture" tabindex="-1">Sự khác biệt giữa Design Pattern và Software Architecture. <a class="header-anchor" href="#su-khac-biet-giua-design-pattern-va-software-architecture" aria-label="Permalink to &quot;Sự khác biệt giữa Design Pattern và Software Architecture.&quot;">​</a></h1><p>Chào mọi người,</p><p>Trong 2 bài trước đây mình đã giải thích cơ bản khái niệm, ưu và nhược điểm của <a href="./2024-01-31-tim-hieu-ve-design-pattern.html">Design Pattern</a> và <a href="./2024-02-01-tim-hieu-ve-architecture.html">Software Architecture</a>. <br> Trong bài viết nay mình sẽ thực hiện so sách sự khác biệt giữa <a href="./2024-01-31-tim-hieu-ve-design-pattern.html">Design Pattern</a> và <a href="./2024-02-01-tim-hieu-ve-architecture.html">Software Architecture</a>.</p><nav class="table-of-contents"><ul><li><a href="#level-va-scope-trong-phat-trien-phan-mem">Level và Scope trong phát triển phần mềm</a></li><li><a href="#so-sanh-su-khac-biet-giua-design-pattern-va-software-architecture-trong-level-va-scope">So sánh sự khác biệt giữa Design Pattern và Software Architecture trong Level và Scope</a><ul><li><a href="#_1-design-pattern">1. Design Pattern</a></li><li><a href="#_2-software-architecture">2. Software Architecture</a></li></ul></li><li><a href="#điem-giong-nhau-giua-design-pattern-va-software-architecture">Điểm giống nhau giữa Design Pattern và Software Architecture</a></li><li><a href="#bang-so-sanh-khac-nhau-giua-design-pattern-va-software-architecture">Bảng so sánh khác nhau giữa Design Pattern và Software Architecture</a><ul><li><a href="#su-khac-biet-chinh">Sự Khác Biệt Chính</a></li></ul></li><li><a href="#tong-ket">Tổng kết</a></li></ul></nav><h2 id="level-va-scope-trong-phat-trien-phan-mem" tabindex="-1">Level và Scope trong phát triển phần mềm <a class="header-anchor" href="#level-va-scope-trong-phat-trien-phan-mem" aria-label="Permalink to &quot;Level và Scope trong phát triển phần mềm&quot;">​</a></h2><p>Đối với tôi, thông thường một hệ thống phục vụ khách hàng sẽ có 5 Level và Scope chính.</p><ol><li><p><strong>Function- implementation of a function(Method)</strong><br> Đây là đơn vị cơ bản nhất của code, thực hiện một tác vụ cụ thể. Một function được định nghĩa trong một ngữ cảnh lập trình để thực hiện một phần nhỏ của nhiệm vụ lớn hơn.</p><ul><li>Ví dụ tạo một Function có đầu vào là A và B. Kết quả sẽ trả về tổng của A và B.</li></ul></li><li><p><strong>Class- implementation of a class (Lớp)</strong><br> Một class là một khuôn mẫu cho các đối tượng, định nghĩa ra các thuộc tính (dữ liệu) và phương thức (hàm) mà đối tượng của class đó sẽ có. Một class là cấu trúc cơ bản trong lập trình hướng đối tượng.</p><ul><li>Ví dụ: Tạo một class chưa các thông tin về con người(Tên, năm sinh, giới tính) và có các phương thức để hành động( Đi, ăn, ngồi, nói...)</li></ul></li><li><p><strong>Project- relations between classes</strong> Một dự án phần mềm thường bao gồm nhiều class và files, và quản lý mối quan hệ giữa chúng. Trong một dự án, các class tương tác với nhau thông qua các phương thức và thuộc tính, và cùng nhau tạo nên chức năng của ứng dụng.</p><ul><li>Ví dụ: Class TinNhan có phương thức send và class TinNhanSMS có phương thức send và class TinNhanEmail có phương thức send.</li><li>Class TinNhan tùy logic sẽ gọi đến phương thức send của TinNhanSMS hoặc TinNhanEmail để thực hiện gửi tin nhắn đến người dùng.</li></ul></li><li><p><strong>Solution- relations between projects</strong> Trong nhiều trường hợp, một giải pháp phần mềm có thể bao gồm nhiều dự án liên quan đến nhau, như các thư viện, ứng dụng con, và dịch vụ. Mỗi dự án có thể độc lập nhưng khi kết hợp lại tạo nên một hệ thống hoàn chỉnh.</p><ul><li>Ví dụ về giải pháp quản lý người dùng gồm AuthenProject và UserProject.</li><li>Khi muốn tạo token cho người dùng, AuthenProject sẽ thực hiện gọi đến UserProject để xác thực thông tin người dùng, sau đó AuthenProject sẽ thực hiện tạo token.</li></ul></li><li><p><strong>System (Hệ thống)</strong> Một hệ thống phần mềm có thể bao gồm nhiều giải pháp và dự án, cùng với các hệ thống bên ngoài hoặc bên thứ ba. Hệ thống là mức độ cao nhất của tổ chức, nơi mà các giải pháp khác nhau tương tác và làm việc cùng nhau để đáp ứng yêu cầu kinh doanh tổng thể.</p><ul><li>Ví dụ: Hệ thống mua hàng gồm SanPhamSolution, OrderSolution, UserSolution và PaymentSolution</li><li>Khi muốn thanh toán OrderSolution sẽ gọi đến SanPhamSolution và PaymentSolution để lấy thông tin sản phẩm và thanh toán. PaymentSolution sẽ gọi đến UserSolution để kiểm tra thông tin người dùng, voucher...etc..</li></ul></li></ol><h2 id="so-sanh-su-khac-biet-giua-design-pattern-va-software-architecture-trong-level-va-scope" tabindex="-1">So sánh sự khác biệt giữa Design Pattern và Software Architecture trong Level và Scope <a class="header-anchor" href="#so-sanh-su-khac-biet-giua-design-pattern-va-software-architecture-trong-level-va-scope" aria-label="Permalink to &quot;So sánh sự khác biệt giữa Design Pattern và Software Architecture trong Level và Scope&quot;">​</a></h2><h3 id="_1-design-pattern" tabindex="-1">1. Design Pattern <a class="header-anchor" href="#_1-design-pattern" aria-label="Permalink to &quot;1. Design Pattern&quot;">​</a></h3><p><img src="/assets/1.tgNQEE1P.jpg" alt="img"></p><ul><li><p><a href="./2024-01-31-tim-hieu-ve-design-pattern.html">Design Pattern</a> sẽ target <code>Function</code>, <code>Class</code> và <code>Project</code>. Với <a href="./2024-01-31-tim-hieu-ve-design-pattern.html">Design Pattern</a> sẽ thực hiện đi sâu vào chi tiết của một <code>Project</code>.</p></li><li><p><strong>Ở mức độ Function và Class</strong> <a href="./2024-01-31-tim-hieu-ve-design-pattern.html">Design Pattern</a> cung cấp các giải pháp tái sử dụng cho các vấn đề thiết kế thường gặp ở mức độ này, như cách tạo đối tượng (Factory Pattern), cách tổ chức mã nguồn, hoặc cách các đối tượng tương tác với nhau (Observer Pattern..etc..).</p></li><li><p><strong>Ở mức độ Project</strong> <a href="./2024-01-31-tim-hieu-ve-design-pattern.html">Design Pattern</a> giúp định hình cách các class trong một dự án tương tác với nhau, nhưng không định nghĩa cấu trúc tổng thể của dự án.</p></li></ul><h3 id="_2-software-architecture" tabindex="-1">2. Software Architecture <a class="header-anchor" href="#_2-software-architecture" aria-label="Permalink to &quot;2. Software Architecture&quot;">​</a></h3><p><img src="/assets/1.U2POy9T8.png" alt="img"></p><ul><li><p><a href="./2024-02-01-tim-hieu-ve-architecture.html">Software Architecture</a> sẽ target <code>Project</code>, <code>Solution</code> và <code>System</code>.</p></li><li><p><strong>Ở mức độ Project trong <a href="./2024-02-01-tim-hieu-ve-architecture.html">Software Architecture</a></strong> sẽ định nghĩa cấu trúc tổng thể của một dự án cụ thể, bao gồm cách các class và modules được tổ chức và tương tác với nhau. Kiến trúc sư phần mềm sẽ quyết định sử dụng các mô hình như n-tier, clean architecture, hoặc các mô hình khác để đảm bảo rằng dự án có thể mở rộng, bảo trì và thử nghiệm dễ dàng.</p></li><li><p><strong>Ở mức độ Solution trong <a href="./2024-02-01-tim-hieu-ve-architecture.html">Software Architecture</a></strong>, Software Architecture định nghĩa cách thức các dự án khác nhau (ví dụ: các microservices, thư viện chia sẻ, hoặc các ứng dụng con) được tổ chức và làm việc cùng nhau. Kiến trúc sư phần mềm sẽ xác định các giao diện, hợp đồng(Contract về dữ liệu giao tiếp), và giao thức giao tiếp giữa các dự án để đảm bảo tích hợp và hoạt động mượt mà.</p></li><li><p><strong>Ở mức độ System trong <a href="./2024-02-01-tim-hieu-ve-architecture.html">Software Architecture</a></strong> khi nói đến toàn bộ hệ thống, Software Architecture xem xét cách thức các giải pháp khác nhau tương tác và hợp tác để tạo nên một hệ thống lớn, phức tạp. Điều này bao gồm việc quản lý các mối quan hệ giữa các hệ thống con, các dịch vụ bên ngoài, và các hệ thống bên thứ ba. Kiến trúc sư phần mềm cần phải xem xét đến các yếu tố như khả năng mở rộng, hiệu suất, bảo mật, và khả năng chịu lỗi của hệ thống.</p></li></ul><h2 id="điem-giong-nhau-giua-design-pattern-va-software-architecture" tabindex="-1">Điểm giống nhau giữa Design Pattern và Software Architecture <a class="header-anchor" href="#điem-giong-nhau-giua-design-pattern-va-software-architecture" aria-label="Permalink to &quot;Điểm giống nhau giữa Design Pattern và Software Architecture&quot;">​</a></h2><ol><li><p><strong>Mục Đích Cải Thiện</strong>: Cả Design Pattern và Software Architecture đều nhằm mục đích cải thiện quá trình phát triển phần mềm, tăng cường khả năng bảo trì và mở rộng, cũng như tối ưu hóa hiệu suất và chất lượng của sản phẩm cuối cùng.</p></li><li><p><strong>Tái Sử Dụng và Best Practices</strong>: Cả hai đều dựa trên việc tái sử dụng các giải pháp đã được chứng minh là hiệu quả và áp dụng các best practices trong phát triển phần mềm.</p></li><li><p><strong>Cung Cấp Mô Hình Giải Pháp</strong>: Design Pattern và Software Architecture đều cung cấp các mô hình giải pháp cho các vấn đề thường gặp, giúp định hình cách tiếp cận và giải quyết vấn đề một cách có hệ thống.</p></li><li><p><strong>Giao Tiếp và Hiểu Biết</strong>: Cả hai đều giúp cải thiện giao tiếp giữa mọi người trong nhóm hoặc khác nhóm bằng cách cung cấp một ngôn ngữ chung và giúp các thành viên trong dự án có cùng một hiểu biết về cấu trúc và thiết kế của hệ thống.</p></li></ol><h2 id="bang-so-sanh-khac-nhau-giua-design-pattern-va-software-architecture" tabindex="-1">Bảng so sánh khác nhau giữa Design Pattern và Software Architecture <a class="header-anchor" href="#bang-so-sanh-khac-nhau-giua-design-pattern-va-software-architecture" aria-label="Permalink to &quot;Bảng so sánh khác nhau giữa Design Pattern và Software Architecture&quot;">​</a></h2><table><thead><tr><th>Type</th><th><a href="./2024-01-31-tim-hieu-ve-design-pattern.html">Design Pattern</a></th><th><a href="./2024-02-01-tim-hieu-ve-architecture.html">Software Architecture</a></th></tr></thead><tbody><tr><td>Mục đích</td><td>Cung cấp giải pháp tiêu chuẩn được nhiều người kiểm chứng để giải quyết các vấn đề cụ thể trong thiết kế phần mềm</td><td>Xác định cấu trúc chính của hệ thống, bao gồm các thành phần, thuộc tính của chúng và cách tương tác với nhau.</td></tr><tr><td>Phạm vi</td><td>Tập trung vào cách giải quyết các vấn đề thiết kế ở mức độ lớp (class) và method trong một project.</td><td>Tập trung vào cấu trúc tổng thể của hệ thống và mối quan hệ giữa các thành phần lớn như modules, components và external systems</td></tr><tr><td>Tái sử dụng</td><td>Cung cấp các mô hình có thể tái sử dụng trong nhiều modunl, chức năng và dự án phần mềm khác nhau.</td><td>Khuyến khích việc thiết kế các thành phần có thể tái sử dụng và tích hợp trong các dự án khác nhau.</td></tr><tr><td>Phân loại</td><td>Chia thành ba loại chính là Creational, Structural và Behavioral.</td><td>Có thể sử dụng nhiều kiểu kiến trúc như Monolithic, Microservices, Event-Driven, Layered, SOA, v.v</td></tr><tr><td>Trừu tượng</td><td>Cung cấp giải pháp ở mức độ trừu tượng cao, không có mã nguồn cụ thể mà là mô tả(Spect) ở mức độ trừu tượng về cách tiếp cận một vấn đề.</td><td>Cung cấp một bản kế hoạch tổng quát cho việc xây dựng và triển khai hệ thống phần mềm có thể bao gồm việc lựa chọn công nghệ cụ thể, mô hình triển khai, và chiến lược phát triển.</td></tr><tr><td>Tầm Quan Trọng</td><td>Nhấn mạnh vào việc tái sử dụng các giải pháp thiết kế đã được kiểm nghiệm để giải quyết các vấn đề cụ thể trong mã nguồn..</td><td>Tập trung vào việc xác định cấu trúc tổng thể của hệ thống, đảm bảo rằng hệ thống có thể đáp ứng các yêu cầu về kỹ thuật và kinh doanh, và có thể mở rộng hoặc thích ứng với các thay đổi trong tương lai.</td></tr><tr><td>Cấp Độ Quyết Định</td><td>Thường được sử dụng và quyết định bởi các nhà phát triển khi họ đang thiết kế và triển khai các phần cụ thể của mã nguồn.</td><td>Đòi hỏi sự tham gia của các kiến trúc sư phần mềm và các nhà quản lý dự án để đưa ra các quyết định cấp cao ảnh hưởng đến toàn bộ hệ thống.</td></tr><tr><td>Thời Điểm Áp Dụng</td><td>Có thể được áp dụng bất cứ lúc nào trong quá trình phát triển, thường là khi đang giải quyết các vấn đề thiết kế cụ thể.</td><td>Thường được xác định ở giai đoạn đầu của quá trình phát triển phần mềm và có ảnh hưởng lâu dài đến cách thức triển khai và bảo trì hệ thống.</td></tr><tr><td>Giao tiếp</td><td>Sử dụng một ngôn ngữ chung để mô tả giải pháp cho các vấn đề thiết kế.</td><td>Cung cấp một ngôn ngữ và cấu trúc chung để các bên liên quan có thể hiểu và thảo luận về hệ thống.</td></tr><tr><td>Nhược điểm</td><td>Có thể tạo thêm sự phức tạp không cần thiết, yêu cầu sự hiểu biết sâu sắc, và có thể hạn chế khả năng áp dụng với người không hiểu.</td><td>Có thể tốn kém, khó hiểu và khó duy trì, có thể hạn chế khả năng mở rộng và linh hoạt, và phụ thuộc vào công nghệ.</td></tr></tbody></table><h3 id="su-khac-biet-chinh" tabindex="-1">Sự Khác Biệt Chính <a class="header-anchor" href="#su-khac-biet-chinh" aria-label="Permalink to &quot;Sự Khác Biệt Chính&quot;">​</a></h3><ul><li><p><strong>Phạm vi Ứng Dụng:</strong> <a href="./2024-01-31-tim-hieu-ve-design-pattern.html">Design Pattern</a> tập trung vào giải quyết các vấn đề thiết kế cụ thể ở mức độ lớp và đối tượng, trong khi <a href="./2024-02-01-tim-hieu-ve-architecture.html">Software Architecture</a> tập trung vào cấu trúc tổng thể và mối quan hệ giữa các thành phần lớn của hệ thống.</p></li><li><p><strong>Tái sử dụng và Trừu tượng:</strong> Cả hai đều khuyến khích tái sử dụng và cung cấp giải pháp ở mức độ trừu tượng, nhưng <a href="./2024-01-31-tim-hieu-ve-design-pattern.html">Design Pattern</a> là các mô hình giải quyết vấn đề cụ thể, trong khi <a href="./2024-02-01-tim-hieu-ve-architecture.html">Software Architecture</a> là bản kế hoạch tổng thể cho toàn bộ hệ thống.</p></li></ul><h2 id="tong-ket" tabindex="-1">Tổng kết <a class="header-anchor" href="#tong-ket" aria-label="Permalink to &quot;Tổng kết&quot;">​</a></h2><ul><li><ol><li><strong><a href="./2024-01-31-tim-hieu-ve-design-pattern.html">Design Pattern</a></strong> tập trung vào giải pháp tái sử dụng cho các vấn đề thiết kế cụ thể ở mức độ function, class và project.</li></ol></li><li><ol start="2"><li><strong><a href="./2024-02-01-tim-hieu-ve-architecture.html">Software Architecture</a></strong> định nghĩa cấu trúc tổng thể của hệ thống, từ mức độ project đến solution và system.</li></ol></li><li><ol start="3"><li><strong><a href="./2024-01-31-tim-hieu-ve-design-pattern.html">Design Pattern</a></strong> giúp định hình cách các class tương tác trong một dự án, không định nghĩa cấu trúc tổng thể.</li></ol></li><li><ol start="4"><li><strong><a href="./2024-02-01-tim-hieu-ve-architecture.html">Software Architecture</a></strong> quyết định cách các class, modules và dự án tương tác, đảm bảo mở rộng, bảo trì và thử nghiệm dễ dàng.</li></ol></li><li><ol start="5"><li><strong>Cả hai</strong> khuyến khích tái sử dụng và trừu tượng hóa, nhưng ở hai mức độ khác nhau:</li></ol><ul><li><ol><li><a href="./2024-01-31-tim-hieu-ve-design-pattern.html">Design Pattern</a> ở mức độ giải pháp cụ thể.</li></ol></li><li><ol start="2"><li><a href="./2024-02-01-tim-hieu-ve-architecture.html">Software Architecture</a> ở mức độ kế hoạch tổng thể.</li></ol></li></ul></li></ul>',22)];const r=t(e,[["render",function(t,i,c,e,r,g){return h(),n("div",null,a)}]]);export{c as __pageData,r as default};
