import{_ as n,j as e,o as r,v as i}from"./chunks/framework.9xwLhOZU.js";const t=JSON.parse('{"title":"Direct Server Return là gì? Giải thích về L2DSR và L3DSR","description":"Direct Server Return (DSR) là một cách để load balancer được sử dụng để tối ưu hóa hiệu suất khi sử dụng load balancer. Bài viết này sẽ giải thích về DSR, L2DSR và L3DSR.","frontmatter":{"footer":true,"title":"Direct Server Return là gì? Giải thích về L2DSR và L3DSR","description":"Direct Server Return (DSR) là một cách để load balancer được sử dụng để tối ưu hóa hiệu suất khi sử dụng load balancer. Bài viết này sẽ giải thích về DSR, L2DSR và L3DSR.","authors":["lethanh"],"date":"2025-02-20T00:00:00.000Z","outline":"deep","draft":false},"headers":[{"level":2,"title":"Giới thiệu về Direct Server Return (DSR)","slug":"gioi-thieu-ve-direct-server-return-dsr","link":"#gioi-thieu-ve-direct-server-return-dsr","children":[{"level":3,"title":"Để hiểu sâu hơn về bài viêt này, bạn nên hiểu cơ bản về OSI model","slug":"đe-hieu-sau-hon-ve-bai-viet-nay-ban-nen-hieu-co-ban-ve-osi-model","link":"#đe-hieu-sau-hon-ve-bai-viet-nay-ban-nen-hieu-co-ban-ve-osi-model","children":[]}]},{"level":2,"title":"L2DSR( Layer 2 Direct server Return ) và L3DSR( Layer 3 Direct server Return )","slug":"l2dsr-layer-2-direct-server-return-va-l3dsr-layer-3-direct-server-return","link":"#l2dsr-layer-2-direct-server-return-va-l3dsr-layer-3-direct-server-return","children":[{"level":3,"title":"L2DSR( Layer 2 Direct server Return )","slug":"l2dsr-layer-2-direct-server-return","link":"#l2dsr-layer-2-direct-server-return","children":[{"level":4,"title":"Ví dụ về L2DSR","slug":"vi-du-ve-l2dsr","link":"#vi-du-ve-l2dsr","children":[]},{"level":4,"title":"Câu hỏi","slug":"cau-hoi","link":"#cau-hoi","children":[]}]},{"level":3,"title":"Ưu điểm của L2DSR","slug":"uu-điem-cua-l2dsr","link":"#uu-điem-cua-l2dsr","children":[]},{"level":3,"title":"Nhược điểm của L2DSR","slug":"nhuoc-điem-cua-l2dsr","link":"#nhuoc-điem-cua-l2dsr","children":[]}]},{"level":2,"title":"L3DSR (Layer 3 Direct Server Return)","slug":"l3dsr-layer-3-direct-server-return","link":"#l3dsr-layer-3-direct-server-return","children":[{"level":4,"title":"Ví dụ về L3DSR","slug":"vi-du-ve-l3dsr","link":"#vi-du-ve-l3dsr","children":[]},{"level":3,"title":"Ưu điểm  L3DSR  :","slug":"uu-điem-l3dsr","link":"#uu-điem-l3dsr","children":[]},{"level":3,"title":"Nhược điểm L3DSR:","slug":"nhuoc-điem-l3dsr","link":"#nhuoc-điem-l3dsr","children":[]}]},{"level":2,"title":"So sánh L2DSR và L3DSR.","slug":"so-sanh-l2dsr-va-l3dsr","link":"#so-sanh-l2dsr-va-l3dsr","children":[]},{"level":2,"title":"Khi nào sử dụng","slug":"khi-nao-su-dung","link":"#khi-nao-su-dung","children":[{"level":3,"title":"L2DSR","slug":"l2dsr","link":"#l2dsr","children":[]},{"level":3,"title":"L3DSR","slug":"l3dsr","link":"#l3dsr","children":[]}]},{"level":2,"title":"Kết luận","slug":"ket-luan","link":"#ket-luan","children":[]}],"relativePath":"blog/2025-02-20-Direct-Server-Return-la-gi-l2dsr-va-l3dsr.md","filePath":"blog/2025-02-20-Direct-Server-Return-la-gi-l2dsr-va-l3dsr.md","lastUpdated":1740291738000}'),a={name:"blog/2025-02-20-Direct-Server-Return-la-gi-l2dsr-va-l3dsr.md"},l=[i('<h1 id="direct-server-return-la-gi-giai-thich-ve-l2dsr-va-l3dsr" tabindex="-1">Direct Server Return là gì? Giải thích về L2DSR và L3DSR <a class="header-anchor" href="#direct-server-return-la-gi-giai-thich-ve-l2dsr-va-l3dsr" aria-label="Permalink to &quot;Direct Server Return là gì? Giải thích về L2DSR và L3DSR&quot;">​</a></h1><h2 id="gioi-thieu-ve-direct-server-return-dsr" tabindex="-1">Giới thiệu về Direct Server Return (DSR) <a class="header-anchor" href="#gioi-thieu-ve-direct-server-return-dsr" aria-label="Permalink to &quot;Giới thiệu về Direct Server Return (DSR)&quot;">​</a></h2><p>Trong bối cảnh công nghệ mạng máy tính ngày nay, việc cân bằng tải là một yêu cầu thiết yếu để đảm bảo hiệu suất và khả năng mở rộng của hệ thống. Direct Server Return (DSR) nổi lên như một giải pháp sáng tạo, đặc biệt là để tối ưu hóa luồng dữ liệu mạng và khắc phục những hạn chế của các hệ thống cân bằng tải (Load Balancer - LB) truyền thống.</p><p>Các LB thông thường thường(Ví dụ dùng nginx) gặp phải vấn đề về độ trễ khi tất cả lưu lượng truy cập phải đi qua LB cả khi vào và ra khỏi hệ thống. Điều này có thể tạo ra một điểm nghẽn tiềm ẩn trong hệ thống mạng, làm chậm tốc độ truyền tải dữ liệu và gia tăng nguy cơ quá tải cũng như giảm hiệu suất tổng thể. Ngược lại, phương pháp DSR cho phép máy chủ xử lý trực tiếp lưu lượng ra, bỏ qua LB trên đường trở về, nhờ đó tiết kiệm băng thông và giảm đáng kể độ trễ mạng.</p><p>L3DSR là một tiến bộ đáng chú ý trong lĩnh vực này, cho phép tích hợp DSR ở lớp 3 của mô hình OSI, mở ra những khả năng mới mẻ và linh hoạt hơn cho việc điều hành lưu lượng dữ liệu một cách hiệu quả hơn.</p><p>Hiểu đơn giản: <strong>Direct Server Return (DSR)</strong> là cấu hình mạng được sử dụng trong <strong>load balancer</strong> để chuyển tiếp các yêu cầu <strong>đến máy chủ backend,</strong> sau đó <strong>gửi phản hồi của máy chủ trực tiếp trở lại client</strong>.</p><p><img src="/assets/layer4_dsr_data_flow.diaGI635.png" alt="layer4_dsr_data_flow.png"></p><h3 id="đe-hieu-sau-hon-ve-bai-viet-nay-ban-nen-hieu-co-ban-ve-osi-model" tabindex="-1">Để hiểu sâu hơn về bài viêt này, bạn nên hiểu cơ bản về OSI model <a class="header-anchor" href="#đe-hieu-sau-hon-ve-bai-viet-nay-ban-nen-hieu-co-ban-ve-osi-model" aria-label="Permalink to &quot;Để hiểu sâu hơn về bài viêt này, bạn nên hiểu cơ bản về OSI model&quot;">​</a></h3><p><img src="/assets/OSI_Model_v1.svg.VbTcemNc.png" alt="OSI_Model_v1.svg.png"></p><p>GIải thích chi tiết về OSI model: <a href="https://vi.wikipedia.org/wiki/M%C3%B4_h%C3%ACnh_OSI" target="_blank" rel="noreferrer">https://vi.wikipedia.org/wiki/Mô_hình_OSI</a></p><h2 id="l2dsr-layer-2-direct-server-return-va-l3dsr-layer-3-direct-server-return" tabindex="-1">L2DSR( Layer 2 Direct server Return ) và L3DSR( Layer 3 Direct server Return ) <a class="header-anchor" href="#l2dsr-layer-2-direct-server-return-va-l3dsr-layer-3-direct-server-return" aria-label="Permalink to &quot;L2DSR( Layer 2 Direct server Return ) và L3DSR( Layer 3 Direct server Return )&quot;">​</a></h2><h3 id="l2dsr-layer-2-direct-server-return" tabindex="-1">L2DSR( Layer 2 Direct server Return ) <a class="header-anchor" href="#l2dsr-layer-2-direct-server-return" aria-label="Permalink to &quot;L2DSR( Layer 2 Direct server Return )&quot;">​</a></h3><p>Đây là một loại trong <strong>Direct Server Return</strong> hoạt động ở Layer 2 ( Datalink Layer ), dữ liệu được định tuyến dựa trên địa chỉ MAC (Media Access control). Gói tin sẽ giữ nguyên tất cả các cấu trúc ban đầu và không thay đổi địa chỉ IP. Chỉ sử dụng địa chỉ MAC để định tuyến đích đến.</p><p>Đối với Layer 2 nơi Load Balancer và Server phải nằm trong cùng một mạng con (subnet).</p><ul><li>Ví dụ: LB 10.10.10.1 và server 10.10.10.2. Nếu server là 10.10.11.2 thì sẽ không được.</li></ul><p>Với L2DSR dữ liệu chỉ có thể truyền trong cùng một mạng (broadcast domain)</p><h4 id="vi-du-ve-l2dsr" tabindex="-1">Ví dụ về L2DSR <a class="header-anchor" href="#vi-du-ve-l2dsr" aria-label="Permalink to &quot;Ví dụ về L2DSR&quot;">​</a></h4><ul><li><strong>Bối cảnh hệ thống:</strong><ul><li><strong>VIP: 192.168.1.100</strong> Cả LB và Server đầu cấu hình dùng chung VIP 192.168.1.100 Tuy nhiên khi client sử dụng ARP để hỏi địa chỉ MAC, thì chỉ LB trả về địa chỉ Mac còn nó còn Server sẽ không trả về</li><li><strong>Load Balancer (LB):</strong><ul><li>IP: 192.168.1.10</li><li>MAC: AA:BB:CC:DD:EE:01</li></ul></li><li><strong>Server:</strong><ul><li>IP: 192.168.1.20</li><li>MAC: AA:BB:CC:DD:EE:02</li></ul></li><li><strong>Client:</strong><ul><li>IP: 192.168.1.200</li><li>MAC: AA:BB:CC:DD:EE:03</li></ul></li></ul></li></ul><img src="https://www.plantuml.com/plantuml/svg/hLLTQzDG6BxFhtZOXNQmbhLJqLp8sdJeM3VA8WClipGqXsKdDJqPxaxObOWNOtWbmdICeTjmGwyQn8kC_O_uItpFITgcjPlp0mfjSzwFvtsUvpsPxmeh4E6cfyqFVWacF0UDoxCG5xj9j8VdINGAB_w6Tlo5GJ2E3PUXAv-sakYGjx6ss8G8uayS3TzgeMPv5hUT05j9z9QXKQcRgvfcsS8FO7ZCuG9M5uRMeOxCPXsB7aoDP4x9W4Pj9C9qWYsgIGUcfd5VEF3bVqC3QfTdrCb6MU_NBvGVL4hbU_TBvL9bTdOQcs4IxNAq2JGRpa7pzRvp1J-NKAQ44bNzyU83ggjD1CwpqEaAnSOEH7ooYUMg0Jk9ZgphSXir8YNf7S1BedSiNse6TaYqsKdli2DhwQXMzLfDDmozNjSN5lJPSabpU0joJ4sxROm3Dv-kvC3Fo5RSJNgd75lnlWBwYhiGuROCqGmK7rA8ZYT6KwQcx0MixGg2kY1S9-2Em6F7QZc1Je-AC0DxCh0v53AAwNXQnTQxueeXK21WMHpnDDvWiQbZHAT1kT4J8XK5cdCwnydG06IJeGCgUewBFbr4jvj4hpcwgOLI8LDAoS7dLcPUuOQK9o3y-3rPkLPIU1wn9FeCtWwtPGoDSR4RdzhkkEegrg0-BzLgM7rQg6gyJs8CBPEXA8pJLSdFHrEGQER_qcp9ZR-IuuHhRQlntx0PsixUCQV5tiUD5QsWaAtX7wWwS8nKy6yKDdC2p-dzYs1Cv1J0icLd-jm0V76evcGm07tML91iVs2hbGtXnYTolPHfFD_lh5lsHYQhz4pMPN1lad52cNaKglMqfSq-SgpJbg93Knnma6_ssCZEcoD7yYQ9pcqQb3Qwqz_zb5VDb5On1bOfLesbVx7B19wkDCGlry5l3P5dTv8LnWqpoH3vE_yERk7kuDxFJ74DTIA9ZZE5f5OhRNepxAcz7kx3X4izXsEUVgfNPlhz4m00" alt="uml diagram"><details class="details custom-block"><summary>Step by step cho ví dụ trên</summary><p><strong>Bước 1</strong>. Client tạo request đến VIP</p><ul><li>Client tạo một gói tin TCP gửi đến VIP (192.168.1.100) <ul><li><strong>Source IP</strong>: 192.168.1.200</li><li><strong>Destination Ip</strong>: 192.168.1.100</li></ul></li><li>Tại Layer 2. Client sẽ gửi 1 gói tin APR để tìm kiếm Mac của VIP (192.168.1.100). LB sẽ trả lời APR và cung cấp địa chỉ Mac của LB (<strong>AA:BB:CC:DD:EE:01</strong>)</li><li><strong>Gói tin rời khỏi client</strong><ul><li>Ethernet Header: <ul><li><strong>Source MAC</strong>: AA:BB:CC:DD:EE:03 (Client Mac)</li><li><strong>Destination MAC</strong>: AA:BB:CC:DD:EE:01 (Load Balancer Mac)</li></ul></li><li>IP Header <ul><li><strong>Source IP</strong>: 192.168.1.200</li><li><strong>Destination Ip</strong>: 192.168.1.100</li></ul></li></ul></li></ul><p><strong>Bước 2</strong>. Load Balancer nhận gói tin từ client và định tuyến gói tin đến Backend Server</p><ul><li>Load Balancer thực hiện nhận gói tin. Dựa vào thuật toán của LB để quyết định Backend Server sẽ xử lý yêu cầu. <ul><li>Trong ví dụ hiện tại Server 192.168.1.20 sẽ xử lý yêu cầu</li></ul></li><li>Một điều quan trọng là khi Load Balancer định tuyến gói tin đến Backend Server sẽ không thay đổi IP nhật yêu cầu, nhưng nó sẽ đổi địa chỉ Mac đích để gửi gói tin đến Server.</li><li><strong>Gói tin rời khỏi Load Balaner</strong><ul><li>Ethernet Header: <ul><li><strong>Source MAC</strong>: AA:BB:CC:DD:EE:03 (Client Mac)</li><li><strong>Destination MAC</strong>: AA:BB:CC:DD:EE:02 (Server Mac)</li></ul></li><li>IP Header <ul><li><strong>Source IP</strong>: 192.168.1.200</li><li><strong>Destination Ip</strong>: 192.168.1.100 <strong>Bước 3</strong>. Server nhận gói tin từ Load Balancer và xử lý yêu cầu</li></ul></li></ul></li><li>Server nhận gói tin từ Load Balacer, thông tin gói tin chỉ bị sửa đổi địa chỉ Destination MAC thành của Backend Server.</li><li>Bởi vì Server Backend đã cấu hình Loopback cho VIP 192.168.1.100 nên backend Server sẽ nhận và xử lý các yêu cầu có Ip đích là 192.168.1.100.</li><li>Sau khi nhận yêu cầu, Backend server tiến hành xử lý và tạo ra response.</li></ul><p><strong>Bước 4</strong>. Server phản hồi trực tiếp đến client</p><ul><li>Sau khi xử lý yêu cầu và tạo response thành công Backend server sẽ tiến hành gửi trực tiếp response về client mà không cần thông qua trung gian Load Balancer.</li><li><strong>Gói tin rời khỏi backend Server</strong><ul><li>Ethernet Header: <ul><li><strong>Source MAC</strong>: AA:BB:CC:DD:EE:03 (Client Mac)</li><li><strong>Destination MAC</strong>: AA:BB:CC:DD:EE:02 (Server Mac)</li></ul></li><li>IP Header <ul><li><strong>Source IP</strong>: 192.168.1.100 (VIP)</li><li><strong>Destination Ip</strong>: 192.168.1.200</li></ul></li></ul></li></ul><p><strong>Bước 5</strong>: Client nhận phản hồi từ Backend Server.</p><ul><li>Client nhận phản hồi trực tiếp từ Backend server nhưng địa chỉ IP nguồn gửi trong gói tin vẫn là VIP (192.168.1.100)</li><li>Bởi vì client đã tạo 1 yêu cầu, nên khi nhận response client sẽ chỉ kiểm tra IP chứ không kiểm tra MAC gửi. Vì vậy Backend server gửi response đến client với MAC của Backend Server nhưng không có lỗi.</li></ul></details><h4 id="cau-hoi" tabindex="-1">Câu hỏi <a class="header-anchor" href="#cau-hoi" aria-label="Permalink to &quot;Câu hỏi&quot;">​</a></h4><ol><li>Vậy việc thiếp lập kết nối TCP giữa client với Load Balancer hay Backend Server? <ul><li>Việc thiếp lập kết nối TCP sẽ là từ Client với Backend Server. Load balancer chỉ tham gia trong quá trình khởi tạo kết nối. Điều này giúp tối ưu hiệu suất và giảm độ trễ.</li></ul></li><li>Điều gì xảy ra nếu Backend Server không tắt phản hồi ARP ? <ul><li>Bởi vì cả Load balancer và Backend Server đều được gán cho ip VIP nên nếu không tắt ARP sẽ bị lỗi xung đột IP.</li></ul></li><li>Nếu Load Balancer và Backend Server không cùng 1 mạng subnet thì sao? <ul><li>Với L2DSR thì Balancer và Backend Server không cùng 1 subnet thì Load balancer sẽ không thể định tuyến gói tin đến Backend Server</li></ul></li><li>Tại sao cần thay đổi Mac, gửi định tuyến thẳng không được ư ? <ul><li>Bởi vì chúng ta đang định tuyến gói tin ở layer 2(Mac), đây là định tuyến gói tin trong mạng Lan, vì vầy switch sẽ sử dụng địa chỉ Mac để xác định đích đến của gói tin. Nếu địa chỉ MAC đích không được thay đổi thành địa chỉ MAC của máy chủ backend, gói tin sẽ không thể đến được máy chủ mong muốn.</li></ul></li></ol><h3 id="uu-điem-cua-l2dsr" tabindex="-1">Ưu điểm của L2DSR <a class="header-anchor" href="#uu-điem-cua-l2dsr" aria-label="Permalink to &quot;Ưu điểm của L2DSR&quot;">​</a></h3><ul><li><strong>Triển khai đơn giản:</strong> Không cần cấu hình phức tạp, bời vì triển khai ở Layer 2</li><li><strong>Không thay đổi Ip:</strong> Không cần thay đổi Ip Source và Ip Destination</li></ul><h3 id="nhuoc-điem-cua-l2dsr" tabindex="-1">Nhược điểm của L2DSR <a class="header-anchor" href="#nhuoc-điem-cua-l2dsr" aria-label="Permalink to &quot;Nhược điểm của L2DSR&quot;">​</a></h3><ul><li>Yêu cầu cùng subnet layer 2: Cả Load Balancer và Backend Server đều cần nằm cùng một Subnet layner 2, hạn chế khả năng mở rộng. Không thể triển khai trên nhiều data center.</li></ul><h2 id="l3dsr-layer-3-direct-server-return" tabindex="-1">L3DSR (Layer 3 Direct Server Return) <a class="header-anchor" href="#l3dsr-layer-3-direct-server-return" aria-label="Permalink to &quot;L3DSR (Layer 3 Direct Server Return)&quot;">​</a></h2><p>Đây là một loại trong Direct Server Return hoạt động ở layer 3(<strong>Network layer</strong>), request được định tuyến thông qua IP để chuyển đến server.</p><p>Đối vơi L3DSR, Load Balancer và Backend Server không cần phải nằm trong cùng một mạng subnet. Load Balancer sẽ thay đổi IP Destination của gói tin để định tuyến đến Backend Server.</p><p>Vì không cần phải nằm trong cùng một mạng subnet nên L3DSR có thể triển khai trên nhiều data center, giúp tăng khả năng mở rộng hệ thống.</p><h4 id="vi-du-ve-l3dsr" tabindex="-1">Ví dụ về L3DSR <a class="header-anchor" href="#vi-du-ve-l3dsr" aria-label="Permalink to &quot;Ví dụ về L3DSR&quot;">​</a></h4><ul><li><strong>Bối cảnh hệ thống:</strong><ul><li>Load Balancer: <ul><li>VIP (Virtual Ip): <strong>203.0.113.1</strong></li><li><strong>Nằm ở Subnet A (192.168.1.0/24)</strong></li></ul></li><li>Backend Servers: <ul><li>Server 1 (<strong>Singapore</strong>): Ip <strong>10.1.1.10</strong> trong <strong>Subnet B (10.1.1.0/24)</strong></li><li>Server 2 (<strong>Jp</strong>): IP <strong>10.2.1.20</strong> trong <strong>Subnet C (10.2.1.0/24)</strong></li><li>Server 3: (<strong>VN</strong>): Ip <strong>192.168.2.30</strong> trong <strong>Subnet D (192.168.2.0/24)</strong> -Client:</li><li>IP: <strong>203.0.114.50</strong></li></ul></li><li>(Các server backend sẽ cấu hình để cấp nhận các gói tin có địa chỉ đích là VIP (203.0.113.1)) bằng cách ánh xạ VIP (203.0.113.1) vào một interface mạng trên Server (hoặc thông qua loopback). Server &quot;đóng vai&quot; VIP (Virtual Server) khi phản hồi, vì vậy Client không bao giờ biết rằng yêu cầu được xử lý bởi một Server cụ thể trong mạng backend.</li></ul></li></ul><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span></span></span>\n<span class="line"><span>          +-----------------+</span></span>\n<span class="line"><span>          |     Client      |</span></span>\n<span class="line"><span>          |  203.0.114.50   |</span></span>\n<span class="line"><span>          +--------+--------+</span></span>\n<span class="line"><span>                   |</span></span>\n<span class="line"><span>                   | ①</span></span>\n<span class="line"><span>                   |</span></span>\n<span class="line"><span>          +--------v--------+</span></span>\n<span class="line"><span>          |      LB         |</span></span>\n<span class="line"><span>          | VIP 203.0.113.3 |</span></span>\n<span class="line"><span>          +--------+--------+</span></span>\n<span class="line"><span>                   |</span></span>\n<span class="line"><span>      +------------+------------+</span></span>\n<span class="line"><span>      |                         |</span></span>\n<span class="line"><span>      |                         |</span></span>\n<span class="line"><span> ② VXLAN, GRE             ② VXLAN, GRE</span></span>\n<span class="line"><span>      |                         |</span></span>\n<span class="line"><span>      |                         |</span></span>\n<span class="line"><span>+-----v-----+            +------v------+</span></span>\n<span class="line"><span>| Singapore |            |     Japan   |</span></span>\n<span class="line"><span>|  Backend Server        |  Backend Server</span></span>\n<span class="line"><span>| loopback(203.0.113.3)  |  loopback(203.0.113.3)</span></span>\n<span class="line"><span>|  IP: 10.1.1.10         |  IP: 10.2.1.20</span></span>\n<span class="line"><span>|  Subnet B              |  Subnet C</span></span>\n<span class="line"><span>|  (10.1.1.0/24)         |  (10.2.1.0/24)</span></span>\n<span class="line"><span>+-----------+            +-------------+</span></span>\n<span class="line"><span>      |                         |</span></span>\n<span class="line"><span>      ③                        ③</span></span>\n<span class="line"><span>      |                         |</span></span>\n<span class="line"><span>  Response client           Response client</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br><span class="line-number">17</span><br><span class="line-number">18</span><br><span class="line-number">19</span><br><span class="line-number">20</span><br><span class="line-number">21</span><br><span class="line-number">22</span><br><span class="line-number">23</span><br><span class="line-number">24</span><br><span class="line-number">25</span><br><span class="line-number">26</span><br><span class="line-number">27</span><br><span class="line-number">28</span><br><span class="line-number">29</span><br><span class="line-number">30</span><br><span class="line-number">31</span><br></div></div><img src="https://www.plantuml.com/plantuml/svg/bLPFYzj65B_hKppsv8LTnNyseTMXRAmK9y4OOo-crx5MbSJQ8qSURUDp3gMKGaFf8OTIEyiIIBga8OTIcnAeGhw7lab_CoFRScHxAGjUs_FUc_VxyvvyEXOi4l5mO9okteb035nw6ARpcQ2FpzF52-wHaovUCMhM7dGx9A8Gt_ZfudiIU7sE3yDu889ZxehlmkZ2C9WZmeZiGU1oGMnCjZ52yS09HWnV73H3TavrDc3SSICgzHwrBQgMQsRPh5HgPkNmGEOqwvj9NJUwH7IlHQNAbrMpSkyBishMoZgurzeQ_BYDu3BYAcOrYtpStXhPRQZ8YlpB8hiDm-2XS2cK0RP1L0VO05JChxY_WT_2uJ7T1ejuEdytf7JnEtNZFiYY-sii5RDyftfoQAfAkx3cSn_apugvcz3pYVRge9YrIKC-gxuwo6Lb2fUMA4_CksKSkVoS97E6OTFnLr3IeeTdP-qxygLB7VT9x8w5vZOAF5-WLbUu8wfOoufUkhW9ADAHqeJp3_nMVdLFZUHzG2BWoFhqPxguSfI7VuHY8fweEazYHaEu6xuDk70Z2UyyNRnZS7RockjuHWwq_O4UjV4vkN5yTU46k6PzYQxj9oyv2HNcPVTlmLUr44tSJ-StV1Mdsb5zVCiSTzdONeEeOZLLRBJbPhfCfghxdpYDjQvFmISDadzeahoEfODVnPgjCpz6Du94cCmuER9O7ov51m8s3opgX340Ty9-m8-ewJB8OOUSkuu8GdpZOqtyvL333O_hREpAo1WjUBhw6Yq6PNuz8KVBAttL2p10WbfiUBHiTfVjYwJtMh2A3pqNpt9CPZvPbTaYmObYpLcchkae91Twa80-_fopHkQBotJ-XeFSAS4WwrbG4v_H8tmsKRRw5OtwoHHct7MFyUzK4pUMai6Mrz0YqgIIO7siPbdesHh32hv85szs6sS5y9RraRjMn11w82ykzRvftcyTKQFpjLfZ7pHhFAyCe6JAjBJcqeOhu-qPYxjMZWs-9VTpqnPvNiqwNEmZSJwJ5K8QXE6epvoBZILt0E7U0zGb2mvKhGi_m239jIwt-Yz14PKjPtmTW4M1VG5T0r0nMZAhTzSMWFVo094wlzguRb-rmcujk91xCOfmgyYAYS3avKHP4qFzLb0VeI-SNKwsvBBJ3_ezbv_PRQ31OiOMfbOyx0EML6le2dRE1AC-qvEePYgJnSEmlvMjGieDqwmVi1XN8ZkPOQ8-MzXMmPXzIPvNMEobJdvE3hD14SaVmwoJt-JPRh8rm3r2vvuSdhI_SkkrM4v4yZSEBlmOAm59kbfB7TdhlVUKaVzfXjNf_Py75VRpb2zdO4tVATwgNs_w_ty0" alt="uml diagram"><details class="details custom-block"><summary>Step by step cho ví dụ trên</summary><ul><li><p><strong>Bước 1</strong>: CLient gửi request đến Load Balancer</p><ul><li>Một Client với IP <strong>203.0.113.50</strong> gửi gói tin HTTP/HTTPS <strong>đến VIP (Virtual IP) 203.0.113.1</strong> – đây chính là địa chỉ của Load Balancer.</li><li>Gói tin được định tuyến qua mạng Internet dựa trên địa chỉ IP đích (VIP) và tới Load Balancer.</li></ul></li><li><p><strong>Bước 2</strong>: Load Balancer nhận gói tin qua một interface mạng</p><ul><li>Load Balancer tiếp nhận gói tin thông qua interface <strong>thuộc subnet A (192.168.1.0/24)</strong></li></ul></li><li><p><strong>Bước 3</strong>: LB phân tích gói tin và chọn Server xử lý yêu cầu</p><ul><li>Dựa trên thuật toán cân bằng tải (ví dụ: Round Robin, Least Connection, hoặc Geo-Based Routing), Load Balancer phân tích gói tin để chọn Server phù hợp.</li><li>Trong trường hợp này, LB xác định Client ở Việt Nam và quyết định chuyển yêu cầu đến một Server tại Việt Nam với địa chỉ IP 192.168.2.30 trong subnet D.</li></ul></li><li><p><strong>Bước 4</strong>: LB chuyển yêu cầu đến Server VN Ip 192.168.2.30</p><ul><li><p>Gói tin được LB chuyển đến Server Việt Nam với IP 192.168.2.30.</p></li><li><p>Địa chỉ đích của gói tin có thể giữ nguyên là VIP 203.0.113.1 hoặc thay đổi thành Ip Server VN 192.168.2.30</p></li><li><p>LB định tuyến gói tin qua L3 dựa trên bảng định tuyến (routing table), từ subnet A (192.168.1.0/24) đến subnet D (192.168.2.0/24).</p></li><li><p><strong>Encapsulation</strong>: Nếu subnet D nằm trong một mạng khác, LB sẽ sử dụng giao thức tunneling (ví dụ: VXLAN, GRE) để đóng gói gói tin và gửi qua mạng Internet hoặc mạng VPN liên kết. Server backend trong subnet D sẽ nhận gói tin với địa chỉ đích là VIP 203.0.113.1 hoặc địa chỉ IP thực của nó (192.168.2.30).</p></li></ul></li><li><p><strong>Bước 5</strong>: Backend nhận các gói tin và xử lý yêu cầu</p><ul><li>Nhờ cấu hình loopback, Server nhận các gói tin có địa chỉ đích là VIP <strong>203.0.113.1</strong> và tiến hành xử lý yêu cầu từ Client.</li></ul></li><li><p><strong>Bước 6</strong>: Backend Server trả phản hồi trực tiếp đến Client (Egress Traffic)</p><ul><li><p>Sau khi hoàn tất xử lý, Server backend gửi gói tin phản hồi trực tiếp đến Client với IP 203.0.113.50, bỏ qua trung gian là LB.</p></li><li><p>Địa chỉ nguồn (Source IP) của gói tin phản hồi là VIP 203.0.113.1, đảm bảo Client nhận phản hồi từ địa chỉ quen thuộc của LB.</p></li><li><p>Nếu là IP khác thì client coi đây là 1 IP lạ hoặc thuộc yêu cầu khác, khi đó sẽ không thể phản hồi</p></li><li><p>Địa chỉ đích (Destination IP) là IP của Client 203.0.113.50.</p></li></ul></li></ul><p><strong>Cách định tuyến phản hồi:</strong></p><ul><li>Server backend sử dụng bảng định tuyến để tìm đường trực tiếp đến Client.</li><li>Nếu Client ở cùng khu vực, gói phản hồi được gửi qua mạng gần nhất.</li><li>Nếu Client ở xa (ví dụ: Mỹ), gói tin được định tuyến qua mạng toàn cầu.</li></ul><p>Lưu ý: TCP Sequence Number và các thông tin phiên kết nối ban đầu giữa Client và LB được Server backend giữ nguyên để đảm bảo tính chính xác, tránh bị Client coi là gói tin giả mạo.</p></details><h3 id="uu-điem-l3dsr" tabindex="-1">Ưu điểm L3DSR : <a class="header-anchor" href="#uu-điem-l3dsr" aria-label="Permalink to &quot;Ưu điểm  L3DSR  :&quot;">​</a></h3><ul><li><strong>Hỗ trợ hệ thống mạng rộng hơn</strong>: Không yêu cầu LB và server cùng một subnet, vì vậy sẽ có khả năng mở rộng cao với các môi trường phức tạp hoặc nhiều trung tâm dữ liệu</li><li><strong>Không giới hạn về subnet</strong>: L3DSR hoạt động với các cơ chế định tuyến, giúp dễ dàng tích hợp trong các mạng lớn hoặc môi trường hybrid cloud(Kết hợp public cloud và private cloud)</li><li><strong>Độc lập về mặt topology</strong>: Các &#39;Server&#39; không cần chia sẻ cùng một lớp Layer 2 với &#39;Load Balancer&#39;, giảm phụ thuộc vào hạ tầng vật lý</li></ul><h3 id="nhuoc-điem-l3dsr" tabindex="-1">Nhược điểm L3DSR: <a class="header-anchor" href="#nhuoc-điem-l3dsr" aria-label="Permalink to &quot;Nhược điểm L3DSR:&quot;">​</a></h3><ul><li><strong>Cần định tuyến phức tạp hơn</strong>: Yêu cầu cấu hình route (hoặc policy routing) trên &#39;Load Balancer&#39; và các &#39;Server&#39;, tăng độ phức tạp khi triển khai.</li><li><strong>Tăng độ trễ</strong>: Layer 3 routing có thể làm tăng độ trễ trong các môi trường yêu cầu độ trễ thấp.</li></ul><h2 id="so-sanh-l2dsr-va-l3dsr" tabindex="-1">So sánh L2DSR và L3DSR. <a class="header-anchor" href="#so-sanh-l2dsr-va-l3dsr" aria-label="Permalink to &quot;So sánh L2DSR và L3DSR.&quot;">​</a></h2><table><thead><tr><th>Tiêu chí</th><th>L2DSR</th><th>L3DSR</th></tr></thead><tbody><tr><td>Phạm vi hoạt động</td><td>Cùng subnet trên layer 2</td><td>Mở rộng trên nhiều subnet, Layner 3. Có thể hoạt động trên nhiều data center</td></tr><tr><td>Hiệu suất</td><td>Tối ưu hơn vì không cần routing phức tạp</td><td>Có thể tăng độ trễ do routing</td></tr><tr><td>Khả năng mở rộng</td><td>Hạn chế vì phụ thuộc Layer 2</td><td>ở rộng linh hoạt nhờ routing</td></tr><tr><td>Độ phức tạp</td><td>Đơn giản hơn</td><td>Phức tạp hơn do cấu hình định tuyến</td></tr><tr><td>Triển khai</td><td>Phù hợp với network nhỏ, đơn giản</td><td>Phù hơp với network l, đa trung tâm dữ liệu</td></tr></tbody></table><h2 id="khi-nao-su-dung" tabindex="-1">Khi nào sử dụng <a class="header-anchor" href="#khi-nao-su-dung" aria-label="Permalink to &quot;Khi nào sử dụng&quot;">​</a></h2><h3 id="l2dsr" tabindex="-1">L2DSR <a class="header-anchor" href="#l2dsr" aria-label="Permalink to &quot;L2DSR&quot;">​</a></h3><ul><li>Khi hệ thống nhỏ, các server và load balancer cùng một mạng subnet layer 2</li><li>Mục tiêu là tối ưu hiệu suất và giảm độ trễ không cần có những rule định tuyến phức tạp</li><li>Không yêu cầu khả năng mở rộng mạng lớn, không sử dụng nhiều data center</li></ul><h3 id="l3dsr" tabindex="-1">L3DSR <a class="header-anchor" href="#l3dsr" aria-label="Permalink to &quot;L3DSR&quot;">​</a></h3><ul><li>Khi các server và load balancer có thể nằm ở nhiều subnet khác nhau, mạng khác nhau hoặc nhiều data center</li><li>Cần thiết cho các môi trường mạng lớn, phức tạp, hoặc hybrid cloud. Yêu cầu cao về khả năng mở rộng</li><li>Triển khai trong môi trường hybrid cloud, kết hợp public cloud và private cloud.</li></ul><h2 id="ket-luan" tabindex="-1">Kết luận <a class="header-anchor" href="#ket-luan" aria-label="Permalink to &quot;Kết luận&quot;">​</a></h2><p><strong>Direct Server Return (DSR)</strong> là một cấu hình mạng được sử dụng trong cân bằng tải để chuyển tiếp các yêu cầu đến máy chủ backend, sau đó gửi phản hồi của máy chủ trực tiếp trở lại client.</p><ul><li><strong>L2DSR (Layer 2 Direct Server Return</strong>) hoạt động ở Layer 2 (Datalink Layer), dữ liệu được định tuyến dựa trên địa chỉ MAC. L2DSR yêu cầu Load Balancer và Server phải nằm trong cùng một mạng con (subnet). Với L2DSR, dữ liệu chỉ có thể truyền trong cùng một mạng (broadcast domain).</li><li><strong>L3DSR (Layer 3 Direct Server Return)</strong> hoạt động ở layer 3 (Network layer), request được định tuyến thông qua IP để chuyển đến server. Với L3DSR, Load Balancer và Backend Server không cần phải nằm trong cùng một mạng subnet. Vì không cần phải nằm trong cùng một mạng subnet nên L3DSR có thể triển khai trên nhiều data center, giúp tăng khả năng mở rộng hệ thống.</li></ul>',49)];const s=n(a,[["render",function(n,i,t,a,s,c){return r(),e("div",null,l)}]]);export{t as __pageData,s as default};
