import{_ as n,c as i,o as t,b as h}from"./chunks/framework.4MfQJnRv.js";const a=JSON.parse('{"title":"Chia sẻ về sự kiện BestAIAwards 2025","description":"Sự kiện trao giải thưởng BestAIAwards 2025 đã diễn ra vào ngày 3 tháng 5 năm 2025 tại Đài Bắc, Đài Loan. Đây là một sự kiện lớn trong ngành công nghiệp AI, quy tụ nhiều chuyên gia, nhà nghiên cứu và doanh nhân hàng đầu trong lĩnh vực này. Sự kiện không chỉ là nơi trao giải thưởng cho những sản phẩm và dịch vụ AI xuất sắc nhất mà còn là cơ hội để các chuyên gia chia sẻ kiến thức và kinh nghiệm của mình.","frontmatter":{"footer":true,"title":"Chia sẻ về sự kiện BestAIAwards 2025","description":"Sự kiện trao giải thưởng BestAIAwards 2025 đã diễn ra vào ngày 3 tháng 5 năm 2025 tại Đài Bắc, Đài Loan. Đây là một sự kiện lớn trong ngành công nghiệp AI, quy tụ nhiều chuyên gia, nhà nghiên cứu và doanh nhân hàng đầu trong lĩnh vực này. Sự kiện không chỉ là nơi trao giải thưởng cho những sản phẩm và dịch vụ AI xuất sắc nhất mà còn là cơ hội để các chuyên gia chia sẻ kiến thức và kinh nghiệm của mình.","authors":["lethanh"],"date":"2025-05-03T00:00:00.000Z","outline":"deep","image":"https://static-cdn.thanhlv.com/blog/images/2025-05-03-chia-se-ve-su-kien-bestaiawards-2025/bestAI2025.jpg","draft":false},"headers":[{"level":2,"title":"1. Tại sao chúng tôi tham gia sự kiện này?","slug":"_1-tai-sao-chung-toi-tham-gia-su-kien-nay","link":"#_1-tai-sao-chung-toi-tham-gia-su-kien-nay","children":[]},{"level":2,"title":"2. Các thông tin được đăng công khai.","slug":"_2-cac-thong-tin-đuoc-đang-cong-khai","link":"#_2-cac-thong-tin-đuoc-đang-cong-khai","children":[]},{"level":2,"title":"3. Tổng quan quy trình tham gia sự kiện này.","slug":"_3-tong-quan-quy-trinh-tham-gia-su-kien-nay","link":"#_3-tong-quan-quy-trinh-tham-gia-su-kien-nay","children":[]},{"level":2,"title":"4. 2 hạng mục giải của sự kiện","slug":"_4-2-hang-muc-giai-cua-su-kien","link":"#_4-2-hang-muc-giai-cua-su-kien","children":[{"level":3,"title":"4.1. AI Application","slug":"_4-1-ai-application","link":"#_4-1-ai-application","children":[]},{"level":3,"title":"4.2. IC Design","slug":"_4-2-ic-design","link":"#_4-2-ic-design","children":[]}]},{"level":2,"title":"5. Một số sản phẩm tham gia sự kiện","slug":"_5-mot-so-san-pham-tham-gia-su-kien","link":"#_5-mot-so-san-pham-tham-gia-su-kien","children":[]},{"level":2,"title":"7. Một số bức ảnh trong sự kiện","slug":"_7-mot-so-buc-anh-trong-su-kien","link":"#_7-mot-so-buc-anh-trong-su-kien","children":[]},{"level":2,"title":"8. Còn lại gì sau sự kiện ?","slug":"_8-con-lai-gi-sau-su-kien","link":"#_8-con-lai-gi-sau-su-kien","children":[]},{"level":2,"title":"Kết luận","slug":"ket-luan","link":"#ket-luan","children":[]}],"relativePath":"blog/2025-05-03-chia-se-ve-su-kien-bestaiawards-2025.md","filePath":"blog/2025-05-03-chia-se-ve-su-kien-bestaiawards-2025.md","lastUpdated":1750175461000}');const c=n({name:"blog/2025-05-03-chia-se-ve-su-kien-bestaiawards-2025.md"},[["render",function(n,a,c,g,s,e){return t(),i("div",null,a[0]||(a[0]=[h('<h1 id="chia-se-ve-su-kien-bestaiawards-2025" tabindex="-1">Chia sẻ về sự kiện BestAIAwards 2025 <a class="header-anchor" href="#chia-se-ve-su-kien-bestaiawards-2025" aria-label="Permalink to &quot;Chia sẻ về sự kiện BestAIAwards 2025&quot;">​</a></h1><p>Sự kiện trao giải thưởng BestAIAwards 2025 đã diễn ra vào ngày 3 tháng 5 năm 2025 tại Đài Bắc, Đài Loan. Đây là một sự kiện lớn trong ngành công nghiệp AI, quy tụ nhiều chuyên gia, nhà nghiên cứu và doanh nhân hàng đầu trong lĩnh vực này. Sự kiện không chỉ là nơi trao giải thưởng cho những sản phẩm và dịch vụ AI xuất sắc nhất mà còn là cơ hội để các chuyên gia chia sẻ kiến thức và kinh nghiệm của mình.</p><p>Tôi và Đạt đã vinh dự được tham gia vòng chung kết của sự kiện này với sản phẩm AI của mình. Chúng tôi đã có cơ hội gặp gỡ và trao đổi với nhiều chuyên gia trong ngành. Sự kiện đã mang lại cho chúng tôi nhiều kiến thức bổ ích và cảm hứng và mở rộng mối quan hệ trong ngành công nghiệp AI.</p><p>Chúng tôi đã có cơ hội học hỏi từ những người đi trước và chia sẻ ý tưởng của mình với những người có cùng đam mê. Đó là một trải nghiệm tuyệt vời mà chúng tôi sẽ không bao giờ quên.</p><p>Tôi cũng muốn gửi lời cảm ơn đến tất cả những người đã hỗ trợ chúng tôi trong hành trình này. Đặc biệt là những người đã tin tưởng và ủng hộ chúng tôi từ những ngày đầu. Chúng tôi sẽ tiếp tục nỗ lực để phát triển sản phẩm của mình và đóng góp cho cộng đồng AI.</p><p>Hôm nay, tôi muốn chia sẻ về một số điều thú vị mà tôi đã học được từ sự kiện này. Một sự kiện lớn như BestAIAwards không chỉ là nơi để trao giải thưởng mà còn là nơi để học hỏi và chia sẻ kiến thức.</p><p><img src="https://static-cdn.thanhlv.com/blog/images/2025-05-03-chia-se-ve-su-kien-bestaiawards-2025/bestAI2025.jpg" alt="Image"></p><h2 id="_1-tai-sao-chung-toi-tham-gia-su-kien-nay" tabindex="-1">1. Tại sao chúng tôi tham gia sự kiện này? <a class="header-anchor" href="#_1-tai-sao-chung-toi-tham-gia-su-kien-nay" aria-label="Permalink to &quot;1. Tại sao chúng tôi tham gia sự kiện này?&quot;">​</a></h2><p>Làm tại LINE technology vietnam chúng tôi thường xuyên được biết các thông tin về công nghệ mới và các tin tức mới về công nghệ. Ngay khi <a href="https://www.tca.org.tw/en/" target="_blank" rel="noreferrer">Taipei Computer Association (TCA)</a> thông báo về sự kiện BestAIAwards 2025, LINE technology vietnam đã nhận được thông tin và đã gửi thông báo đến tất cả các nhân viên.</p><p>Sau một thời gian tìm hiểu về sự kiện, tôi đã quyết định tham gia với sản phẩm AI của mình. Tôi đã gửi đơn đăng ký tham gia và phiên bản demo + video giới thiệu sản phẩm của chúng tôi. Sau một thời gian chờ đợi, chúng tôi đã nhận được thông báo rằng sản phẩm của chúng tôi đã được chọn vào vòng chung kết của sự kiện này. Đây là một niềm vui lớn đối với chúng tôi.</p><h2 id="_2-cac-thong-tin-đuoc-đang-cong-khai" tabindex="-1">2. Các thông tin được đăng công khai. <a class="header-anchor" href="#_2-cac-thong-tin-đuoc-đang-cong-khai" aria-label="Permalink to &quot;2. Các thông tin được đăng công khai.&quot;">​</a></h2><p>Tất cả các thông tin về sự kiện đều được đăng công khai trên <a href="https://bestaiawards.com.tw/" target="_blank" rel="noreferrer">trang web của sự kiện.</a>, điều này đảm bảo rằng tất cả mọi người đều có thể theo dõi và cập nhật thông tin về sự kiện. Cũng đảm bảo rằng tất cả các thông tin đều được công khai và minh bạch. Các thông tin về các sản phẩm tham gia, các giải thưởng, các diễn giả, các hoạt động trong sự kiện đều được đăng công khai trên trang web của sự kiện. Điều này giúp cho mọi người có thể theo dõi và cập nhật thông tin về sự kiện một cách dễ dàng.</p><h2 id="_3-tong-quan-quy-trinh-tham-gia-su-kien-nay" tabindex="-1">3. Tổng quan quy trình tham gia sự kiện này. <a class="header-anchor" href="#_3-tong-quan-quy-trinh-tham-gia-su-kien-nay" aria-label="Permalink to &quot;3. Tổng quan quy trình tham gia sự kiện này.&quot;">​</a></h2><ol><li><p>Khoảng tháng 4, <a href="https://www.moea.gov.tw/Mns/doit_e/news/News_En.aspx?kind=6&amp;menu_id=5673&amp;news_id=119252" target="_blank" rel="noreferrer">có 1,253 đội ở 36 quốc gia tương đương 1,253</a> ý tưởng cùng tham gia vào sự kiện này. Tất cả các đội sẽ cùng chuẩn bị các tài liệu chi tiết của dự án và một video demo sản phẩm. Sau đó sẽ gửi đến ban tổ chức để đánh giá.</p></li><li><p>Ban tổ chức đánh giá và chọn ra 233 đội vào vòng chung kết. Các đội sẽ được thông báo qua email và sẽ được yêu cầu chuẩn bị một video demo sản phẩm và một bài thuyết trình về sản phẩm của mình. <img src="https://static-cdn.thanhlv.com/blog/images/2025-05-03-chia-se-ve-su-kien-bestaiawards-2025/5090c410-d7cb-454b-b933-eeef5d897c59.jpg" alt="Image"></p></li><li><p>Một người hỗ trợ sự kiện sẽ liên hệ với các đội để hướng dẫn và hỗ trợ các đội trong việc chuẩn bị cho sự kiện. Với tôi và Đạt, chúng tôi đã được hỗ trợ rất nhiệt tình từ ban tổ chức. Bởi vì chúng tôi là đội tuyển nước ngoài, vì vậy có rất nhiều điều mà chúng tôi không biết. Ban tổ chức đã hỗ trợ chúng tôi rất nhiều trong việc chuẩn bị cho sự kiện. Họ đã giúp chúng tôi tìm hiểu về các quy định của sự kiện, cách thức trình bày sản phẩm và cách thức thuyết trình. Họ cũng đã giúp chúng tôi chuẩn bị các tài liệu cần thiết cho sự kiện.</p></li><li><p>Tất cả các đội lọt vào vòng chung kết cùng nhau đến Đài Bắc, Đài Loan vào ngày 2 tháng 5 năm 2025 để tham gia sự kiện và trình bày sản phẩm của mình. Tất cả các đội sẽ có 5 phút để trình bày sản phẩm của mình và 5 phút để trả lời câu hỏi từ ban giám khảo. Ban giám khảo sẽ đánh giá sản phẩm dựa trên các tiêu chí như tính sáng tạo, tính khả thi, tính ứng dụng và tính thương mại hóa của sản phẩm.</p></li><li><p>Sau khi tất cả các đội đã trình bày sản phẩm của mình, ban giám khảo sẽ họp để đánh giá và chọn ra các đội đạt giải thưởng. Các giải thưởng sẽ được công bố trong buổi lễ trao giải vào cuối sự kiện.</p></li><li><p>Sau khi sự kiện kết thúc, ban tổ chức sẽ gửi email cho tất cả các đội để thông báo về kết quả của sự kiện. Các đội đạt giải thưởng sẽ được công bố trên trang web của sự kiện và sẽ nhận được chứng nhận và giải thưởng từ ban tổ chức.</p></li></ol><h2 id="_4-2-hang-muc-giai-cua-su-kien" tabindex="-1">4. 2 hạng mục giải của sự kiện <a class="header-anchor" href="#_4-2-hang-muc-giai-cua-su-kien" aria-label="Permalink to &quot;4. 2 hạng mục giải của sự kiện&quot;">​</a></h2><p>Sự kiện BestAIAwards 2025 có 2 hạng mục giải thưởng chính là AI Application và IC Design.</p><h3 id="_4-1-ai-application" tabindex="-1">4.1. AI Application <a class="header-anchor" href="#_4-1-ai-application" aria-label="Permalink to &quot;4.1. AI Application&quot;">​</a></h3><p>Đây là các sản phẩm sử dụng AI để giải quyết các vấn đề trong cuộc sống hàng ngày. Các sản phẩm này có thể là các ứng dụng di động, các dịch vụ trực tuyến hoặc các sản phẩm phần mềm.</p><h3 id="_4-2-ic-design" tabindex="-1">4.2. IC Design <a class="header-anchor" href="#_4-2-ic-design" aria-label="Permalink to &quot;4.2. IC Design&quot;">​</a></h3><p>Đây là các sản phẩm sử dụng AI để thiết kế các mạch tích hợp (IC). Với hạng mục này, sản phẩm sẽ cần có phần cứng. Các sản phẩm này có thể là các mạch tích hợp, các bo mạch hoặc các thiết bị điện tử.</p><h2 id="_5-mot-so-san-pham-tham-gia-su-kien" tabindex="-1">5. Một số sản phẩm tham gia sự kiện <a class="header-anchor" href="#_5-mot-so-san-pham-tham-gia-su-kien" aria-label="Permalink to &quot;5. Một số sản phẩm tham gia sự kiện&quot;">​</a></h2><p>Tất cả các sản phẩm tham gia sự kiện đều rất chất lượng và sáng tạo. Các sản phẩm này đều có tính ứng dụng cao và có thể giải quyết được các vấn đề trong cuộc sống hàng ngày. Một số sản phẩm nổi bật mà tôi đã thấy trong sự kiện này là:</p><ul><li><strong>Phần mềm dịch tức thời</strong>: Tôi có cơ hội thử nghiệm sản phẩm này và thấy rằng nó có thể dịch gần như tức thời các ngôn ngữ, AI sẽ tự động phân tích câu nói để xác định ngôn ngữ của người nói. Ví dụ 1 người nói tiếng Anh và 1 người nói tiếng Việt, AI sẽ tự động nhận diện ngôn ngữ của người nói và dịch tức thời. Không cần phải chọn ngôn ngữ. <ul><li>Tốc độ dịch rất nhanh, gần như tức thời. Tôi cảm nhận độ trễ chỉ khoảng 1s.</li><li>Chất lượng dịch rất tốt, gần như không có lỗi ngữ pháp. Tôi đã thử nghiệm với nhiều câu khác nhau và thấy rằng sản phẩm này có thể dịch rất chính xác.</li></ul></li><li>*<em>Thiết bị đánh giá sức khỏe</em>: Một team là các sinh viên tại <a href="https://eng.tmu.edu.tw/" target="_blank" rel="noreferrer">Taipei Medical University</a> đã phát triển 1 thiết bị giúp tự khám sức khỏe tại nhà.(Giải 3)</li><li><strong>Chíp tối ưu thuật toán giúp đào tạo AI</strong>: Sinh viên Đại học Bác Khoa Hà Nội đã phát triển 1 chíp giúp tối ưu hóa thuật toán đào tạo AI. Chíp này có thể giúp giảm thời gian đào tạo AI so với các chíp hiện tại.(Giải 2)</li><li>... và nhiều dự án khác...</li></ul><h2 id="_7-mot-so-buc-anh-trong-su-kien" tabindex="-1">7. Một số bức ảnh trong sự kiện <a class="header-anchor" href="#_7-mot-so-buc-anh-trong-su-kien" aria-label="Permalink to &quot;7. Một số bức ảnh trong sự kiện&quot;">​</a></h2><p><img src="https://static-cdn.thanhlv.com/blog/images/2025-05-03-chia-se-ve-su-kien-bestaiawards-2025/de771408-affb-47b1-99e5-3afc2cb8e9d8.jpg" alt="Image"><img src="https://static-cdn.thanhlv.com/blog/images/2025-05-03-chia-se-ve-su-kien-bestaiawards-2025/e3259137-917d-4c1c-990d-c557e10095ef.jpg" alt="Image"><img src="https://static-cdn.thanhlv.com/blog/images/2025-05-03-chia-se-ve-su-kien-bestaiawards-2025/6d1d25f8-b8b7-45b8-aaab-599a3a033f03.jpg" alt="Image"><img src="https://static-cdn.thanhlv.com/blog/images/2025-05-03-chia-se-ve-su-kien-bestaiawards-2025/2243c580-50a4-43f9-aa29-e65213b4002e.jpg" alt="Image"><img src="https://static-cdn.thanhlv.com/blog/images/2025-05-03-chia-se-ve-su-kien-bestaiawards-2025/89b97962-ee81-43a3-880d-57b70397eed0.jpg" alt="Image"><img src="https://static-cdn.thanhlv.com/blog/images/2025-05-03-chia-se-ve-su-kien-bestaiawards-2025/1340901f-705f-4a97-abf3-e7a49b6e702e.jpg" alt="Image"><img src="https://static-cdn.thanhlv.com/blog/images/2025-05-03-chia-se-ve-su-kien-bestaiawards-2025/86513332-0712-4090-952b-e39869bafb20.jpg" alt="Image"><img src="https://static-cdn.thanhlv.com/blog/images/2025-05-03-chia-se-ve-su-kien-bestaiawards-2025/388d7b88-c3e4-41f0-9dae-60078727f760.jpg" alt="Image"><img src="https://static-cdn.thanhlv.com/blog/images/2025-05-03-chia-se-ve-su-kien-bestaiawards-2025/c13cc082-f541-4b63-a40b-b05e104d90bd.jpg" alt="Image"><img src="https://static-cdn.thanhlv.com/blog/images/2025-05-03-chia-se-ve-su-kien-bestaiawards-2025/c411a0ef-dab2-4cca-bbdc-ed2e99e7dbad.jpg" alt="Image"><img src="https://static-cdn.thanhlv.com/blog/images/2025-05-03-chia-se-ve-su-kien-bestaiawards-2025/be6cfd1d-bd53-465c-8605-f47939846d5f.jpg" alt="Image"><img src="https://static-cdn.thanhlv.com/blog/images/2025-05-03-chia-se-ve-su-kien-bestaiawards-2025/78a25f20-5841-4fb4-8dee-cd4ad8bb1af4.jpg" alt="Image"><img src="https://static-cdn.thanhlv.com/blog/images/2025-05-03-chia-se-ve-su-kien-bestaiawards-2025/6b5dd0e1-83c6-411b-8729-9f73281b1154.jpg" alt="Image"><img src="https://static-cdn.thanhlv.com/blog/images/2025-05-03-chia-se-ve-su-kien-bestaiawards-2025/6a801986-9ac2-43b6-909c-20236578fe4f.jpg" alt="Image"><img src="https://static-cdn.thanhlv.com/blog/images/2025-05-03-chia-se-ve-su-kien-bestaiawards-2025/1d8ce756-15b7-43cc-b404-54fe1593f0a2.jpg" alt="Image"><img src="https://static-cdn.thanhlv.com/blog/images/2025-05-03-chia-se-ve-su-kien-bestaiawards-2025/674eef63-b17a-4143-8b62-b823155811ac.jpg" alt="Image"><img src="https://static-cdn.thanhlv.com/blog/images/2025-05-03-chia-se-ve-su-kien-bestaiawards-2025/1d6f4e2a-8621-4e11-afd8-f6a53ebd6541.jpg" alt="Image"><img src="https://static-cdn.thanhlv.com/blog/images/2025-05-03-chia-se-ve-su-kien-bestaiawards-2025/7f1359d8-52d5-4c10-9781-8bc94ff40bcc.jpg" alt="Image"></p><h2 id="_8-con-lai-gi-sau-su-kien" tabindex="-1">8. Còn lại gì sau sự kiện ? <a class="header-anchor" href="#_8-con-lai-gi-sau-su-kien" aria-label="Permalink to &quot;8. Còn lại gì sau sự kiện ?&quot;">​</a></h2><ol><li>Tôi đã có cơ hội gặp gỡ và trao đổi với nhiều chuyên gia trong ngành. Sự kiện đã mang lại cho tôi nhiều kiến thức bổ ích và cảm hứng và mở rộng mối quan hệ trong ngành công nghiệp AI.</li><li>Tất nhiên rồi, việc nhận được giải thưởng là một điều tuyệt vời. Chúng tôi đã nhận được giải thưởng cho sản phẩm của mình và đây là một niềm vui lớn đối với chúng tôi. Giải thưởng này không chỉ là một sự công nhận cho sản phẩm của chúng tôi mà còn là một động lực để chúng tôi tiếp tục phát triển sản phẩm của mình.</li><li>Chúng tôi đã có cơ hội học hỏi từ những người đi trước và chia sẻ ý tưởng của mình với những người có cùng đam mê. Đó là một trải nghiệm tuyệt vời mà chúng tôi sẽ không bao giờ quên.</li></ol><h2 id="ket-luan" tabindex="-1">Kết luận <a class="header-anchor" href="#ket-luan" aria-label="Permalink to &quot;Kết luận&quot;">​</a></h2><ul><li>Hãy tham gia các sự kiện như BestAIAwards để mở rộng mối quan hệ và học hỏi từ những người đi trước. Đây là một cơ hội tuyệt vời để gặp gỡ và trao đổi với nhiều chuyên gia trong ngành.</li><li>Hãy chuẩn bị kỹ lưỡng cho sự kiện. Các sản phẩm tham gia sự kiện đều rất chất lượng và sáng tạo. Hãy chắc chắn rằng sản phẩm của bạn có tính ứng dụng cao và có thể giải quyết được các vấn đề trong cuộc sống hàng ngày.</li><li>Hãy tự tin và thể hiện bản thân. Đây là một cơ hội tuyệt vời để giới thiệu sản phẩm của bạn và chia sẻ ý tưởng của mình với những người có cùng đam mê.</li><li>Khi còn trẻ, hãy trải nghiệm.</li></ul>',29)]))}]]);export{a as __pageData,c as default};
