import{_ as n,c as h,o as a,b as t}from"./chunks/framework.CDs6etjB.js";const i=JSON.parse('{"title":"Sharding nên chọn database như nào?","description":"Sharding là kỹ thuật phân chia dữ liệu thành các phần nhỏ hơn (gọi là shards) và lưu trữ chúng trên nhiều máy chủ khác nhau.","frontmatter":{"footer":true,"title":"Sharding nên chọn database như nào?","description":"Sharding là kỹ thuật phân chia dữ liệu thành các phần nhỏ hơn (gọi là shards) và lưu trữ chúng trên nhiều máy chủ khác nhau.","authors":["lethanh"],"date":"2025-12-04T00:00:00.000Z","outline":"deep","image":"https://static-cdn.thanhlv.com/short-blog/images/2025-12-04-sharding-nen-chon-database-nhu-nao/sharding.jpg","draft":false},"headers":[{"level":2,"title":"Hiểu về Sharding","slug":"hieu-ve-sharding","link":"#hieu-ve-sharding","children":[{"level":3,"title":"Ví dụ về Sharding","slug":"vi-du-ve-sharding","link":"#vi-du-ve-sharding","children":[]}]},{"level":2,"title":"Tiêu chí lựa chọn Database cho Sharding","slug":"tieu-chi-lua-chon-database-cho-sharding","link":"#tieu-chi-lua-chon-database-cho-sharding","children":[{"level":3,"title":"1. Hỗ trợ Sharding Tự Nhiên hoặc cần tự Triển Khai","slug":"_1-ho-tro-sharding-tu-nhien-hoac-can-tu-trien-khai","link":"#_1-ho-tro-sharding-tu-nhien-hoac-can-tu-trien-khai","children":[{"level":4,"title":"Hỗ trợ Sharding Tự Nhiên","slug":"ho-tro-sharding-tu-nhien","link":"#ho-tro-sharding-tu-nhien","children":[]},{"level":4,"title":"Tự Triển Khai Sharding","slug":"tu-trien-khai-sharding","link":"#tu-trien-khai-sharding","children":[]}]},{"level":3,"title":"2. Khả Năng Mở Rộng","slug":"_2-kha-nang-mo-rong","link":"#_2-kha-nang-mo-rong","children":[]},{"level":3,"title":"3. Hiệu Suất","slug":"_3-hieu-suat","link":"#_3-hieu-suat","children":[]},{"level":3,"title":"4. Tính nhất quán","slug":"_4-tinh-nhat-quan","link":"#_4-tinh-nhat-quan","children":[]},{"level":3,"title":"5. Hỗ trợ truy vấn phức tạp","slug":"_5-ho-tro-truy-van-phuc-tap","link":"#_5-ho-tro-truy-van-phuc-tap","children":[]},{"level":3,"title":"6. Phù hợp với mô hình dữ liệu","slug":"_6-phu-hop-voi-mo-hinh-du-lieu","link":"#_6-phu-hop-voi-mo-hinh-du-lieu","children":[]},{"level":3,"title":"8. Cộng đồng và Hỗ trợ","slug":"_8-cong-đong-va-ho-tro","link":"#_8-cong-đong-va-ho-tro","children":[]},{"level":3,"title":"9. Phù hợp với dự án và quyết định bởi nhóm của bạn.","slug":"_9-phu-hop-voi-du-an-va-quyet-đinh-boi-nhom-cua-ban","link":"#_9-phu-hop-voi-du-an-va-quyet-đinh-boi-nhom-cua-ban","children":[]}]},{"level":2,"title":"Dự án của mình đang làm","slug":"du-an-cua-minh-đang-lam","link":"#du-an-cua-minh-đang-lam","children":[{"level":3,"title":"Lựa chọn Mysql","slug":"lua-chon-mysql","link":"#lua-chon-mysql","children":[]},{"level":3,"title":"Cách triển khai sharding trên Mysql","slug":"cach-trien-khai-sharding-tren-mysql","link":"#cach-trien-khai-sharding-tren-mysql","children":[]}]},{"level":2,"title":"Kết luận","slug":"ket-luan","link":"#ket-luan","children":[]}],"relativePath":"short-blog/2025-12-04-sharding-nen-chon-database-nhu-nao.md","filePath":"short-blog/2025-12-04-sharding-nen-chon-database-nhu-nao.md","lastUpdated":1764867230000}');const c=n({name:"short-blog/2025-12-04-sharding-nen-chon-database-nhu-nao.md"},[["render",function(n,i,c,r,u,d){return a(),h("div",null,[...i[0]||(i[0]=[t('<p>Xin chào, hôm nay trong lúc ngồi chém gió và thảo luận về sharding database với dự án của mình, mình nhận ra có khá nhiều bạn vẫn còn mơ hồ về việc lựa chọn database phù hợp để triển khai sharding. Vậy nên mình quyết định viết bài này để chia sẻ một số kinh nghiệm và gợi ý về việc chọn database khi triển khai sharding.</p><p><img src="https://static-cdn.thanhlv.com/short-blog/images/2025-12-04-sharding-nen-chon-database-nhu-nao/sharding.jpg" alt="Image"></p><h2 id="hieu-ve-sharding" tabindex="-1">Hiểu về Sharding <a class="header-anchor" href="#hieu-ve-sharding" aria-label="Permalink to &quot;Hiểu về Sharding&quot;">​</a></h2><p>Trước tiên, chúng ta cần hiểu rõ sharding là gì.?</p><p>Sharding là kỹ thuật phân chia dữ liệu thành các phần nhỏ hơn (gọi là shards) và lưu trữ chúng trên nhiều máy chủ khác nhau.</p><p>Mục tiêu của sharding là cải thiện hiệu suất, khả năng mở rộng và độ tin cậy của hệ thống.</p><h3 id="vi-du-ve-sharding" tabindex="-1">Ví dụ về Sharding <a class="header-anchor" href="#vi-du-ve-sharding" aria-label="Permalink to &quot;Ví dụ về Sharding&quot;">​</a></h3><p>Dữ liệu của bạn có 100 tỉ bản ghi, việc lưu tất cả trên 1 máy chủ gần như là bất khả thi và rủi do rất cao. Thay vào đó, bạn có thể chia nhỏ dữ liệu này thành 10 shards, mỗi shard chứa 10 tỉ bản ghi và lưu trữ trên 10 máy chủ khác nhau.</p><h2 id="tieu-chi-lua-chon-database-cho-sharding" tabindex="-1">Tiêu chí lựa chọn Database cho Sharding <a class="header-anchor" href="#tieu-chi-lua-chon-database-cho-sharding" aria-label="Permalink to &quot;Tiêu chí lựa chọn Database cho Sharding&quot;">​</a></h2><p>Mình cần lưu ý trước, tất cả các tiêu chí phía dưới đều mang tính chất tham khảo và không phải tuyệt đối. Tùy vào từng dự án, từng yêu cầu cụ thể mà bạn có thể điều chỉnh sao cho phù hợp nhất. Dưới đây là một số tiêu chí quan trọng khi lựa chọn database để triển khai sharding:</p><h3 id="_1-ho-tro-sharding-tu-nhien-hoac-can-tu-trien-khai" tabindex="-1">1. Hỗ trợ Sharding Tự Nhiên hoặc cần tự Triển Khai <a class="header-anchor" href="#_1-ho-tro-sharding-tu-nhien-hoac-can-tu-trien-khai" aria-label="Permalink to &quot;1. Hỗ trợ Sharding Tự Nhiên hoặc cần tự Triển Khai&quot;">​</a></h3><h4 id="ho-tro-sharding-tu-nhien" tabindex="-1">Hỗ trợ Sharding Tự Nhiên <a class="header-anchor" href="#ho-tro-sharding-tu-nhien" aria-label="Permalink to &quot;Hỗ trợ Sharding Tự Nhiên&quot;">​</a></h4><p>Đúng. Chính là tự nhiên, bản thân database bạn sử dụng đã có sẵn cơ chế sharding mà bạn không cần phải tự tay triển khai từ đầu. Một số database như MongoDB, Cassandra, và CockroachDB có hỗ trợ sharding tự nhiên, giúp bạn dễ dàng triển khai và quản lý sharding hơn.</p><p>Tuy nhiên, nhược điểm của các database này thường là tính phức tạp trong việc cấu hình và quản lý, cũng như có thể không phù hợp với tất cả các loại ứng dụng.</p><p>Ví dụ, đối với tìm kiếm dữ liệu dạng <code>like</code> thì các loại database này có thể không tối ưu bằng các database chuyên dụng khác.</p><h4 id="tu-trien-khai-sharding" tabindex="-1">Tự Triển Khai Sharding <a class="header-anchor" href="#tu-trien-khai-sharding" aria-label="Permalink to &quot;Tự Triển Khai Sharding&quot;">​</a></h4><p>Nếu bạn chọn một database không hỗ trợ sharding tự nhiên và bạn vẫn mong muốn triển khai sharding trên database đó thì bạn vẫn có thể tự triển khai sharding. Ví dụ, bạn có thể sử dụng PostgreSQL hoặc MySQL và tự xây dựng cơ chế sharding dựa trên ứng dụng của mình. Tuy nhiên, việc này đòi hỏi bạn phải có kiến thức sâu rộng về database và sharding.</p><p>Có 2 cách để tự triển khai sharding là dùng thư viện bên thứ 3 là một proxy trung gian để xử lý sharding hoặc tự xây dựng logic sharding trong ứng dụng của bạn.</p><h3 id="_2-kha-nang-mo-rong" tabindex="-1">2. Khả Năng Mở Rộng <a class="header-anchor" href="#_2-kha-nang-mo-rong" aria-label="Permalink to &quot;2. Khả Năng Mở Rộng&quot;">​</a></h3><p>Khi lựa chọn database, bạn cần xem xét khả năng mở rộng của nó. Database nên hỗ trợ việc thêm hoặc bớt các shard một cách dễ dàng mà không ảnh hưởng đến hiệu suất của hệ thống. Các database như cassandra cho phép bạn thêm node mới vào cluster một cách dễ dàng mà không cần phải tắt hệ thống, hệ thống sẽ tự động tính toán để rebalance dữ liệu trên các node hiện có.</p><h3 id="_3-hieu-suat" tabindex="-1">3. Hiệu Suất <a class="header-anchor" href="#_3-hieu-suat" aria-label="Permalink to &quot;3. Hiệu Suất&quot;">​</a></h3><p>Hiệu suất là một yếu tố quan trọng khi lựa chọn database cho sharding. Bạn cần đảm bảo rằng database có khả năng xử lý các truy vấn nhanh chóng và hiệu quả trên các shard. Các database như MongoDB và Cassandra, Mysql được thiết kế để xử lý các truy vấn phân tán một cách hiệu quả, giúp cải thiện hiệu suất tổng thể của hệ thống.</p><h3 id="_4-tinh-nhat-quan" tabindex="-1">4. Tính nhất quán <a class="header-anchor" href="#_4-tinh-nhat-quan" aria-label="Permalink to &quot;4. Tính nhất quán&quot;">​</a></h3><p>Đây là một tiêu chí quan trọng khác khi lựa chọn database cho sharding. Bạn cần xem xét cách database xử lý tính nhất quán dữ liệu như nào. Thông thường, các database distributed sẽ tuân theo mô hình CAP theorem, bạn cần xác định xem ứng dụng của bạn ưu tiên tính nhất quán (Consistency), sẵn sàng (Availability) hay phân vùng (Partition tolerance) để lựa chọn database phù hợp.</p><p>Ví dụ như Cassandra ưu tiên Availability và Partition tolerance, trong khi MongoDB có thể cấu hình để ưu tiên Consistency hoặc Availability tùy theo nhu cầu.</p><p>Nếu bạn cần đảm bảo dữ liệu nhất quán thì bạn không thể chọn các database như Cassandra.</p><h3 id="_5-ho-tro-truy-van-phuc-tap" tabindex="-1">5. Hỗ trợ truy vấn phức tạp <a class="header-anchor" href="#_5-ho-tro-truy-van-phuc-tap" aria-label="Permalink to &quot;5. Hỗ trợ truy vấn phức tạp&quot;">​</a></h3><p>Nếu ứng dụng của bạn yêu cầu các truy vấn phức tạp như join, aggregate thì bạn cần chọn database hỗ trợ tốt các loại truy vấn này trên các shard. Ví dụ, PostgreSQL hỗ trợ các truy vấn phức tạp rất tốt, trong khi MongoDB, Cassandra có thể gặp khó khăn với các truy vấn join phức tạp.(Hiệu suất rất chậm)</p><h3 id="_6-phu-hop-voi-mo-hinh-du-lieu" tabindex="-1">6. Phù hợp với mô hình dữ liệu <a class="header-anchor" href="#_6-phu-hop-voi-mo-hinh-du-lieu" aria-label="Permalink to &quot;6. Phù hợp với mô hình dữ liệu&quot;">​</a></h3><p>Bạn cần chọn database phù hợp với mô hình dữ liệu của bạn. Ví dụ, nếu bạn có dữ liệu quan hệ phức tạp thì bạn nên chọn các database quan hệ như PostgreSQL hoặc MySQL. Nếu bạn có dữ liệu phi cấu trúc thì các database NoSQL như MongoDB hoặc Cassandra có thể là lựa chọn tốt hơn.</p><h3 id="_8-cong-đong-va-ho-tro" tabindex="-1">8. Cộng đồng và Hỗ trợ <a class="header-anchor" href="#_8-cong-đong-va-ho-tro" aria-label="Permalink to &quot;8. Cộng đồng và Hỗ trợ&quot;">​</a></h3><p>Bạn nên xem xét cộng đồng và hỗ trợ của database. Một cộng đồng lớn và hoạt động sẽ giúp bạn dễ dàng tìm kiếm tài liệu, giải pháp cho các vấn đề gặp phải khi triển khai sharding. Các database phổ biến như PostgreSQL, MySQL, MongoDB có cộng đồng lớn và nhiều tài liệu hỗ trợ, giúp bạn dễ dàng triển khai và quản lý sharding.</p><h3 id="_9-phu-hop-voi-du-an-va-quyet-đinh-boi-nhom-cua-ban" tabindex="-1">9. Phù hợp với dự án và quyết định bởi nhóm của bạn. <a class="header-anchor" href="#_9-phu-hop-voi-du-an-va-quyet-đinh-boi-nhom-cua-ban" aria-label="Permalink to &quot;9. Phù hợp với dự án và quyết định bởi nhóm của bạn.&quot;">​</a></h3><p>Cuối cùng, bạn cần xem xét dự án của bạn và quyết định bởi nhóm của bạn. Mỗi dự án có những yêu cầu và đặc điểm riêng, do đó bạn cần lựa chọn database phù hợp với nhu cầu cụ thể của dự án. Hãy thảo luận với nhóm của bạn để đưa ra quyết định cuối cùng về việc lựa chọn database cho sharding.</p><h2 id="du-an-cua-minh-đang-lam" tabindex="-1">Dự án của mình đang làm <a class="header-anchor" href="#du-an-cua-minh-đang-lam" aria-label="Permalink to &quot;Dự án của mình đang làm&quot;">​</a></h2><p>Hiện tại dự án của mình đang sử dụng Mysql để triển khai sharding.</p><p>Theo tính toán, mỗi Node sharidng có tổng dung lượng tối đa là 5TB, với dự liệu ước tính trong 5 năm tới sẽ lên tới 40TB, nên mình sẽ cần triển khai ít nhất 8 nodes sharding để lưu trữ dữ liệu.</p><h3 id="lua-chon-mysql" tabindex="-1">Lựa chọn Mysql <a class="header-anchor" href="#lua-chon-mysql" aria-label="Permalink to &quot;Lựa chọn Mysql&quot;">​</a></h3><p>Thực tế, lúc đầu chúng mình lựa chọn Cassandra để triển khai sharding, tuy nhiên Cassandra không hỗ trợ tốt các truy vấn dạng <code>like</code> và các truy vấn phức tạp, điều này ảnh hưởng lớn đến hiệu suất của ứng dụng.</p><p>Vì vậy, chúng mình sẽ cần triển khai thêm một ES hoặc Mysql để hỗ trợ các truy vấn phức tạp này, điều này làm tăng độ phức tạp của hệ thống và chi phí vận hành.</p><p>Sau khi cân nhắc kỹ lưỡng, chúng mình quyết định sử dụng Mysql để triển khai sharding. Mặc dù việc tự triển khai sharding trên Mysql đòi hỏi nhiều công sức và kiến thức, nhưng nó giúp chúng mình kiểm soát tốt hơn về mặt hiệu suất và tính nhất quán của dữ liệu.</p><h3 id="cach-trien-khai-sharding-tren-mysql" tabindex="-1">Cách triển khai sharding trên Mysql <a class="header-anchor" href="#cach-trien-khai-sharding-tren-mysql" aria-label="Permalink to &quot;Cách triển khai sharding trên Mysql&quot;">​</a></h3><p>Lúc đầu, chúng mình cũng có tìm kiếm một số giải pháp proxy trung gian để hỗ trợ sharding trên Mysql như Vitess...etc.... Tuy nhiên, việc triển khai thêm 1 proxy trung gian làm tăng độ phức tạp của hệ thống và có thể ảnh hưởng đến hiệu suất.</p><p>Cuối cùng, chúng mình quyết định tự xây dựng logic sharding trong ứng dụng của mình. Chúng mình sử dụng một hàm băm (hash function) để xác định shard nào sẽ lưu trữ dữ liệu dựa trên một số cặp trường nhất định (ví dụ: user_id, order_id...etc...).</p><p>Điều này giúp chúng mình kiểm soát tốt hơn về cách dữ liệu được phân phối trên các shard và tối ưu hóa hiệu suất truy vấn.</p><p>Tuy nhiên, do mình sử dng Hash function để phân phối dữ liệu nên việc mở rộng thêm sharding là rất phức tạp, cần phải rebalance lại toàn bộ dữ liệu trên các shard hiện có. Đây là một điểm hạn chế lớn của việc tự triển khai sharding trên Mysql.</p><p>Chúng mình cũng đã đánh giá sử dụng <code>Consistent Hashing</code>, tuy nhiên nó sẽ tăng độ phức tạp, vì nếu sử dụng Consistent Hashing thì sẽ cần xây dựng lại toàn bộ cơ chế phân phối dữ liệu và quản lý shard, điều này không phải là main business của chúng mình nên chúng mình quyết định không sử dụng Consistent Hashing.</p><h2 id="ket-luan" tabindex="-1">Kết luận <a class="header-anchor" href="#ket-luan" aria-label="Permalink to &quot;Kết luận&quot;">​</a></h2><p>Việc lựa chọn database để triển khai sharding là một quyết định quan trọng và cần được xem xét kỹ lưỡng dựa trên nhiều tiêu chí khác nhau.</p><p>Có lẽ nhiều bạn bảo dự án của mình chọn Mysql là không tốt, không tối ưu và chưa tính đến tương lai. Mình hoàn toàn đồng ý với bạn, tuy nhiên trong thực tế, việc lựa chọn database không chỉ dựa trên các tiêu chí kỹ thuật mà còn phụ thuộc vào nhiều yếu tố khác như kinh nghiệm của đội ngũ phát triển, chi phí vận hành, và yêu cầu cụ thể của dự án.</p><p>Hiện tại, chúng mình vẫn hài lòng với Mysql ^^.</p><p>Hy vọng bài viết này sẽ giúp bạn có cái nhìn rõ ràng hơn về việc lựa chọn database khi triển khai sharding. Nếu bạn có bất kỳ câu hỏi hoặc muốn chia sẻ kinh nghiệm của mình, hãy để lại bình luận bên dưới nhé!</p>',52)])])}]]);export{i as __pageData,c as default};
