import{_ as n,j as t,o as i,v as h}from"./chunks/framework.OuVixYWh.js";const c=JSON.parse('{"title":"INT11176 - Nhập môn Internet và E-learning","description":"Nhập môn Internet và E-learning là khóa học giúp người học hiểu rõ về E-learning, các đặc điểm, cấu trúc, thành phần của hệ thống E-learning, các phương thức học tập E-learning, và các kỹ năng cần thiết để học E-learning.","frontmatter":{"title":"INT11176 - Nhập môn Internet và E-learning","description":"Nhập môn Internet và E-learning là khóa học giúp người học hiểu rõ về E-learning, các đặc điểm, cấu trúc, thành phần của hệ thống E-learning, các phương thức học tập E-learning, và các kỹ năng cần thiết để học E-learning.","footer":true,"outline":"deep","date":"2025-07-05T00:00:00.000Z","group":"All"},"headers":[{"level":2,"title":"Đặc điểm của E-learning","slug":"đac-điem-cua-e-learning","link":"#đac-điem-cua-e-learning","children":[{"level":3,"title":"Lớp học truyền thống","slug":"lop-hoc-truyen-thong","link":"#lop-hoc-truyen-thong","children":[]},{"level":3,"title":"Lớp học E-learning","slug":"lop-hoc-e-learning","link":"#lop-hoc-e-learning","children":[]}]},{"level":2,"title":"Cấu trúc của hệ thống E-learning","slug":"cau-truc-cua-he-thong-e-learning","link":"#cau-truc-cua-he-thong-e-learning","children":[{"level":3,"title":"Hạ tầng và kỹ thuật","slug":"ha-tang-va-ky-thuat","link":"#ha-tang-va-ky-thuat","children":[]}]},{"level":2,"title":"Các thành phần chính của hệ thống E-learning","slug":"cac-thanh-phan-chinh-cua-he-thong-e-learning","link":"#cac-thanh-phan-chinh-cua-he-thong-e-learning","children":[]},{"level":2,"title":"Các phương thức học tập E-learning","slug":"cac-phuong-thuc-hoc-tap-e-learning","link":"#cac-phuong-thuc-hoc-tap-e-learning","children":[{"level":3,"title":"Synchronous Learning","slug":"synchronous-learning","link":"#synchronous-learning","children":[{"level":4,"title":"Lợi ích:","slug":"loi-ich","link":"#loi-ich","children":[]}]},{"level":3,"title":"Asynchronous Learning","slug":"asynchronous-learning","link":"#asynchronous-learning","children":[{"level":4,"title":"Ví dụ:","slug":"vi-du","link":"#vi-du","children":[]},{"level":4,"title":"Lợi ích:","slug":"loi-ich-1","link":"#loi-ich-1","children":[]}]}]},{"level":2,"title":"Học tập kết hợp (Blended Learning)","slug":"hoc-tap-ket-hop-blended-learning","link":"#hoc-tap-ket-hop-blended-learning","children":[{"level":3,"title":"Lợi ích:","slug":"loi-ich-2","link":"#loi-ich-2","children":[]}]},{"level":2,"title":"Các kỹ năng cần thiết để học E-learning","slug":"cac-ky-nang-can-thiet-đe-hoc-e-learning","link":"#cac-ky-nang-can-thiet-đe-hoc-e-learning","children":[{"level":3,"title":"Kỹ năng sử dụng công nghệ","slug":"ky-nang-su-dung-cong-nghe","link":"#ky-nang-su-dung-cong-nghe","children":[]},{"level":3,"title":"Kỹ năng tự học","slug":"ky-nang-tu-hoc","link":"#ky-nang-tu-hoc","children":[]}]},{"level":2,"title":"Internet là gì?","slug":"internet-la-gi","link":"#internet-la-gi","children":[]},{"level":2,"title":"Ai là người sử dụng Internet?","slug":"ai-la-nguoi-su-dung-internet","link":"#ai-la-nguoi-su-dung-internet","children":[]},{"level":2,"title":"Các dịch vụ Internet","slug":"cac-dich-vu-internet","link":"#cac-dich-vu-internet","children":[]},{"level":2,"title":"WWW là gì?","slug":"www-la-gi","link":"#www-la-gi","children":[]},{"level":2,"title":"Cách dịch vụ phổ biến","slug":"cach-dich-vu-pho-bien","link":"#cach-dich-vu-pho-bien","children":[{"level":3,"title":"Dịch vụ tìm kiếm thông tin","slug":"dich-vu-tim-kiem-thong-tin","link":"#dich-vu-tim-kiem-thong-tin","children":[]},{"level":3,"title":"Email","slug":"email","link":"#email","children":[]},{"level":3,"title":"Dịch vụ truyền tệp tin.","slug":"dich-vu-truyen-tep-tin","link":"#dich-vu-truyen-tep-tin","children":[]},{"level":3,"title":"Dịch vụ kinh doanh điện tử (E-business)","slug":"dich-vu-kinh-doanh-đien-tu-e-business","link":"#dich-vu-kinh-doanh-đien-tu-e-business","children":[]}]},{"level":2,"title":"Lịch sử của Internet","slug":"lich-su-cua-internet","link":"#lich-su-cua-internet","children":[]},{"level":2,"title":"Nhà cung cấp dịch vụ Internet (ISP)","slug":"nha-cung-cap-dich-vu-internet-isp","link":"#nha-cung-cap-dich-vu-internet-isp","children":[]},{"level":2,"title":"Các cách kết nối","slug":"cac-cach-ket-noi","link":"#cac-cach-ket-noi","children":[]},{"level":2,"title":"Website là gì?","slug":"website-la-gi","link":"#website-la-gi","children":[]},{"level":2,"title":"Các thành phần của một website","slug":"cac-thanh-phan-cua-mot-website","link":"#cac-thanh-phan-cua-mot-website","children":[]},{"level":2,"title":"Chi tiết hơn về web server","slug":"chi-tiet-hon-ve-web-server","link":"#chi-tiet-hon-ve-web-server","children":[]}],"relativePath":"study/thanhlv-ptit-hoc-online/2025-07-05-INT11176-nhap-mon-internet-va-elearning.md","filePath":"study/thanhlv-ptit-hoc-online/2025-07-05-INT11176-nhap-mon-internet-va-elearning.md","lastUpdated":1751798662000}'),l={name:"study/thanhlv-ptit-hoc-online/2025-07-05-INT11176-nhap-mon-internet-va-elearning.md"},e=[h('<h1 id="e-learning-la-gi" tabindex="-1">E-learning là gì? <a class="header-anchor" href="#e-learning-la-gi" aria-label="Permalink to &quot;E-learning là gì?&quot;">​</a></h1><ul><li><strong>E-learning</strong> là viết tắt của &quot;Electronic Learning&quot;, nghĩa là học tập thông qua các phương tiện điện tử, thường là qua internet. <img src="https://static-cdn.thanhlv.com/study/thanhlv-study-2025-ptit-hoc-online/2025-07-05-INT11176-nhap-mon-internet-va-elearning/bMz3aFhmjrzCjYaA.png" alt=""></li></ul><h2 id="đac-điem-cua-e-learning" tabindex="-1">Đặc điểm của E-learning <a class="header-anchor" href="#đac-điem-cua-e-learning" aria-label="Permalink to &quot;Đặc điểm của E-learning&quot;">​</a></h2><h3 id="lop-hoc-truyen-thong" tabindex="-1">Lớp học truyền thống <a class="header-anchor" href="#lop-hoc-truyen-thong" aria-label="Permalink to &quot;Lớp học truyền thống&quot;">​</a></h3><ul><li><strong>Lớp học truyền thống</strong> thường diễn ra trong một không gian vật lý, nơi học sinh và giáo viên gặp nhau trực tiếp.</li><li><strong>Đặc điểm</strong>: <ul><li><ol><li>Học sinh và giáo viên gặp nhau trực tiếp tại một không gian học tập cụ thể. (Lớp học, trường học, trung tâm đào tạo...)</li></ol></li><li><ol start="2"><li>Sử dụng bảng đen, sách giáo khoa, và các tài liệu học tập truyền thống, đôi khi vẫn có thể sử dụng máy tính hoặc thiết bị điện tử.</li></ol></li><li><ol start="3"><li>Thời gian học cố định, thường theo lịch trình của trường học.</li></ol></li><li><ol start="4"><li>Học sinh có thể tương tác trực tiếp với giáo viên và bạn học.</li></ol></li></ul></li></ul><h3 id="lop-hoc-e-learning" tabindex="-1">Lớp học E-learning <a class="header-anchor" href="#lop-hoc-e-learning" aria-label="Permalink to &quot;Lớp học E-learning&quot;">​</a></h3><ul><li><strong>Lớp học E-learning</strong> là lớp học trực tuyến, nơi học sinh và giáo viên có thể tương tác qua internet.</li><li><strong>Đặc điểm</strong>: <ul><li>Học sinh và giáo viên có thể ở bất cứ đâu, chỉ cần có kết nối internet.</li><li>Sử dụng các công cụ trực tuyến như video call, chat, và các nền tảng học tập trực tuyến.</li><li>Thời gian học linh hoạt, có thể truy cập tài liệu học tập bất cứ lúc nào.</li><li>Học sinh có thể tương tác với giáo viên và bạn học qua các công cụ trực tuyến.</li></ul></li></ul><h2 id="cau-truc-cua-he-thong-e-learning" tabindex="-1">Cấu trúc của hệ thống E-learning <a class="header-anchor" href="#cau-truc-cua-he-thong-e-learning" aria-label="Permalink to &quot;Cấu trúc của hệ thống E-learning&quot;">​</a></h2><h3 id="ha-tang-va-ky-thuat" tabindex="-1">Hạ tầng và kỹ thuật <a class="header-anchor" href="#ha-tang-va-ky-thuat" aria-label="Permalink to &quot;Hạ tầng và kỹ thuật&quot;">​</a></h3><ul><li><strong>Hạ tầng</strong>: Yêu cầu một hệ thống máy chủ mạnh mẽ để lưu trữ và xử lý dữ liệu học tập, bao gồm video, tài liệu, và các bài tập.</li><li><strong>Hệ thống</strong>: Sử dụng các nền tảng học tập trực tuyến (LMS) <ul><li><strong>Kỹ thuật</strong>: Sử dụng các công nghệ web như HTML, CSS, JavaScript để xây dựng giao diện người dùng, và các ngôn ngữ lập trình như Python, Java, hoặc Node.js để phát triển backend.</li><li><strong>Cơ sở dữ liệu</strong>: Sử dụng các hệ quản trị cơ sở dữ liệu như MySQL, PostgreSQL, hoặc MongoDB để lưu trữ thông tin người dùng, khóa học, và kết quả học tập.</li></ul></li><li><strong>Nội dung hoc tập</strong>: Bao gồm video, bài viết, bài tập, và các tài liệu học tập khác. Nội dung này có thể được tạo ra bởi giáo viên hoặc các chuyên gia trong lĩnh vực.</li></ul><h2 id="cac-thanh-phan-chinh-cua-he-thong-e-learning" tabindex="-1">Các thành phần chính của hệ thống E-learning <a class="header-anchor" href="#cac-thanh-phan-chinh-cua-he-thong-e-learning" aria-label="Permalink to &quot;Các thành phần chính của hệ thống E-learning&quot;">​</a></h2><ul><li><strong>Người dùng</strong>: Bao gồm học sinh, giáo viên, và quản trị viên hệ thống.</li><li><strong>Nền tảng học tập trực tuyến (LMS)</strong>: Là nơi lưu trữ và quản lý các khóa học, tài liệu học tập, và kết quả học tập của người dùng.</li><li><strong>Công cụ tương tác</strong>: Bao gồm các công cụ video call, chat, và các công cụ trực tuyến khác để người dùng có thể tương tác với nhau.</li><li><strong>Công cụ đánh giá</strong>: Bao gồm các bài kiểm tra, bài tập, và các công cụ đánh giá khác để đánh giá kết quả học tập của người dùng.</li><li><strong>Công cụ quản lý</strong>: Bao gồm các công cụ để quản lý người dùng, khóa học, và các tài liệu học tập. Quản trị viên có thể sử dụng các công cụ này để theo dõi tiến độ học tập của người dùng, quản lý nội dung học tập, và xử lý các vấn đề kỹ thuật.</li></ul><h2 id="cac-phuong-thuc-hoc-tap-e-learning" tabindex="-1">Các phương thức học tập E-learning <a class="header-anchor" href="#cac-phuong-thuc-hoc-tap-e-learning" aria-label="Permalink to &quot;Các phương thức học tập E-learning&quot;">​</a></h2><ul><li>Có 2 kiểu học tập E-learning chính: <ul><li><strong>Synchronous Learning</strong>: Học trực tuyến theo thời gian thực, có thể tương tác với giảng viên và bạn học.</li><li><strong>Asynchronous Learning</strong>: Học trực tuyến không theo thời gian thực, có thể truy cập tài liệu học tập bất cứ lúc nào.</li><li><strong>Blended Learning</strong>: Kết hợp giữa học trực tuyến và học truyền thống, cho phép linh hoạt trong việc học tập.</li></ul></li></ul><h3 id="synchronous-learning" tabindex="-1">Synchronous Learning <a class="header-anchor" href="#synchronous-learning" aria-label="Permalink to &quot;Synchronous Learning&quot;">​</a></h3><ul><li><strong>Đặc điểm</strong>: <ul><li>Học trực tuyến theo thời gian thực.</li><li>Có thể tương tác với giảng viên và bạn học.</li><li>Thường sử dụng video call, chat, hoặc các công cụ trực tuyến khác.</li></ul></li></ul><h4 id="loi-ich" tabindex="-1">Lợi ích: <a class="header-anchor" href="#loi-ich" aria-label="Permalink to &quot;Lợi ích:&quot;">​</a></h4><ul><li>Có thể tương tác trực tiếp với giảng viên và bạn học.</li><li>Giúp duy trì động lực học tập tốt hơn là học một mình --&gt; Con người là động vật xã hội, cần có sự tương tác với người khác để duy trì động lực học tập.</li><li>Giúp giải quyết các vấn đề khó khăn ngay lập tức. Có thể hỏi giảng viên hoặc bạn học ngay lập tức khi gặp khó khăn trong quá trình học tập.</li></ul><h3 id="asynchronous-learning" tabindex="-1">Asynchronous Learning <a class="header-anchor" href="#asynchronous-learning" aria-label="Permalink to &quot;Asynchronous Learning&quot;">​</a></h3><ul><li><strong>Đặc điểm</strong>: <ul><li>Học trực tuyến không theo thời gian thực.</li><li>Có thể truy cập tài liệu học tập bất cứ lúc nào.</li><li>Thường sử dụng video, bài viết, hoặc các tài liệu học tập khác.</li></ul></li></ul><h4 id="vi-du" tabindex="-1">Ví dụ: <a class="header-anchor" href="#vi-du" aria-label="Permalink to &quot;Ví dụ:&quot;">​</a></h4><ul><li>Bạn ra quán coffee ngồi học thông qua E-learning platform như Coursera, Udemy, hoặc chính LMS của trường.</li></ul><h4 id="loi-ich-1" tabindex="-1">Lợi ích: <a class="header-anchor" href="#loi-ich-1" aria-label="Permalink to &quot;Lợi ích:&quot;">​</a></h4><ul><li>Linh hoạt khi học tập, có thể học bất cứ đầu và bất cứ lúc nào.</li><li>Có thể học theo tốc độ của riêng mình, không bị áp lực về thời gian.</li><li>Có thể truy cập lại tài liệu học tập bất cứ lúc nào để ôn tập hoặc xem lại.</li><li>Không cần phải tham gia vào các buổi học trực tuyến theo thời gian thực, giúp tiết kiệm thời gian và công sức.</li><li>Có thể học tập ở bất cứ đâu, chỉ cần có kết nối internet.</li></ul><h2 id="hoc-tap-ket-hop-blended-learning" tabindex="-1">Học tập kết hợp (Blended Learning) <a class="header-anchor" href="#hoc-tap-ket-hop-blended-learning" aria-label="Permalink to &quot;Học tập kết hợp (Blended Learning)&quot;">​</a></h2><ul><li><strong>Đặc điểm</strong>: <ul><li>Kết hợp giữa học trực tuyến và học truyền thống.</li><li>Cho phép linh hoạt trong việc học tập.</li><li>Có thể học trực tuyến và tham gia các buổi học trực tiếp tại lớp học. Sau đó, có thể truy cập lại tài liệu học tập trực tuyến để ôn tập hoặc xem lại.</li></ul></li></ul><h3 id="loi-ich-2" tabindex="-1">Lợi ích: <a class="header-anchor" href="#loi-ich-2" aria-label="Permalink to &quot;Lợi ích:&quot;">​</a></h3><ul><li>Kết hợp giữa lợi ích của học trực tuyến và học truyền thống.</li><li>Cho phép linh hoạt trong việc học tập, có thể học trực tuyến hoặc tham gia các buổi học trực tiếp tại lớp học.</li><li>Giúp duy trì động lực học tập tốt hơn là học một mình, vì có thể tương tác với giảng viên và bạn học trong các buổi học trực tiếp.</li><li>Giúp giải quyết các vấn đề khó khăn ngay lập tức trong các buổi học trực tiếp, có thể hỏi giảng viên hoặc bạn học ngay lập tức khi gặp khó khăn trong quá trình học tập.</li></ul><h2 id="cac-ky-nang-can-thiet-đe-hoc-e-learning" tabindex="-1">Các kỹ năng cần thiết để học E-learning <a class="header-anchor" href="#cac-ky-nang-can-thiet-đe-hoc-e-learning" aria-label="Permalink to &quot;Các kỹ năng cần thiết để học E-learning&quot;">​</a></h2><h3 id="ky-nang-su-dung-cong-nghe" tabindex="-1">Kỹ năng sử dụng công nghệ <a class="header-anchor" href="#ky-nang-su-dung-cong-nghe" aria-label="Permalink to &quot;Kỹ năng sử dụng công nghệ&quot;">​</a></h3><ul><li><strong>Truy cập internet</strong>: Biết cách sử dụng các thiết bị điện tử như máy tính, laptop, hoặc điện thoại thông minh để truy cập internet.</li><li><strong>Sử dụng các công cụ trực tuyến</strong>: Biết cách sử dụng các công cụ trực tuyến như video call, chat, và các nền tảng học tập trực tuyến.</li><li><strong>Tìm kiếm thông tin</strong>: Biết cách tìm kiếm thông tin trên internet, kho dữ liệu của trừng, sử dụng các công cụ tìm kiếm như Google, và đánh giá độ tin cậy của các nguồn thông tin.</li><li><strong>Giao tiếp trực tuyến</strong>: Biết cách giao tiếp trực tuyến qua email, chat, và các công cụ trực tuyến khác.</li><li><strong>Cộng tác trực tuyến</strong>: Biết cách làm việc nhóm trực tuyến, sử dụng các công cụ như Google Docs, Trello, hoặc Slack để cộng tác với người khác.</li><li><strong>Quản lý thời gian</strong>: Biết cách quản lý thời gian học tập, đặt lịch học, và theo dõi tiến độ học tập của bản thân.</li></ul><h3 id="ky-nang-tu-hoc" tabindex="-1">Kỹ năng tự học <a class="header-anchor" href="#ky-nang-tu-hoc" aria-label="Permalink to &quot;Kỹ năng tự học&quot;">​</a></h3><ul><li><strong>THói quen học tập</strong>: Biết tự học, tự nghiên cứu và tự đánh giá kết quả học tập của mình.</li><li><strong>Tự động viên</strong>: Biết cách tự động viên bản thân để duy trì động lực học tập, đặc biệt là khi học trực tuyến một mình.</li><li><strong>Tính tự giác</strong>: Tự giác trong việc hoàn thành bài tập, tham gia các buổi học trực tuyến, và theo dõi tiến độ học tập của bản thân.</li><li><strong>Độc lập</strong>: Biết cách làm việc độc lập, tự giải quyết các vấn đề khó khăn trong quá trình học tập mà không cần sự giúp đỡ của người khác.</li></ul><h1 id="tim-hieu-ve-internet" tabindex="-1">Tìm hiểu về Internet <a class="header-anchor" href="#tim-hieu-ve-internet" aria-label="Permalink to &quot;Tìm hiểu về Internet&quot;">​</a></h1><h2 id="internet-la-gi" tabindex="-1">Internet là gì? <a class="header-anchor" href="#internet-la-gi" aria-label="Permalink to &quot;Internet là gì?&quot;">​</a></h2><p>Internet là một mạng lưới toàn cầu kết nối hàng triệu máy tính và thiết bị, cho phép người dùng truy cập thông tin, giao tiếp, và chia sẻ dữ liệu với nhau.</p><p>Hiểu 1 cách đơn giản hơn, bạn có 1 dây cáp mạng, bạn kết nối máy của bạn trực tiếp với máy của <strong>Tuấn</strong>, bạn có thể gửi và nhận dữ liệu từ máy của người khác thông qua dây cáp mạng đó.</p><p>Khi đó, một mạng internet nhỏ đã được hình thành, mang internet này chỉ có máy của bạn và Tuấn kết nối với nhau.</p><p>Mạng internet này có thể được mở rộng bằng cách kết nối thêm nhiều máy tính khác, tạo thành một mạng lưới lớn hơn.</p><p>Đó chính là mang internet mà chúng ta đang sử dụng ngày nay. Các nhóm người, tổ chức, và công ty, quốc gia đã kết nối hàng triệu máy tính và thiết bị với nhau để tạo thành một mạng lưới toàn cầu.</p><p>Thông thường, các máy tính, thiết bị di động, sử dụng một giao thức chung để giao tiếp với nhau, đó là <strong>TCP/IP</strong> (Transmission Control Protocol/Internet Protocol).</p><h2 id="ai-la-nguoi-su-dung-internet" tabindex="-1">Ai là người sử dụng Internet? <a class="header-anchor" href="#ai-la-nguoi-su-dung-internet" aria-label="Permalink to &quot;Ai là người sử dụng Internet?&quot;">​</a></h2><p>Internet và web đã ảnh hưởng đến sâu rộng đến cuộc sống của chúng ta hiện nay.</p><ul><li><strong>Người dùng cá nhân</strong>: Sử dụng Internet để truy cập thông tin, giao tiếp, giải trí, và học tập.</li><li><strong>Doanh nghiệp</strong>: Sử dụng Internet để quảng bá sản phẩm, dịch vụ, và kết nối với khách hàng.</li><li><strong>Chính phủ</strong>: Sử dụng Internet để cung cấp dịch vụ công, thông tin, và tương tác với công dân.</li><li><strong>Tổ chức phi lợi nhuận</strong>: Sử dụng Internet để vận động, quyên góp, và kết nối với cộng đồng.</li><li><strong>Nhà nghiên cứu</strong>: Sử dụng Internet để truy cập dữ liệu, công bố nghiên cứu, và hợp tác với các nhà nghiên cứu khác.</li><li><strong>Giáo dục</strong>: Sử dụng Internet để cung cấp tài liệu học tập, giảng dạy trực tuyến, và kết nối với học sinh, sinh viên.</li><li><strong>Ngành công nghiệp giải trí</strong>: Sử dụng Internet để phát hành phim, âm nhạc, trò chơi, và các nội dung giải trí khác.</li><li><strong>Ngành thương mại điện tử</strong>: Sử dụng Internet để bán hàng trực tuyến, quản lý kho hàng, và giao hàng.</li><li>.... Và rất nhiều lĩnh vực khác.</li></ul><p>Bất cứ ai sử dụng internet thì đểu có thể nói là ngưới sử dụng internet.</p><h2 id="cac-dich-vu-internet" tabindex="-1">Các dịch vụ Internet <a class="header-anchor" href="#cac-dich-vu-internet" aria-label="Permalink to &quot;Các dịch vụ Internet&quot;">​</a></h2><ul><li>Duyệt và tìm kiếm thông tin: Sử dụng các trình duyệt web như Chrome, Firefox, Safari để truy cập các trang web và tìm kiếm thông tin. Ví dụ như google, stackoverflow, wikipedia...</li><li>Giao tiếp: Sử dụng email, mạng xã hội, và các ứng dụng nhắn tin để giao tiếp với người khác. Ví dụ như Gmail, Facebook, Zalo, Telegram...</li><li>Giải trí: Xem video, nghe nhạc, chơi game trực tuyến, và đọc sách điện tử. Ví dụ như Youtube, Spotify, Netflix...</li><li>Tải lên và tải về các dữ liệu: Sử dụng các dịch vụ lưu trữ đám mây như Google Drive, Dropbox để lưu trữ và chia sẻ dữ liệu. Ví dụ như Google Drive, Dropbox, OneDrive...</li></ul><h2 id="www-la-gi" tabindex="-1">WWW là gì? <a class="header-anchor" href="#www-la-gi" aria-label="Permalink to &quot;WWW là gì?&quot;">​</a></h2><p><strong>WWW</strong> (World Wide Web) là một hệ thống các trang web liên kết với nhau thông qua các liên kết (hyperlinks) và được truy cập thông qua trình duyệt web.</p><p>Thường được gọi tắt là web, là một phần của Internet, nhưng không phải là toàn bộ Internet.</p><p>WWW sử dụng Markup ngôn ngữ HTML (HyperText Markup Language) để định dạng nội dung và CSS (Cascading Style Sheets) để trình bày nội dung.</p><p>WWW cũng sẽ cần sử dụng các máy chủ web để lưu trữ các trang web và các tài nguyên liên quan, khi người dùng truy cập vào một trang web, trình duyệt sẽ gửi yêu cầu đến máy chủ web để lấy nội dung của trang web đó.</p><p>Trình duyệt là các công cụ hỗ trợ để người dùng thao trên trình duyệt</p><h2 id="cach-dich-vu-pho-bien" tabindex="-1">Cách dịch vụ phổ biến <a class="header-anchor" href="#cach-dich-vu-pho-bien" aria-label="Permalink to &quot;Cách dịch vụ phổ biến&quot;">​</a></h2><h3 id="dich-vu-tim-kiem-thong-tin" tabindex="-1">Dịch vụ tìm kiếm thông tin <a class="header-anchor" href="#dich-vu-tim-kiem-thong-tin" aria-label="Permalink to &quot;Dịch vụ tìm kiếm thông tin&quot;">​</a></h3><p>Các tài nguyên cung cấp dưới dạng website, được lưu trữ trên các máy chủ web và có thể truy cập thông qua các trình duyệt web.</p><p>Để dễ dàng tìm kiếm hơn, các ông lớn công nghệ đã tạo ra các dịch vụ hỗ trợ tìm kiếm thông tin trên web, giúp người dùng có thể tìm kiếm thông tin một cách nhanh chóng và dễ dàng. Ví dụ google, bing, yahoo... hoặc hiện tại có thêm là AI như chatgpt, gemini.</p><h3 id="email" tabindex="-1">Email <a class="header-anchor" href="#email" aria-label="Permalink to &quot;Email&quot;">​</a></h3><ul><li><strong>Email</strong> là một dịch vụ cho phép người dùng gửi và nhận thư điện tử qua Internet. Nó giống như chúng ta gửi thư qua bưu điện vậy.</li></ul><h3 id="dich-vu-truyen-tep-tin" tabindex="-1">Dịch vụ truyền tệp tin. <a class="header-anchor" href="#dich-vu-truyen-tep-tin" aria-label="Permalink to &quot;Dịch vụ truyền tệp tin.&quot;">​</a></h3><ul><li><strong>FTP (File Transfer Protocol)</strong>: Là một giao thức cho phép người dùng truyền tệp tin qua Internet. Nó cho phép người dùng tải lên và tải xuống các tệp tin từ máy chủ FTP.</li></ul><h3 id="dich-vu-kinh-doanh-đien-tu-e-business" tabindex="-1">Dịch vụ kinh doanh điện tử (E-business) <a class="header-anchor" href="#dich-vu-kinh-doanh-đien-tu-e-business" aria-label="Permalink to &quot;Dịch vụ kinh doanh điện tử (E-business)&quot;">​</a></h3><ul><li>Giúp doanh nghiệp tương tác với khác hàng (B2C)</li><li>Doanh nghiệp tương tác với doanh nghiệp khác (B2B)</li><li>Doanh nghiệp tương tác với chính phủ (B2G)</li><li>Doanh nghiệp với nhân viên (B2E)</li><li>Người tiêu dùng với người tiêu dùng (C2C)</li></ul><h2 id="lich-su-cua-internet" tabindex="-1">Lịch sử của Internet <a class="header-anchor" href="#lich-su-cua-internet" aria-label="Permalink to &quot;Lịch sử của Internet&quot;">​</a></h2><ul><li><strong>1960s</strong>: Internet được phát triển ban đầu như một dự án nghiên cứu của Bộ Quốc phòng Hoa Kỳ, gọi là ARPANET (Advanced Research Projects Agency Network) để kết nối các máy tính của các trường đại học và viện nghiên cứu.</li><li><strong>1970s</strong>: ARPANET trở thành mạng lưới đầu tiên sử dụng giao thức TCP/IP, cho phép các máy tính khác nhau giao tiếp với nhau.</li><li><strong>1980s</strong>: Internet bắt đầu mở rộng ra ngoài các trường đại học và viện nghiên cứu, với sự ra đời của các dịch vụ như email và FTP.</li><li><strong>1985</strong>: NSFNET (Network Solutions) được thành lập, cung cấp dịch vụ đăng ký tên miền và quản lý DNS. Cho phép thương mại hóa Internet. Vào năm 1995.</li><li><strong>1991</strong>: World Wide Web (WWW) được phát minh bởi Tim Berners-Lee, cho phép người dùng truy cập các trang web thông qua trình duyệt web.</li></ul><h2 id="nha-cung-cap-dich-vu-internet-isp" tabindex="-1">Nhà cung cấp dịch vụ Internet (ISP) <a class="header-anchor" href="#nha-cung-cap-dich-vu-internet-isp" aria-label="Permalink to &quot;Nhà cung cấp dịch vụ Internet (ISP)&quot;">​</a></h2><ul><li><strong>ISP (Internet Service Provider)</strong> là các công ty cung cấp dịch vụ kết nối Internet cho người dùng. Họ cung cấp các gói cước khác nhau để người dùng có thể truy cập Internet.</li><li>Các ISP cung cấp các dịch vụ như: <ul><li>Kết nối Internet qua cáp quang, ADSL, hoặc mạng di động.</li><li>Cung cấp địa chỉ IP cho người dùng.</li><li>Cung cấp các dịch vụ email, lưu trữ web, và các dịch vụ khác.</li></ul></li><li>Mỗi một quốc gia sẽ có các nhà cung cấp dịch vụ Internet khác nhau, ví dụ như: <ul><li>Ở Việt Nam: FPT, Viettel, VNPT, MobiFone, VTC...</li><li>Ở Mỹ: Comcast, AT&amp;T, Verizon, CenturyLink...</li><li>Ở Châu Âu: BT Group (Anh), Deutsche Telekom (Đức), Orange (Pháp)...</li></ul></li><li>Khi chọn nhà cung cấp dịch vụ, tốc độ kết nối là một yếu tố quan trọng. Tốc độ kết nối được tính toán dựa trên số các Bit có thể truyền đi trong 1 giây (Bits per second - bps). Tốc độ kết nối càng cao, người dùng càng có thể truy cập Internet nhanh chóng và hiệu quả hơn.</li></ul><h2 id="cac-cach-ket-noi" tabindex="-1">Các cách kết nối <a class="header-anchor" href="#cac-cach-ket-noi" aria-label="Permalink to &quot;Các cách kết nối&quot;">​</a></h2><ul><li>Cáp ( Cable): Sử dụng cáp đồng trục hoặc cáp quang để kết nối Internet. Đây là phương pháp phổ biến nhất hiện nay, cung cấp tốc độ cao và ổn định.)</li><li>Đường dây điện thoại (DSL): Sử dụng đường dây điện thoại để kết nối Internet. Tốc độ kết nối thường thấp hơn so với cáp, nhưng vẫn đủ để sử dụng các dịch vụ cơ bản như email và duyệt web.</li><li>ĐƯờng dây thuê bao số bất đối xứng (ADSL): Là một phiên bản nâng cao của DSL, cho phép tốc độ tải xuống cao hơn so với tốc độ tải lên.</li><li>Wireless (Wi-Fi): Sử dụng sóng vô tuyến để kết nối Internet. Đây là phương pháp phổ biến cho các thiết bị di động như điện thoại, máy tính bảng, và laptop. <ul><li>Wireless Fidelity (Wi-Fi): Đây là công nghệ kết nối mạng.</li><li>Hotspot: Là một điểm truy cập Wi-Fi công cộng, cho phép người dùng kết nối Internet miễn phí hoặc với một khoản phí nhỏ.</li><li>Điểm truy cập không dây (Wireless Access Point - WAP): Là một thiết bị cho phép kết nối các thiết bị không dây với mạng Internet.</li></ul></li><li>Truy cập mạng qua vệ tinh (Satellite): Sử dụng vệ tinh để kết nối Internet. Phương pháp này thường được sử dụng ở các khu vực xa xôi hoặc không có hạ tầng cáp quang.</li></ul><h1 id="dich-vu-web" tabindex="-1">Dịch vụ web <a class="header-anchor" href="#dich-vu-web" aria-label="Permalink to &quot;Dịch vụ web&quot;">​</a></h1><h2 id="website-la-gi" tabindex="-1">Website là gì? <a class="header-anchor" href="#website-la-gi" aria-label="Permalink to &quot;Website là gì?&quot;">​</a></h2><p>Một website là một tập hợp các các web được liên kết với nhau. Một website sẽ chứa nhiều web page (trang web), mỗi trang web sẽ có một địa chỉ URL riêng biệt. Web home là trang chủ của một website, thường là trang đầu tiên mà người dùng truy cập khi vào website đó.</p><h2 id="cac-thanh-phan-cua-mot-website" tabindex="-1">Các thành phần của một website <a class="header-anchor" href="#cac-thanh-phan-cua-mot-website" aria-label="Permalink to &quot;Các thành phần của một website&quot;">​</a></h2><ul><li><strong>Domain name</strong>: Là tên miền của website, giúp người dùng dễ dàng nhớ và truy cập vào website. Ví dụ: thanhlv.com, google.com, facebook.com...</li><li><strong>Web server</strong>: Là máy chủ lưu trữ các trang web và các tài nguyên liên quan. Khi người dùng truy cập vào một trang web, trình duyệt sẽ gửi yêu cầu đến máy chủ web để lấy nội dung của trang web đó.</li><li><strong>Web browser</strong>: Là phần mềm cho phép người dùng truy cập và duyệt các trang web. Ví dụ: Google Chrome, Mozilla Firefox, Safari, Microsoft Edge...</li><li><strong>Web page</strong>: Là một trang web cụ thể trong một website, có thể chứa văn bản, hình ảnh, video, và các tài nguyên khác. Mỗi trang web sẽ có một địa chỉ URL riêng biệt.</li><li><strong>HTML (HyperText Markup Language)</strong>: Là ngôn ngữ đánh dấu được sử dụng để tạo cấu trúc và nội dung của các trang web. HTML cho phép định dạng văn bản, chèn hình ảnh, video, và các liên kết.</li><li><strong>CSS (Cascading Style Sheets)</strong>: Là ngôn ngữ được sử dụng để định dạng và trình bày các trang web. CSS cho phép thay đổi màu sắc, font chữ, kích thước, và bố cục của các phần tử trên trang web.</li><li><strong>JavaScript</strong>: Là ngôn ngữ lập trình được sử dụng để tạo các hiệu ứng động và tương tác trên các trang web. JavaScript cho phép người dùng tương tác với các phần tử trên trang web, xử lý sự kiện, và thực hiện các tác vụ khác.</li><li><strong>Database</strong>: Là nơi lưu trữ dữ liệu của website, bao gồm thông tin người dùng, nội dung bài viết, và các tài nguyên khác. Các hệ quản trị cơ sở dữ liệu phổ biến bao gồm MySQL, PostgreSQL, MongoDB, và SQLite.</li></ul><h2 id="chi-tiet-hon-ve-web-server" tabindex="-1">Chi tiết hơn về web server <a class="header-anchor" href="#chi-tiet-hon-ve-web-server" aria-label="Permalink to &quot;Chi tiết hơn về web server&quot;">​</a></h2><ul><li><strong>Web server</strong> là phần mềm hoặc phần cứng chịu trách nhiệm lưu trữ, xử lý và phục vụ các trang web cho người dùng thông qua Internet.</li><li><strong>Chức năng của web server</strong>: <ul><li>Nhận yêu cầu từ trình duyệt web (HTTP request).</li><li>Xử lý yêu cầu và truy xuất dữ liệu từ cơ sở dữ liệu hoặc các tệp tin trên máy chủ.</li><li>Gửi lại dữ liệu cho trình duyệt web (HTTP response) để hiển thị trang web cho người dùng.</li></ul></li><li>Một webserver có thể chứa nhiều website khác nhau, mỗi website sẽ có một tên miền riêng biệt.</li><li>Web server thường theo mô hình client-server, trong đó trình duyệt web (client) gửi yêu cầu đến máy chủ web (server) để lấy nội dung của trang web.</li><li>Địa chỉIP (IP address): Là một địa chỉ duy nhất được gán cho mỗi máy tính hoặc thiết bị kết nối Internet. Địa chỉ IP giúp xác định vị trí của máy tính trên mạng và cho phép các máy tính giao tiếp với nhau. <ul><li>Bạn có thể hiểu địa chỉ IP giống như địa chỉ nhà của bạn, giúp người khác có thể gửi thư đến cho bạn.</li><li>Khi bạn đăng ký mạng Internet, nhà cung cấp dịch vụ sẽ gán cho bạn một địa chỉ IP duy nhất. Địa chỉ IP có thể là tĩnh (không thay đổi) hoặc động (thay đổi theo thời gian). <ul><li>IP động là IP dùng chung xoay vòng giữa các người dùng, thường được sử dụng trong các mạng gia đình hoặc văn phòng nhỏ. <ul><li>Hoặc đơng giản khi bạn kết nối thiết bị vào mạng Lan nhà bạn, máy sẽ tự động được cấp phát một địa chỉ IP động từ router của bạn, nếu rút ra cắm lại, địa chỉ IP có thể sẽ thay đổi.</li></ul></li><li>IP tĩnh là IP cố định, không thay đổi theo thời gian, thường được sử dụng trong các máy chủ web hoặc các dịch vụ yêu cầu kết nối ổn định. <ul><li>Khi đăng ký IP tĩnh, bạn sẽ phải trả thêm một khoản phí hàng tháng cho nhà cung cấp dịch vụ Internet.</li></ul></li></ul></li><li>CÓ 2 loại địa chỉ IP chính: <ul><li>IPv4: Là phiên bản đầu tiên của địa chỉ IP, sử dụng 32 bit để định danh. <ul><li>IPv4 có dạng xxx.xxx.xxx.xxx, trong đó mỗi xxx là một số từ 0 đến 255. Ví dụ: 123.456.789.012</li></ul></li><li>IPv6: Là phiên bản mới hơn của địa chỉ IP, sử dụng 128 bit để định danh, cho phép có nhiều địa chỉ IP hơn so với IPv4. <ul><li>IPv6 có dạng xxxx:xxxx:xxxx:xxxx:xxxx:xxxx:xxxx:xxxx, trong đó mỗi xxxx là một số hexa từ 0 đến F. Ví dụ: 2001:0db8:85a3:0000:0000:8a2e:0370:7334</li></ul></li></ul></li></ul></li></ul>',76)];const g=n(l,[["render",function(n,h,c,l,g,a){return i(),t("div",null,e)}]]);export{c as __pageData,g as default};
