import{_ as n,j as t,o as i,v as h}from"./chunks/framework.OuVixYWh.js";const a=JSON.parse('{"title":"Internet hoạt động như thế nào?","description":"","frontmatter":{"footer":true,"title":"Internet hoạt động như thế nào?","authors":["lethanh"],"date":"2024-03-14T00:00:00.000Z","image":"https://static-cdn.thanhlv.com/study/thanhlv-study-2024/2024-03-14-Internet-Internet-hoat-dong-nhu-the-nao/Arpanet_logical_map_march_1977.png","outline":"deep","group":"1. Internet"},"headers":[{"level":2,"title":"Lịch sử của  Internet","slug":"lich-su-cua-internet","link":"#lich-su-cua-internet","children":[{"level":3,"title":"1. Trước thời sơ khai (Trước công nguyên :V)","slug":"_1-truoc-thoi-so-khai-truoc-cong-nguyen-v","link":"#_1-truoc-thoi-so-khai-truoc-cong-nguyen-v","children":[]},{"level":3,"title":"2. Mạng Internet tập trung đầu tiên.","slug":"_2-mang-internet-tap-trung-đau-tien","link":"#_2-mang-internet-tap-trung-đau-tien","children":[]},{"level":3,"title":"2. Thời Kỳ Sơ Khai (1960s - 1974s)","slug":"_2-thoi-ky-so-khai-1960s-1974s","link":"#_2-thoi-ky-so-khai-1960s-1974s","children":[]},{"level":3,"title":"3. Phát Triển và Mở Rộng (1974 - 1989)","slug":"_3-phat-trien-va-mo-rong-1974-1989","link":"#_3-phat-trien-va-mo-rong-1974-1989","children":[]},{"level":3,"title":"4. Sự Bùng Nổ của World Wide Web (1990s)","slug":"_4-su-bung-no-cua-world-wide-web-1990s","link":"#_4-su-bung-no-cua-world-wide-web-1990s","children":[]}]},{"level":2,"title":"Internet hoạt động như thế nào?","slug":"internet-hoat-đong-nhu-the-nao-1","link":"#internet-hoat-đong-nhu-the-nao-1","children":[{"level":3,"title":"3 thành phần chính","slug":"_3-thanh-phan-chinh","link":"#_3-thanh-phan-chinh","children":[]},{"level":3,"title":"ví dụ","slug":"vi-du","link":"#vi-du","children":[]}]},{"level":2,"title":"Lợi và hại của Internet","slug":"loi-va-hai-cua-internet","link":"#loi-va-hai-cua-internet","children":[{"level":3,"title":"Lợi ích của mạng Internet","slug":"loi-ich-cua-mang-internet","link":"#loi-ich-cua-mang-internet","children":[]},{"level":3,"title":"Tác động tiêu cực của Internet","slug":"tac-đong-tieu-cuc-cua-internet","link":"#tac-đong-tieu-cuc-cua-internet","children":[]}]},{"level":2,"title":"Tổng kết","slug":"tong-ket","link":"#tong-ket","children":[]}],"relativePath":"study/thanhlv-study-2024/2024-03-14-Internet-Internet-hoat-dong-nhu-the-nao.md","filePath":"study/thanhlv-study-2024/2024-03-14-Internet-Internet-hoat-dong-nhu-the-nao.md","lastUpdated":1748360452000}'),e={name:"study/thanhlv-study-2024/2024-03-14-Internet-Internet-hoat-dong-nhu-the-nao.md"},l=[h('<h1 id="internet-hoat-đong-nhu-the-nao" tabindex="-1">Internet hoạt động như thế nào? <a class="header-anchor" href="#internet-hoat-đong-nhu-the-nao" aria-label="Permalink to &quot;Internet hoạt động như thế nào?&quot;">​</a></h1><p>Trong thời đại công nghệ phát triển với tốc độ chóng mặt, chúng ta thường tập trung vào việc học tập và làm việc thông qua các top layer. Tuy nhiên, để những layer này có thể hoạt động một cách trơn tru, chúng ta không thể không nhắc đến sự hỗ trợ không thể thiếu của nhiều layer nền tảng ẩn bên dưới chúng.</p><p>Trong số đó, việc hiểu rõ về cách thức hoạt động của Internet là một trong những khái niệm cơ bản và vô cùng quan trọng đối với mỗi lập trình viên</p><nav class="table-of-contents"><ul><li><a href="#lich-su-cua-internet">Lịch sử của Internet</a><ul><li><a href="#_1-truoc-thoi-so-khai-truoc-cong-nguyen-v">1. Trước thời sơ khai (Trước công nguyên :V)</a></li><li><a href="#_2-mang-internet-tap-trung-đau-tien">2. Mạng Internet tập trung đầu tiên.</a></li><li><a href="#_2-thoi-ky-so-khai-1960s-1974s">2. Thời Kỳ Sơ Khai (1960s - 1974s)</a></li><li><a href="#_3-phat-trien-va-mo-rong-1974-1989">3. Phát Triển và Mở Rộng (1974 - 1989)</a></li><li><a href="#_4-su-bung-no-cua-world-wide-web-1990s">4. Sự Bùng Nổ của World Wide Web (1990s)</a></li></ul></li><li><a href="#internet-hoat-đong-nhu-the-nao-1">Internet hoạt động như thế nào?</a><ul><li><a href="#_3-thanh-phan-chinh">3 thành phần chính</a></li><li><a href="#vi-du">ví dụ</a></li></ul></li><li><a href="#loi-va-hai-cua-internet">Lợi và hại của Internet</a><ul><li><a href="#loi-ich-cua-mang-internet">Lợi ích của mạng Internet</a></li><li><a href="#tac-đong-tieu-cuc-cua-internet">Tác động tiêu cực của Internet</a></li></ul></li><li><a href="#tong-ket">Tổng kết</a></li></ul></nav><h2 id="lich-su-cua-internet" tabindex="-1">Lịch sử của Internet <a class="header-anchor" href="#lich-su-cua-internet" aria-label="Permalink to &quot;Lịch sử của  Internet&quot;">​</a></h2><h3 id="_1-truoc-thoi-so-khai-truoc-cong-nguyen-v" tabindex="-1">1. Trước thời sơ khai (Trước công nguyên :V) <a class="header-anchor" href="#_1-truoc-thoi-so-khai-truoc-cong-nguyen-v" aria-label="Permalink to &quot;1. Trước thời sơ khai (Trước công nguyên :V)&quot;">​</a></h3><ul><li>Thực chất thì tại thời điểm này Internet vẫn chưa tồn tại. Tại thời điểm này để gửi dữ liệu giữa các máy tính với nhau chúng ta sẽ cần ghi vào đĩa CD ở máy A sau đó chuyển sang máy B để đọc.</li><li>Một cách khác nữa là sử dụng công nghệ analog. Công nghệ analog sẽ thực hiện sử dụng các tín hiệu, thường là sóng điện từ. <ul><li>Ví dụ: Radio(Sóng vô tuyến), điện thoại cố định(Truyền giọng nói qua dây bằng tín hiệu analog)</li></ul></li></ul><h3 id="_2-mang-internet-tap-trung-đau-tien" tabindex="-1">2. Mạng Internet tập trung đầu tiên. <a class="header-anchor" href="#_2-mang-internet-tap-trung-đau-tien" aria-label="Permalink to &quot;2. Mạng Internet tập trung đầu tiên.&quot;">​</a></h3><ul><li><ol><li>Mạng Internet đầu tiên được sử dụng năm 1950 đến năm 1960. Tên là Mô hình tập trung (Centralized Network Model)</li></ol><ul><li>Trong mô hình này, tất cả các kết nối đều được điều khiển bởi một máy chủ trung tâm central server.</li><li>Máy chủ có trách nghiệm quản lý tất cả kết nối, nhận tất thông tin từ các kết nối sau đó gửi lại đến đích</li><li>Trong mô hình này, nếu máy chủ gặp sự cố, toàn bộ mạng sẽ sập.</li></ul></li><li><ol start="2"><li>Mô hình phân phối (1960 - 1970)</li></ol><ul><li>Mô hình này không phụ thuộc vào một máy chủ trung tâm để kiểm soát họoạt động, thay vào đó các Node hoạt động cùng nhau.</li><li>Các máy tính sẽ gửi thông tin đến Node sau đó node sẽ gửi dữ liệu để đồng bộ hóa đến các Node khác.</li><li>Nếu 1 node gặp sự cố, Automatic Failover sẽ được bật, kết nối sẽ được chuyển đến Node khác.</li></ul></li></ul><h3 id="_2-thoi-ky-so-khai-1960s-1974s" tabindex="-1">2. Thời Kỳ Sơ Khai (1960s - 1974s) <a class="header-anchor" href="#_2-thoi-ky-so-khai-1960s-1974s" aria-label="Permalink to &quot;2. Thời Kỳ Sơ Khai (1960s - 1974s)&quot;">​</a></h3><ul><li><ol start="1960"><li>Dự án <a href="https://vi.wikipedia.org/wiki/ARPANET" target="_blank" rel="noreferrer">ARPANET</a> (<code>Advanced Research Projects Agency Network</code>) của Cơ Quan Dự án Nghiên cứu Tiên Tiến Quốc Phòng Hoa Kỳ khởi xướng, đánh dấu bước đầu tiên của Internet. <img src="https://static-cdn.thanhlv.com/study/thanhlv-study-2024/2024-03-14-Internet-Internet-hoat-dong-nhu-the-nao/Arpanet_logical_map_march_1977.png" alt="Arpanet_logical_map_march_1977"></li></ol><ul><li>ARPANET đã tạo ra 2 giao thức và 1 cấu trúc mạng là nền tảng của Internet ở sau này.</li><li><ol><li>Giao thức chuyển mạch gói (<a href="https://vi.wikipedia.org/wiki/Chuy%E1%BB%83n_m%E1%BA%A1ch_g%C3%B3i" target="_blank" rel="noreferrer">Packet Switching</a>):</li></ol><ul><li>ARPANET sử dụng một kỹ thuật gọi là chuyển mạch gói (<a href="https://vi.wikipedia.org/wiki/Chuy%E1%BB%83n_m%E1%BA%A1ch_g%C3%B3i" target="_blank" rel="noreferrer">packet switching</a>), nơi thông tin được chia thành các đơn vị nhỏ gọi là gói (packets). Mỗi gói chứa thông tin địa chỉ của người nhận, cho phép nó được chuyển từ một nút mạng (node) này sang nút khác thông qua các đường truyền dữ liệu khác nhau(nhiều router khác nhau), không cần phải theo một đường cố định.</li></ul></li><li><ol start="2"><li>Giao thức NCP (<a href="https://en.wikipedia.org/wiki/Network_Control_Protocol_(ARPANET)" target="_blank" rel="noreferrer">Network Control Protocol</a>)</li></ol><ul><li>Giao thức kết nối đầu tiên là NCP sau đó là TCP sau này. NCP đảm bảo rằng dữ liệu được gửi và nhận một cách chính xác và nó quản lý việc chia nhỏ dữ liệu thành Packet và tạo lại dữ liệu khi đến đích.</li></ul></li><li><ol start="3"><li>Cấu trúc mạng phi tập trung</li></ol><ul><li>Kết nối đầu tiên với <a href="https://vi.wikipedia.org/wiki/ARPANET" target="_blank" rel="noreferrer">ARPANET</a> được thực hiện vào ngày 29 tháng 10 năm 1969, giữa IMP tại UCLA và IMP tại SRI</li></ul></li></ul></li><li><ol start="1972"><li>Ray Tomlinson phát minh ra email.</li></ol></li><li><ol start="1973"><li>Giao thức mạng TCP/IP được phát triển bởi Vint Cerf và Bob Kahn <img src="https://static-cdn.thanhlv.com/study/thanhlv-study-2024/2024-03-14-Internet-Internet-hoat-dong-nhu-the-nao/SRI_First_Internetworked_Connection_diagram.jpg" alt="SRI_First_Internetworked_Connection_diagram"></li></ol></li></ul><h3 id="_3-phat-trien-va-mo-rong-1974-1989" tabindex="-1">3. Phát Triển và Mở Rộng (1974 - 1989) <a class="header-anchor" href="#_3-phat-trien-va-mo-rong-1974-1989" aria-label="Permalink to &quot;3. Phát Triển và Mở Rộng (1974 - 1989)&quot;">​</a></h3><ul><li><ol start="1974"><li>Thuật ngữ <code>Internet</code> được sử dụng lần đầu trong một bài báo của Vint Cerf và Bob Kahn</li></ol></li><li><ol start="1976"><li>Phòng thí nghiệm của AT&amp;T phát mình ra truyền tệp mạng FTP</li></ol></li><li><ol start="1982"><li>Giao thức TPC/IP được chuẩn hóa giúp phổ biến mạng kết nối.</li></ol></li><li><ol start="1983"><li>Giao thức TCP/IP là chuẩn trong quân sự Mỹ. Các máy tính kết nối với hệ thống ARPANET của Mỹ đều cần phải dùng chuẩn này.</li></ol></li><li><ol start="1984"><li>Tim Berners-Lee tại CERN đề xuất hệ thống thông tin liên kết siêu văn bản, là cơ sở của World Wide Web.</li></ol></li></ul><h3 id="_4-su-bung-no-cua-world-wide-web-1990s" tabindex="-1">4. Sự Bùng Nổ của World Wide Web (1990s) <a class="header-anchor" href="#_4-su-bung-no-cua-world-wide-web-1990s" aria-label="Permalink to &quot;4. Sự Bùng Nổ của World Wide Web (1990s)&quot;">​</a></h3><ul><li><ol start="1990"><li>Dự án ARPANET chính thức ngừng hoạt động. Tim Berners-Lee bắt đầu viết WorldWideWeb, trình duyệt web đầu tiên sau hai năm vận động hành lang ban quản lý CERN. Ông đã xây dựng tất cả các công cụ cần thiết cho một web hoạt động như giao thức truyền tải siêu văn bản (HTTP) 0.9, ngôn ngữ đánh dấu siêu văn bản (HTML), trình duyệt web đầu tiên (cũng là trình soạn thảo HTML và có thể truy cập các nhóm tin Usenet và các tệp FTP), phần mềm máy chủ HTTP đầu tiên (sau này được gọi là CERN httpd), máy chủ web đầu tiên và các trang web đầu tiên mô tả chính dự án.</li></ol></li><li><ol start="1991"><li>World Wide Web (WWW) được công bố.</li></ol></li></ul><h2 id="internet-hoat-đong-nhu-the-nao-1" tabindex="-1">Internet hoạt động như thế nào? <a class="header-anchor" href="#internet-hoat-đong-nhu-the-nao-1" aria-label="Permalink to &quot;Internet hoạt động như thế nào?&quot;">​</a></h2><h3 id="_3-thanh-phan-chinh" tabindex="-1">3 thành phần chính <a class="header-anchor" href="#_3-thanh-phan-chinh" aria-label="Permalink to &quot;3 thành phần chính&quot;">​</a></h3><p>Dù thiết kế có sự thay đổi ở từng thời điểm khác nhau nhưng nhìn chung mạng internet gồm 3 thành phần chính.</p><ul><li>Các máy tính</li><li>Các thiết bị mạng đóng vai trò kết nối các máy tính với nhau</li><li>Phần mềm hỗ trợ kết nối.</li></ul><h3 id="vi-du" tabindex="-1">ví dụ <a class="header-anchor" href="#vi-du" aria-label="Permalink to &quot;ví dụ&quot;">​</a></h3><ul><li>Khi bạn truy cập vào một web hoặc gửi dữ liệu qua terminal, FPT...etc.. thiết bị của bạn sẽ gửi yêu cầu qua các dây cáp(Cáp quang, dây đồng điện thoại) tới máy chủ nhà phân phối mạng (FPT, VNPT...etc..)</li><li>Yêu cầu của bạn có thể sẽ cần đi qua một số router khác nữa trước khi đi tới máy chủ(Nếu ở trong nước) hoặc cáp quang ngoài biển hoặc dất liền để ra thế giới.</li><li>Yêu cầu của bạn sau khi đi qua nhiều router sẽ đến máy chủ nhà phân phối của máy chủ web. Nhà điều phối sẽ thực hiện gửi dữ liệu đến máy chủ web.</li><li>Máy chủ web thực hiện xử lý dữ liệu sau đó thực hiện gửi ngược lại dữ liệu về client.</li></ul><p>Lưu ý: Các loại kết nối không dây như wifi, 3G/4G/5G tuy không kết nối qua dây nhưng cũng dựa trên những loại cáp vật lý để truy cập Internet.</p><h2 id="loi-va-hai-cua-internet" tabindex="-1">Lợi và hại của Internet <a class="header-anchor" href="#loi-va-hai-cua-internet" aria-label="Permalink to &quot;Lợi và hại của Internet&quot;">​</a></h2><h3 id="loi-ich-cua-mang-internet" tabindex="-1">Lợi ích của mạng Internet <a class="header-anchor" href="#loi-ich-cua-mang-internet" aria-label="Permalink to &quot;Lợi ích của mạng Internet&quot;">​</a></h3><ul><li>Nơi lưu trữ kho tàng kiến thức khổng lồ, dễ dàng liên lạc vượt qua khoảng cách địa lý.</li><li>Kinh doanh, buôn bán</li><li>Một thế giới ảo,(Nghe nhạc, xem phim)</li><li>Giúp kết nối mọi người</li><li>...</li></ul><h3 id="tac-đong-tieu-cuc-cua-internet" tabindex="-1">Tác động tiêu cực của Internet <a class="header-anchor" href="#tac-đong-tieu-cuc-cua-internet" aria-label="Permalink to &quot;Tác động tiêu cực của Internet&quot;">​</a></h3><ul><li>Khiến con người lười vận động.</li><li>Khó kiểm soát được nội dung độc hại.</li><li>Lừa đảo của internet</li><li>...</li></ul><h2 id="tong-ket" tabindex="-1">Tổng kết <a class="header-anchor" href="#tong-ket" aria-label="Permalink to &quot;Tổng kết&quot;">​</a></h2><ul><li>Internet là một hệ thống phức tạp, bao gồm nhiều lớp và thành phần từ phần cứng đến phần mềm.</li><li>Trước Internet, trao đổi dữ liệu giữa máy tính dựa vào đĩa CD hoặc công nghệ analog như radio và điện thoại.</li><li>Lịch sử Internet bắt đầu từ dự án ARPANET của thập niên 1960, đặt nền móng cho mạng lưới toàn cầu.</li><li>Các giao thức như chuyển mạch gói và NCP, sau này là TCP/IP, đã được phát triển, làm cơ sở cho Internet hiện đại.</li><li>Sự phát triển qua các giai đoạn dẫn đến sự ra đời của World Wide Web vào đầu những năm 1990.</li><li>Khi truy cập web hoặc gửi dữ liệu, yêu cầu từ thiết bị của người dùng đi qua nhiều router đến máy chủ.</li><li>Internet mang lại lợi ích như kho tàng kiến thức, kết nối mọi người và hỗ trợ kinh doanh.</li><li>Tuy nhiên, Internet cũng có tác động tiêu cực như sự lười vận động và khó kiểm soát nội dung độc hại.</li><li>Lừa đảo trực tuyến là một trong những vấn đề nghiêm trọng liên quan đến Internet.</li></ul>',29)];const c=n(e,[["render",function(n,h,a,e,c,r){return i(),t("div",null,l)}]]);export{a as __pageData,c as default};
