import{_ as n,j as i,o as h,v as t}from"./chunks/framework.9xwLhOZU.js";const e=JSON.parse('{"title":"World wide web là gì ?","description":"","frontmatter":{"footer":true,"title":"World wide web là gì ?","authors":["lethanh"],"date":"2024-03-18T00:00:00.000Z","outline":"deep","image":"/assets/img.ldf-rwrM.png","draft":false,"group":"1. Internet"},"headers":[{"level":2,"title":"Vậy World wide web là gì ?","slug":"vay-world-wide-web-la-gi","link":"#vay-world-wide-web-la-gi","children":[]},{"level":2,"title":"Các thành phần của WWW (World Wide Web)","slug":"cac-thanh-phan-cua-www-world-wide-web","link":"#cac-thanh-phan-cua-www-world-wide-web","children":[]},{"level":2,"title":"Các phiên bản của WWW.","slug":"cac-phien-ban-cua-www","link":"#cac-phien-ban-cua-www","children":[{"level":3,"title":"Web 1.0 (The static web)","slug":"web-1-0-the-static-web","link":"#web-1-0-the-static-web","children":[]},{"level":3,"title":"Web 2.0 (The Social Web)","slug":"web-2-0-the-social-web","link":"#web-2-0-the-social-web","children":[]},{"level":3,"title":"Web 3.0 ( The Semantic Web )","slug":"web-3-0-the-semantic-web","link":"#web-3-0-the-semantic-web","children":[]},{"level":3,"title":"Web 4.0 (The Symbiotic Web)","slug":"web-4-0-the-symbiotic-web","link":"#web-4-0-the-symbiotic-web","children":[]}]},{"level":2,"title":"Một luồng hoạt động cơ bản của WWW (World Wide Web)","slug":"mot-luong-hoat-đong-co-ban-cua-www-world-wide-web","link":"#mot-luong-hoat-đong-co-ban-cua-www-world-wide-web","children":[{"level":3,"title":"1.Người dùng tạo yêu cầu","slug":"_1-nguoi-dung-tao-yeu-cau","link":"#_1-nguoi-dung-tao-yeu-cau","children":[]},{"level":3,"title":"2. Phân giải tên miền","slug":"_2-phan-giai-ten-mien","link":"#_2-phan-giai-ten-mien","children":[]},{"level":3,"title":"3. Thiết lập Kết nối","slug":"_3-thiet-lap-ket-noi","link":"#_3-thiet-lap-ket-noi","children":[]},{"level":3,"title":"4. Gửi Yêu Cầu HTTP.","slug":"_4-gui-yeu-cau-http","link":"#_4-gui-yeu-cau-http","children":[]},{"level":3,"title":"5. Xử lý Yêu Cầu","slug":"_5-xu-ly-yeu-cau","link":"#_5-xu-ly-yeu-cau","children":[]},{"level":3,"title":"6. Gửi Phản hồi HTTP","slug":"_6-gui-phan-hoi-http","link":"#_6-gui-phan-hoi-http","children":[]},{"level":3,"title":"7. Hiển thị Trang Web","slug":"_7-hien-thi-trang-web","link":"#_7-hien-thi-trang-web","children":[]},{"level":3,"title":"8. Tải Tài nguyên Bổ sung","slug":"_8-tai-tai-nguyen-bo-sung","link":"#_8-tai-tai-nguyen-bo-sung","children":[]},{"level":3,"title":"9. Tương tác Người Dùng","slug":"_9-tuong-tac-nguoi-dung","link":"#_9-tuong-tac-nguoi-dung","children":[]},{"level":3,"title":"10. Đóng Kết nối","slug":"_10-đong-ket-noi","link":"#_10-đong-ket-noi","children":[]}]},{"level":2,"title":"Ưu và nhược điểm của WWW.","slug":"uu-va-nhuoc-điem-cua-www","link":"#uu-va-nhuoc-điem-cua-www","children":[{"level":3,"title":"Ưu điểm","slug":"uu-điem","link":"#uu-điem","children":[{"level":4,"title":"1. Truy cập thông tin","slug":"_1-truy-cap-thong-tin","link":"#_1-truy-cap-thong-tin","children":[]},{"level":4,"title":"2. Tính liên kết","slug":"_2-tinh-lien-ket","link":"#_2-tinh-lien-ket","children":[]},{"level":4,"title":"3. Tương tác","slug":"_3-tuong-tac","link":"#_3-tuong-tac","children":[]},{"level":4,"title":"4. Thương mại điện tử","slug":"_4-thuong-mai-đien-tu","link":"#_4-thuong-mai-đien-tu","children":[]},{"level":4,"title":"5. Giáo dục và học tập:","slug":"_5-giao-duc-va-hoc-tap","link":"#_5-giao-duc-va-hoc-tap","children":[]},{"level":4,"title":"6. Giao tiếp và cộng đồng","slug":"_6-giao-tiep-va-cong-đong","link":"#_6-giao-tiep-va-cong-đong","children":[]},{"level":4,"title":"7. Phân phối nội dụng.","slug":"_7-phan-phoi-noi-dung","link":"#_7-phan-phoi-noi-dung","children":[]}]},{"level":3,"title":"Nhược điểm","slug":"nhuoc-điem","link":"#nhuoc-điem","children":[{"level":4,"title":"1. Quá tải thông tin","slug":"_1-qua-tai-thong-tin","link":"#_1-qua-tai-thong-tin","children":[]},{"level":4,"title":"2. An ninh mạng","slug":"_2-an-ninh-mang","link":"#_2-an-ninh-mang","children":[]},{"level":4,"title":"3. Bảo mật dữ liệu.","slug":"_3-bao-mat-du-lieu","link":"#_3-bao-mat-du-lieu","children":[]},{"level":4,"title":"4. Phụ thuộc vào công nghệ","slug":"_4-phu-thuoc-vao-cong-nghe","link":"#_4-phu-thuoc-vao-cong-nghe","children":[]},{"level":4,"title":"5. Chênh lệnh kỹ thuật số","slug":"_5-chenh-lenh-ky-thuat-so","link":"#_5-chenh-lenh-ky-thuat-so","children":[]},{"level":4,"title":"6. Nội dung không lành mạnh","slug":"_6-noi-dung-khong-lanh-manh","link":"#_6-noi-dung-khong-lanh-manh","children":[]},{"level":4,"title":"7. Ảnh hưởng đến năng suất","slug":"_7-anh-huong-đen-nang-suat","link":"#_7-anh-huong-đen-nang-suat","children":[]}]}]},{"level":2,"title":"Tổng kết","slug":"tong-ket","link":"#tong-ket","children":[]}],"relativePath":"study/thanhlv-study-2024/2024-03-18-world-wide-web-la-gi.md","filePath":"study/thanhlv-study-2024/2024-03-18-world-wide-web-la-gi.md","lastUpdated":1743006169000}'),a={name:"study/thanhlv-study-2024/2024-03-18-world-wide-web-la-gi.md"},l=[t('<h1 id="world-wide-web-la-gi" tabindex="-1">World wide web là gì ? <a class="header-anchor" href="#world-wide-web-la-gi" aria-label="Permalink to &quot;World wide web là gì ?&quot;">​</a></h1><p>Trong thế giới kỹ thuật số ngày nay, World Wide Web đã trở thành một phần không thể tách rời của Internet, là nơi diễn ra hàng loạt hoạt động từ học tập, làm việc đến giải trí.</p><p>Mặc dù vậy, không phải lập trình viên nào cũng thấu hiểu hoặc nắm rõ về World Wide Web và những tầng nền vững chắc mà nó dựa trên.</p><p>Hãy cùng nhau khám phá và mở rộng kiến thức về World Wide Web, một thế giới siêu văn bản rộng lớn nằm ngay dưới đầu ngón tay chúng ta.</p><nav class="table-of-contents"><ul><li><a href="#vay-world-wide-web-la-gi">Vậy World wide web là gì ?</a></li><li><a href="#cac-thanh-phan-cua-www-world-wide-web">Các thành phần của WWW (World Wide Web)</a></li><li><a href="#cac-phien-ban-cua-www">Các phiên bản của WWW.</a><ul><li><a href="#web-1-0-the-static-web">Web 1.0 (The static web)</a></li><li><a href="#web-2-0-the-social-web">Web 2.0 (The Social Web)</a></li><li><a href="#web-3-0-the-semantic-web">Web 3.0 ( The Semantic Web )</a></li><li><a href="#web-4-0-the-symbiotic-web">Web 4.0 (The Symbiotic Web)</a></li></ul></li><li><a href="#mot-luong-hoat-đong-co-ban-cua-www-world-wide-web">Một luồng hoạt động cơ bản của WWW (World Wide Web)</a><ul><li><a href="#_1-nguoi-dung-tao-yeu-cau">1.Người dùng tạo yêu cầu</a></li><li><a href="#_2-phan-giai-ten-mien">2. Phân giải tên miền</a></li><li><a href="#_3-thiet-lap-ket-noi">3. Thiết lập Kết nối</a></li><li><a href="#_4-gui-yeu-cau-http">4. Gửi Yêu Cầu HTTP.</a></li><li><a href="#_5-xu-ly-yeu-cau">5. Xử lý Yêu Cầu</a></li><li><a href="#_6-gui-phan-hoi-http">6. Gửi Phản hồi HTTP</a></li><li><a href="#_7-hien-thi-trang-web">7. Hiển thị Trang Web</a></li><li><a href="#_8-tai-tai-nguyen-bo-sung">8. Tải Tài nguyên Bổ sung</a></li><li><a href="#_9-tuong-tac-nguoi-dung">9. Tương tác Người Dùng</a></li><li><a href="#_10-đong-ket-noi">10. Đóng Kết nối</a></li></ul></li><li><a href="#uu-va-nhuoc-điem-cua-www">Ưu và nhược điểm của WWW.</a><ul><li><a href="#uu-điem">Ưu điểm</a><ul><li><a href="#_1-truy-cap-thong-tin">1. Truy cập thông tin</a></li><li><a href="#_2-tinh-lien-ket">2. Tính liên kết</a></li><li><a href="#_3-tuong-tac">3. Tương tác</a></li><li><a href="#_4-thuong-mai-đien-tu">4. Thương mại điện tử</a></li><li><a href="#_5-giao-duc-va-hoc-tap">5. Giáo dục và học tập:</a></li><li><a href="#_6-giao-tiep-va-cong-đong">6. Giao tiếp và cộng đồng</a></li><li><a href="#_7-phan-phoi-noi-dung">7. Phân phối nội dụng.</a></li></ul></li><li><a href="#nhuoc-điem">Nhược điểm</a><ul><li><a href="#_1-qua-tai-thong-tin">1. Quá tải thông tin</a></li><li><a href="#_2-an-ninh-mang">2. An ninh mạng</a></li><li><a href="#_3-bao-mat-du-lieu">3. Bảo mật dữ liệu.</a></li><li><a href="#_4-phu-thuoc-vao-cong-nghe">4. Phụ thuộc vào công nghệ</a></li><li><a href="#_5-chenh-lenh-ky-thuat-so">5. Chênh lệnh kỹ thuật số</a></li><li><a href="#_6-noi-dung-khong-lanh-manh">6. Nội dung không lành mạnh</a></li><li><a href="#_7-anh-huong-đen-nang-suat">7. Ảnh hưởng đến năng suất</a></li></ul></li></ul></li><li><a href="#tong-ket">Tổng kết</a></li></ul></nav><h2 id="vay-world-wide-web-la-gi" tabindex="-1">Vậy World wide web là gì ? <a class="header-anchor" href="#vay-world-wide-web-la-gi" aria-label="Permalink to &quot;Vậy World wide web là gì ?&quot;">​</a></h2><ul><li><code>WWW</code> hay gọi chuẩn là <code>World Wide Web</code> là một hế thống thông tin toàn cầu dựa trên <a href="./2024-03-14-Internet-Internet-hoat-dong-nhu-the-nao.html">Internet</a>, cho phép người dùng truy cập vào các trang web và được liên kết với nhau thông qua siêu liên kết (hyperlinks).</li><li><code>WWW</code> là dịch vụ phổ biến nhất trên Internet, <code>WWW</code> bị nhiều người nhầm lẫn với <a href="./2024-03-14-Internet-Internet-hoat-dong-nhu-the-nao.html">Internet</a> nhưng thực chất đây là một trong những dịch vụ phổ biến của <a href="./2024-03-14-Internet-Internet-hoat-dong-nhu-the-nao.html">Internet</a> được cung cấp dưới dạng Web cho phép truy cập và chia sẻ thông tin thông qua web page dựa trên HTML và được liên kết với nhau thông qua Hyperlinks.</li><li>Như <a href="./2024-03-14-Internet-Internet-hoat-dong-nhu-the-nao.html#_4-su-bung-no-cua-world-wide-web-1990s">lịch sử đã được viết </a> WWW được phát minh bởi <a href="https://vi.wikipedia.org/wiki/Tim_Berners-Lee" target="_blank" rel="noreferrer">Tim Berners-Lee</a> vào năm 1989 và công bố vào năm 1990.</li><li><a href="https://vi.wikipedia.org/wiki/Tim_Berners-Lee" target="_blank" rel="noreferrer">Tim Berners-Lee</a> và các đồng nghiệp đã phát triển các công nghệ cơ bản như HTML, HTTP và URL để tạo lên nền tảng của WWW.</li><li>Ngày nay, <code>World Wide Web</code> là một phần không thể thiếu trong đời sống xã hội hiện đại, được sử dụng rộng rãi cho giáo dục, thông tin, giải trí, kinh doanh và nhiều lĩnh vực khác. <img src="/assets/img.ldf-rwrM.png" alt=""></li></ul><h2 id="cac-thanh-phan-cua-www-world-wide-web" tabindex="-1">Các thành phần của WWW (World Wide Web) <a class="header-anchor" href="#cac-thanh-phan-cua-www-world-wide-web" aria-label="Permalink to &quot;Các thành phần của WWW (World Wide Web)&quot;">​</a></h2><p>WWW (World Wide Web) sẽ có 4 thành phần chính để có thể hoạt động.</p><ol><li><strong>HyperText Markup Language (HTML)</strong>: HTML là ngôn ngữ đánh dấu sử dụng các thẻ để tạo ra cấu trúc và nội dung cho các web page. Nó cho phép văn bảm, hình ảnh, video và nhiều dữ liệu khác được nhúng và hiển thị trên trình duyệt.</li><li><strong>HyperText Transfer Protocol (HTTP)</strong>: HTTP là giao thức truyền tải siêu văn bản, HTTP quy ước các thức dữ liệu được truyền tải trên web. Khi bạn nhập vào một URL trên trình duyệt, HTTP sẽ đưược kích hoatj</li><li><strong>Uniform Resource Locator (URL)</strong>: URL là địa chỉ của một tài nguyên trên Web, URL giúp xác định ví trị của tài nguyên trên Internet. Đến thời điểm 18-3-2014 thì URL hiện tại có 2 giao thức là Http và Https <ul><li>Cấu trúc của 1 URL gồm: Giao thức(http/https) + tên miền hoặc IP + đường dẫn tài nguyên(path)</li></ul></li><li><strong>Trình duyệt web (Web browser)</strong>: Trình duyệt là một phần mềm cho phép người dùng xem và tương tác với nội dung trên World Wide Web. Các trình duyệt phổ biến hiện tại gồm Chrome, Firefox, Safari và Edge.</li></ol><h2 id="cac-phien-ban-cua-www" tabindex="-1">Các phiên bản của WWW. <a class="header-anchor" href="#cac-phien-ban-cua-www" aria-label="Permalink to &quot;Các phiên bản của WWW.&quot;">​</a></h2><p>Những phiên bản WWW không phải là các bản cập nhật chính thức hay phát hành giống như phần mềm, thay vào đó các phiên bản của WWW là các thuật ngữ được cộng đồng sử dụng nhiều để mô tả sự tiến hóa của WWW qua các thời kỳ khác nhau bởi công nghệ và các chúng ta sử dụng và tích hợp đến WWW.</p><h3 id="web-1-0-the-static-web" tabindex="-1">Web 1.0 (The static web) <a class="header-anchor" href="#web-1-0-the-static-web" aria-label="Permalink to &quot;Web 1.0 (The static web)&quot;">​</a></h3><ul><li><strong>Thời gian</strong>: Khoảng 1991 - 2000</li><li><strong>Đặc điểm</strong>: <ul><li>Web 1.0 là phiên bản đầu tiên của World Wide Web, phiên bản này thường được gọi là <code>Web tĩnh</code>.</li><li>Các trang web sẽ chỉ chứa các thông tin tĩnh và chỉ có thể đọc, không thể tương tác hoặc cập nhật dữ liệu bởi người dùng.</li><li>Thời điểm đầu tiên các trang web chỉ có HTML. Đến năm 1995 Javascript ra đời và 1996 Css 1.0 ra đời nhưng nó vẫn rất hạn chế và ít được sử dụng. <ul><li>Tại giai đoạn này Css và Javascript vẫn chưa có chuẩn chung, các trình duyệt vẫn chưa tích hợp đồng nhất nên có nhiều sự khác biệt khi hiển thị trên các trình duyệt khác nhau.</li></ul></li><li>Các trang web chỉ có thể cập nhật dữ liệu bởi các nhà phát triển hoặc chủ sở hữu Web, người dùng không thể cập nhật.</li></ul></li></ul><h3 id="web-2-0-the-social-web" tabindex="-1">Web 2.0 (The Social Web) <a class="header-anchor" href="#web-2-0-the-social-web" aria-label="Permalink to &quot;Web 2.0 (The Social Web)&quot;">​</a></h3><ul><li><strong>Thời gian</strong>: Khoảng 2000 - 2010</li><li><strong>Đặc điểm</strong>: <ul><li>Web <code>2.0</code> đánh dấu sự thay đổi các trang web từ <code>web tĩnh</code> sang <code>web động</code> và có tương tác bởi người dùng.</li><li>Sự phát triển mạnh mẽ của Ajax, các JavaScript framework và các phiên bản mới của tiêu chuẩn HTML, CSS, JS tạo ra trải nghiệm của người dùng phong phú hơn.</li><li>Bùng nổ nhiều mạng xã hội và các dịch vụ online như Yahoo, Facebook, Youtube,...</li><li>Ngoài đọc nội dung ở <code>web 1.0</code>, tại <code>web 2.0</code> người dùng có thể tạo nội dung.</li><li>Bắt đầu phát triển mạnh các dịch vụ đám mây.</li></ul></li></ul><h3 id="web-3-0-the-semantic-web" tabindex="-1">Web 3.0 ( The Semantic Web ) <a class="header-anchor" href="#web-3-0-the-semantic-web" aria-label="Permalink to &quot;Web 3.0 ( The Semantic Web )&quot;">​</a></h3><p>Một điều mình nghĩ nên giải thích là <code>Web 3.0</code> khác với <code>web3</code>. <code>Web3</code> là sử dụng công nghệ blockchain còn <code>web 3.0</code> thì bảo gồm <code>Web3</code>.</p><ul><li><strong>Thời gian</strong>: Khoảng 2010 - hiện tại</li><li><strong>Đặc điểm</strong>: <ul><li>Các dịch vụ, ứng dụng được triển khai trên nền tảng web(Word, Excel, game...etc..)</li><li>Các nhân hóa mạnh mẽ hơn trong các kết quả tìm kiếm và đề suất nội dung cho người dùng trên web.</li><li>Tích hợp nhiều công nghệ như AI, machine learning, và blockchain...etc.. để cải thiện kết quả tìm kiếm, quản lý dữ liệu người dùng, bảo mật.</li></ul></li></ul><h3 id="web-4-0-the-symbiotic-web" tabindex="-1">Web 4.0 (The Symbiotic Web) <a class="header-anchor" href="#web-4-0-the-symbiotic-web" aria-label="Permalink to &quot;Web 4.0 (The Symbiotic Web)&quot;">​</a></h3><ul><li><strong>Thời gian</strong>: Ý tưởng tưương lai.</li><li><strong>Đặc điểm</strong>: <ul><li>Dự đoán <code>web 4.0</code> sẽ tích hợp nhiều công nghệ, <code>web 4.0</code> sẽ kết hợp sâu giữa con người và công nghệ.</li><li>Công nghệ có thể tiên tiến đến mức dự đoán nhu cầu của người dùng trước khi người dùng nghĩ và nhận thức được nhu cầu đó.</li><li>Sự kết hợp giữa thực tế ảo/augmented reality và IoT (Internet of Things) có thể đưa đến một trải nghiệm web hoàn toàn mới.</li></ul></li></ul><h2 id="mot-luong-hoat-đong-co-ban-cua-www-world-wide-web" tabindex="-1">Một luồng hoạt động cơ bản của WWW (World Wide Web) <a class="header-anchor" href="#mot-luong-hoat-đong-co-ban-cua-www-world-wide-web" aria-label="Permalink to &quot;Một luồng hoạt động cơ bản của WWW (World Wide Web)&quot;">​</a></h2><p><img src="data:image/svg+xml;base64," alt=""> Đây là một quy trình cơ bản và tương đối đơn giản của việc tải và hiển thị một trang web trong WWW. Trong thực tế, quy trình này có thể phức tạp hơn nhiều, bao gồm các bước như caching, xử lý cookie, xác thực người dùng, và nhiều quy trình khác.</p><h3 id="_1-nguoi-dung-tao-yeu-cau" tabindex="-1">1.Người dùng tạo yêu cầu <a class="header-anchor" href="#_1-nguoi-dung-tao-yeu-cau" aria-label="Permalink to &quot;1.Người dùng tạo yêu cầu&quot;">​</a></h3><p>Người dùng mở trình duyệt web (Web browser) và nhập địa chỉ một trang web(URL- Uniform Resource Locator) và thanh địa chỉ hoặc nhấp từ một liên kết(link) nào đó.</p><h3 id="_2-phan-giai-ten-mien" tabindex="-1">2. Phân giải tên miền <a class="header-anchor" href="#_2-phan-giai-ten-mien" aria-label="Permalink to &quot;2. Phân giải tên miền&quot;">​</a></h3><p>Trình duyệt sẽ gửi yêu cầu đến hệ thống phân giải tên miền (DNS- Domain Name System) để hỏi và nhận về domain url người dùng nhập tương ứng với IP máy chủ web bao nhiêu.</p><h3 id="_3-thiet-lap-ket-noi" tabindex="-1">3. Thiết lập Kết nối <a class="header-anchor" href="#_3-thiet-lap-ket-noi" aria-label="Permalink to &quot;3. Thiết lập Kết nối&quot;">​</a></h3><p>Trình duyệt sử dụng địa chỉ IP để thiếp lập một kết nối ICP/IP với máy chủ web thông qua giao thức HTTP (HyperText Transfer Protocol) hoặc HTTPS (HTTP Secure- HyperText Transfer Protocol Secure).</p><h3 id="_4-gui-yeu-cau-http" tabindex="-1">4. Gửi Yêu Cầu HTTP. <a class="header-anchor" href="#_4-gui-yeu-cau-http" aria-label="Permalink to &quot;4. Gửi Yêu Cầu HTTP.&quot;">​</a></h3><p>Trình duyệt gửi một yêu cầu HTTP đến máy chủ web bao gồm body, header...etc.., yêu cầu truy cập trang web hoặc tài nguyên cụ thể.</p><h3 id="_5-xu-ly-yeu-cau" tabindex="-1">5. Xử lý Yêu Cầu <a class="header-anchor" href="#_5-xu-ly-yeu-cau" aria-label="Permalink to &quot;5. Xử lý Yêu Cầu&quot;">​</a></h3><p>Máy chủ web nhận yêu cầu và sử lý nó. Máy chủ sẽ tạo ra nội dung từ một file html tĩnh hoặc thực thi các code để tạo ra một HTMl riêng cho người dùng.</p><h3 id="_6-gui-phan-hoi-http" tabindex="-1">6. Gửi Phản hồi HTTP <a class="header-anchor" href="#_6-gui-phan-hoi-http" aria-label="Permalink to &quot;6. Gửi Phản hồi HTTP&quot;">​</a></h3><p>Máy chủ phản hổi lại HTTP cho người dùng bao gồm mã trạng thái, Header và gửi nội dung trang web(thường là HTML) trở lại cho trình duyệt của người dùng.</p><h3 id="_7-hien-thi-trang-web" tabindex="-1">7. Hiển thị Trang Web <a class="header-anchor" href="#_7-hien-thi-trang-web" aria-label="Permalink to &quot;7. Hiển thị Trang Web&quot;">​</a></h3><p>Trình duyệt nhận phản hồi và mã HTML, sau đó dùng trình biên dịch HTML để xây dựng và hiển thị trang web trên màn hình người dùng.</p><h3 id="_8-tai-tai-nguyen-bo-sung" tabindex="-1">8. Tải Tài nguyên Bổ sung <a class="header-anchor" href="#_8-tai-tai-nguyen-bo-sung" aria-label="Permalink to &quot;8. Tải Tài nguyên Bổ sung&quot;">​</a></h3><p>Trang web có thể chứa tham chiếu đến các tài nguyên khác như hình ảnh, CSS (Cascading Style Sheets), và JavaScript..etc... Trình duyệt sẽ thực hiện thêm các yêu cầu HTTP để tải những tài nguyên này và xử lý chúng để hoàn thiện hiển thị trang web.</p><h3 id="_9-tuong-tac-nguoi-dung" tabindex="-1">9. Tương tác Người Dùng <a class="header-anchor" href="#_9-tuong-tac-nguoi-dung" aria-label="Permalink to &quot;9. Tương tác Người Dùng&quot;">​</a></h3><p>Người dùng có thể tương tác với trang web, ví dụ như điền vào một biểu mẫu và nhấn nút gửi. Các hành động này có thể tạo ra các yêu cầu HTTP mới, và quy trình trên sẽ được lặp lại.</p><h3 id="_10-đong-ket-noi" tabindex="-1">10. Đóng Kết nối <a class="header-anchor" href="#_10-đong-ket-noi" aria-label="Permalink to &quot;10. Đóng Kết nối&quot;">​</a></h3><p>Sau khi trang web được tải và hiển thị hoàn chỉnh, kết nối TCP/IP thường được đóng lại, trừ khi sử dụng kỹ thuật như HTTP Persistent Connections (kết nối liên tục), HTTP2..etc..</p><h2 id="uu-va-nhuoc-điem-cua-www" tabindex="-1">Ưu và nhược điểm của WWW. <a class="header-anchor" href="#uu-va-nhuoc-điem-cua-www" aria-label="Permalink to &quot;Ưu và nhược điểm của WWW.&quot;">​</a></h2><p>World Wide Web (WWW), kể từ khi được tạo ra, đã trở thành một phần không thể thiếu trong cuộc sống hàng ngày và hoạt động kinh doanh. Dưới đây là một số ưu và nhược điểm của WWW:</p><h3 id="uu-điem" tabindex="-1">Ưu điểm <a class="header-anchor" href="#uu-điem" aria-label="Permalink to &quot;Ưu điểm&quot;">​</a></h3><h4 id="_1-truy-cap-thong-tin" tabindex="-1">1. Truy cập thông tin <a class="header-anchor" href="#_1-truy-cap-thong-tin" aria-label="Permalink to &quot;1. Truy cập thông tin&quot;">​</a></h4><p>WWW cung cấp khả năng truy cập vào một lượng lớn thông tin từ khắp nơi trên thế giới.</p><h4 id="_2-tinh-lien-ket" tabindex="-1">2. Tính liên kết <a class="header-anchor" href="#_2-tinh-lien-ket" aria-label="Permalink to &quot;2. Tính liên kết&quot;">​</a></h4><p>Liên kết siêu văn bản cho phép người dùng dễ dàng di chuyển từ trang này sang trang khác, tạo ra một trải nghiệm liền mạch.</p><h4 id="_3-tuong-tac" tabindex="-1">3. Tương tác <a class="header-anchor" href="#_3-tuong-tac" aria-label="Permalink to &quot;3. Tương tác&quot;">​</a></h4><p>Web 2.0 và các công nghệ tiếp theo đã cải thiện khả năng tương tác cho phép người dùng không chỉ tiêu thụ mà còn tạo và chia sẻ nội dụng.</p><h4 id="_4-thuong-mai-đien-tu" tabindex="-1">4. Thương mại điện tử <a class="header-anchor" href="#_4-thuong-mai-đien-tu" aria-label="Permalink to &quot;4. Thương mại điện tử&quot;">​</a></h4><p>WWW là nền tảng cho thương mại điện tử, cho phép mua sắm trực tuyến và giao dịch tài chính.</p><h4 id="_5-giao-duc-va-hoc-tap" tabindex="-1">5. Giáo dục và học tập: <a class="header-anchor" href="#_5-giao-duc-va-hoc-tap" aria-label="Permalink to &quot;5. Giáo dục và học tập:&quot;">​</a></h4><p>Cung cấp nguồn tài nguyên giáo dục phong phú, hỗ trợ việc học tập trực tuyến và tự học.</p><h4 id="_6-giao-tiep-va-cong-đong" tabindex="-1">6. Giao tiếp và cộng đồng <a class="header-anchor" href="#_6-giao-tiep-va-cong-đong" aria-label="Permalink to &quot;6. Giao tiếp và cộng đồng&quot;">​</a></h4><p>Mạng xã hội và các diễn đàn trực tuyến xây dựng cộng đồng và thúc đẩy giao tiếp giữa người dùng từ khắp nơi trên thế giới.</p><h4 id="_7-phan-phoi-noi-dung" tabindex="-1">7. Phân phối nội dụng. <a class="header-anchor" href="#_7-phan-phoi-noi-dung" aria-label="Permalink to &quot;7. Phân phối nội dụng.&quot;">​</a></h4><p>Dễ dàng phân phối các nội dung số như âm nhạc, video, sách, ảnh, phần mềm.</p><h3 id="nhuoc-điem" tabindex="-1">Nhược điểm <a class="header-anchor" href="#nhuoc-điem" aria-label="Permalink to &quot;Nhược điểm&quot;">​</a></h3><h4 id="_1-qua-tai-thong-tin" tabindex="-1">1. Quá tải thông tin <a class="header-anchor" href="#_1-qua-tai-thong-tin" aria-label="Permalink to &quot;1. Quá tải thông tin&quot;">​</a></h4><p>Sự phóng phú về thông tin cho thể gây quá tải và khó khăn trong việc tìm kiếm thông tin chính xác và tin cậy.</p><h4 id="_2-an-ninh-mang" tabindex="-1">2. An ninh mạng <a class="header-anchor" href="#_2-an-ninh-mang" aria-label="Permalink to &quot;2. An ninh mạng&quot;">​</a></h4><p>Các vấn đề về an ninh mạng như virus, trojan, phishing thường được phân phối thông qua WWW.(Người dùng tải ở WWW về, quảng cáo..etc..) và các cuộc tấn công khác tạo ra các mối lo ngại về an toàn thông tin và tài chính.</p><h4 id="_3-bao-mat-du-lieu" tabindex="-1">3. Bảo mật dữ liệu. <a class="header-anchor" href="#_3-bao-mat-du-lieu" aria-label="Permalink to &quot;3. Bảo mật dữ liệu.&quot;">​</a></h4><p>WWW lưu trữ một số lượng khổng lồ dữ liệu người dùng trên các máy chủ. Vấn đề bảo vệ dữ liệu và quyền riêng tư là một thách thức lớn.</p><h4 id="_4-phu-thuoc-vao-cong-nghe" tabindex="-1">4. Phụ thuộc vào công nghệ <a class="header-anchor" href="#_4-phu-thuoc-vao-cong-nghe" aria-label="Permalink to &quot;4. Phụ thuộc vào công nghệ&quot;">​</a></h4><p>Sự phụ thuộc vào nhiều vào WWW đã làm giảm tương tác xã hội ngoại tuyến và các vấn đề về sức khỏe như mất ngủ, mỏi mắt, thức đêm.</p><h4 id="_5-chenh-lenh-ky-thuat-so" tabindex="-1">5. Chênh lệnh kỹ thuật số <a class="header-anchor" href="#_5-chenh-lenh-ky-thuat-so" aria-label="Permalink to &quot;5. Chênh lệnh kỹ thuật số&quot;">​</a></h4><p>Không phải mọi người đều có quyền truy cập Internet hoặc có đủ kỹ năng sử dụng công nghệ, tạo ra chênh lệch kỹ thuật số giữa các nhóm dân cư khác nhau.</p><h4 id="_6-noi-dung-khong-lanh-manh" tabindex="-1">6. Nội dung không lành mạnh <a class="header-anchor" href="#_6-noi-dung-khong-lanh-manh" aria-label="Permalink to &quot;6. Nội dung không lành mạnh&quot;">​</a></h4><p>WWW chứa nhiều nội dung không phù hợp hoặc có hại bao gồm cả các nội dung bất hợp pháp và độc hại.</p><h4 id="_7-anh-huong-đen-nang-suat" tabindex="-1">7. Ảnh hưởng đến năng suất <a class="header-anchor" href="#_7-anh-huong-đen-nang-suat" aria-label="Permalink to &quot;7. Ảnh hưởng đến năng suất&quot;">​</a></h4><p>Truy cập không giới hạn vào WWW có thể gây xao lãng, mất tập trung, giảm năng suất học tập và năng suất làm việc.</p><h2 id="tong-ket" tabindex="-1">Tổng kết <a class="header-anchor" href="#tong-ket" aria-label="Permalink to &quot;Tổng kết&quot;">​</a></h2><ul><li>World Wide Web (WWW) là một hệ thống thông tin toàn cầu cho phép truy cập các trang web qua hyperlinks.</li><li>WWW được phát minh bởi Tim Berners-Lee vào năm 1989, là nền tảng cho nhiều hoạt động trực tuyến hiện đại.</li><li>Các thành phần cốt lõi của WWW bao gồm HTML (ngôn ngữ đánh dấu siêu văn bản), HTTP (giao thức truyền tải siêu văn bản), URL (địa chỉ tài nguyên thống nhất) và trình duyệt web.</li><li>WWW đã phát triển từ Web 1.0 (tĩnh) đến Web 2.0 (tương tác và xã hội) và Web 3.0 (ngữ nghĩa và thông minh), với ý tưởng về Web 4.0 kết hợp sâu giữa con người và công nghệ.</li><li>WWW đem lại nhiều lợi ích như truy cập thông tin rộng rãi, thương mại điện tử, giáo dục trực tuyến và giao tiếp xã hội.</li><li>Tuy nhiên, cũng tồn tại các thách thức như quá tải thông tin, vấn đề an ninh mạng, bảo mật dữ liệu, và chênh lệch kỹ thuật số.</li></ul>',77)];const c=n(a,[["render",function(n,t,e,a,c,g){return h(),i("div",null,l)}]]);export{e as __pageData,c as default};
