import{_ as n,j as i,o as t,v as g}from"./chunks/framework.9xwLhOZU.js";const h=JSON.parse('{"title":"Message là gì ?","description":"","frontmatter":{"footer":true,"title":"Message là gì ?","authors":["lethanh"],"date":"2024-04-15T00:00:00.000Z","image":"/assets/1.1Jqj7e7N.jpg","outline":"deep","draft":false,"group":"2. Message and event stream"},"headers":[{"level":2,"title":"Các loại chỉ thông tin được truyền tải giữa các đối tượng","slug":"cac-loai-chi-thong-tin-đuoc-truyen-tai-giua-cac-đoi-tuong","link":"#cac-loai-chi-thong-tin-đuoc-truyen-tai-giua-cac-đoi-tuong","children":[]},{"level":2,"title":"Vậy Message là gì ?","slug":"vay-message-la-gi","link":"#vay-message-la-gi","children":[{"level":3,"title":"Tóm gọn Message là gì","slug":"tom-gon-message-la-gi","link":"#tom-gon-message-la-gi","children":[]}]},{"level":2,"title":"Đặc điểm của Message","slug":"đac-điem-cua-message","link":"#đac-điem-cua-message","children":[]},{"level":2,"title":"Ví dụ về Message","slug":"vi-du-ve-message","link":"#vi-du-ve-message","children":[{"level":3,"title":"Ví dụ 1","slug":"vi-du-1","link":"#vi-du-1","children":[]},{"level":3,"title":"Ví dụ 2","slug":"vi-du-2","link":"#vi-du-2","children":[]}]},{"level":2,"title":"Các phương pháp trao đổi Message giữa các hệ thống.","slug":"cac-phuong-phap-trao-đoi-message-giua-cac-he-thong","link":"#cac-phuong-phap-trao-đoi-message-giua-cac-he-thong","children":[]},{"level":2,"title":"Tổng kết","slug":"tong-ket","link":"#tong-ket","children":[]}],"relativePath":"study/thanhlv-study-2024/2024-04-15-message-la-gi.md","filePath":"study/thanhlv-study-2024/2024-04-15-message-la-gi.md","lastUpdated":1738744277000}'),a={name:"study/thanhlv-study-2024/2024-04-15-message-la-gi.md"},l=[g('<h2 id="cac-loai-chi-thong-tin-đuoc-truyen-tai-giua-cac-đoi-tuong" tabindex="-1">Các loại chỉ thông tin được truyền tải giữa các đối tượng <a class="header-anchor" href="#cac-loai-chi-thong-tin-đuoc-truyen-tai-giua-cac-đoi-tuong" aria-label="Permalink to &quot;Các loại chỉ thông tin được truyền tải giữa các đối tượng&quot;">​</a></h2><nav class="table-of-contents"><ul><li><a href="#cac-loai-chi-thong-tin-đuoc-truyen-tai-giua-cac-đoi-tuong">Các loại chỉ thông tin được truyền tải giữa các đối tượng</a></li><li><a href="#vay-message-la-gi">Vậy Message là gì ?</a><ul><li><a href="#tom-gon-message-la-gi">Tóm gọn Message là gì</a></li></ul></li><li><a href="#đac-điem-cua-message">Đặc điểm của Message</a></li><li><a href="#vi-du-ve-message">Ví dụ về Message</a><ul><li><a href="#vi-du-1">Ví dụ 1</a></li><li><a href="#vi-du-2">Ví dụ 2</a></li></ul></li><li><a href="#cac-phuong-phap-trao-đoi-message-giua-cac-he-thong">Các phương pháp trao đổi Message giữa các hệ thống.</a></li><li><a href="#tong-ket">Tổng kết</a></li></ul></nav><p>Mở đầu học về <code>Message and event stream</code> thì mình sẽ giải thích các kiến thức cơ bản trước. Kiến thức đầu tiên là hiểu về <strong>Message</strong>.</p><p>Ngày nay, 1 ngày có hàng tỉ GB dữ liệu được truyền tải, giao tiếp giữa các hệ thống hoặc giữa hệ thống và khách hàng.</p><p>Tuy nhiên dữ liệu là một thứ khó để hiểu nếu chưa được làm mịn và xử lý, Trong các ngữ cảnh khác nhau thì dữ liệu có thể khác nhau, để dễ hiểu hơn về dữ liệu chúng ta gửi và nhận chúng ta thường sẽ phân loại và định nghĩa thông tin được truyền tải giữa các đối tượng.</p><p>Có một số định nghĩa chung được nhiều bên sử dụng, có thể xem ở dưới đây.</p><ul><li>Các loại chỉ thông tin được truyền tải giữa các đối tượng <ol><li>Gói tin (Packet) <ul><li>Ngữ cảnh : Truyền dữ liệu qua mạng và internet, được sử dụng nhiều trong giao thức TCP/IP.</li><li><a href="https://en.wikipedia.org/wiki/Network_packet" target="_blank" rel="noreferrer">Định nghĩa</a>: Một đơn vị(Unit) được truyền qua mạng, chứa dữ liệu và thông tin điều khiển như địa chỉ nguồn, đích và thông tin khác như số thứ tự (sequence number), kiểm tra lỗi (error checking),...</li></ul></li><li>Frame (Khung) <ul><li>Ngữ cảnh : Tầng liên kết dữ liệu trong mô hình OSI, truyền thông Ethernet và các mạng khác.</li><li><a href="https://en.wikipedia.org/wiki/Frame_(networking)" target="_blank" rel="noreferrer">Định nghĩa</a>: Một đơn vị dữ liệu trong tầng liên kết dữ liệu của mô hình OSI, Một frame bao gồm header (phần đầu), payload (phần dữ liệu), và trailer (phần cuối). Header chứa các thông tin như địa chỉ MAC (địa chỉ vật lý của thiết bị mạng), điều khiển lỗi và điều khiển truy cập vào phương trình mạng.</li></ul></li><li>Lệnh (Command) <ul><li>Ngữ cảnh : Tương tác giữa các hệ thống hoặc người dùng với hệ thống(Lệnh điều khiển...etc..)</li><li><a href="https://en.wikipedia.org/wiki/Command_(computing)" target="_blank" rel="noreferrer">Định nghĩa</a>: Thông tin chứa yêu cầu hoặc hướng dẫn để thực hiện một hành động cụ thể trên hệ thống.</li></ul></li><li>Truy vấn (Query) <ul><li>Ngữ cảnh : Sử dụng để tìm kiếm thông tin</li><li>Định nghĩa: Query trong Computing and technology là một yêu cầu để truy xuất dữ liệu trên database hoặc các hệ thống thông tin.</li></ul></li><li>Thông báo (Notification) <ul><li>Ngữ cảnh: Ứng dụng và hệ thống gửi thông báo cho người dùng về sự kiện hoặc trạng thái</li><li><a href="https://en.wikipedia.org/wiki/Notification" target="_blank" rel="noreferrer">Định nghĩa</a> : Thông tin được gửi từ một hệ thống hoặc ứng dụng để thông báo về một sự kiện hoặc trạng thái.</li></ul></li><li>Sự kiện (Event) <ul><li>Ngữ cảnh: Lập trình sự kiện, hệ thống giám sát, hệ thống phản ứng sự kiện...</li><li><a href="https://en.wikipedia.org/wiki/Event_(computing)" target="_blank" rel="noreferrer">Định nghĩa</a>: Một hành động hoặc trạng thái đã xảy ra và được phát hiện và thông báo trong hệ thống.</li></ul></li><li>Log Entry (Bản ghi log) <ul><li>Ngữ cảnh: Giám sát hệ thống, phân tích sự kiện...</li><li><a href="https://en.wikipedia.org/wiki/Logging_(computing)" target="_blank" rel="noreferrer">Định nghĩa</a>: Một mục trong tệp hoặc hệ thống nhật ký, ghi lại các sự kiện hoặc hành động trong hệ thống.</li></ul></li><li>Siêu dữ liệu (Metadata) <ul><li>Ngữ cảnh: Quản lý thông tin, tài liệu, hệ thống lưu trữ dữ liệu.</li><li><a href="https://en.wikipedia.org/wiki/Metadata" target="_blank" rel="noreferrer">Định nghĩa</a>: Metadata là &quot;dữ liệu cung cấp thông tin về dữ liệu khác&quot;, nhưng không phải nội dung của chính dữ liệu đó.</li></ul></li><li>Thông điệp (Message) <ul><li>Ngữ cảnh: Giao tiếp thông qua các hệ thống như email, tin nhắn văn bản,hệ thống giao tiếp với nhau...</li><li><a href="https://en.wikipedia.org/wiki/Message" target="_blank" rel="noreferrer">Định nghĩa</a>: Thông tin được truyền từ người gửi đến người nhận qua một kênh truyền thông cụ thể.</li></ul></li></ol><ul><li>Và nhiều khai niệm khác hoặc bạn có thể tự định nghĩa ra khái niệm của bạn, nhóm hoặc tổ chức của bạn.</li></ul></li><li>Khi nào có thể sử dụng các khái niệm này? <ol><li>Truyền dữ liệu qua mạng: Sử dụng &quot;Gói tin (Packet)&quot; và &quot;Khung (Frame)&quot; để mô tả các đơn vị dữ liệu truyền qua mạng.</li><li>Giao tiếp giữa người dùng: Sử dụng &quot;Thông điệp (Message)&quot; và &quot;Thông báo (Notification)&quot; trong ngữ cảnh gửi email, tin nhắn văn bản, và thông báo ứng dụng.</li><li>Tương tác hệ thống: Sử dụng &quot;Lệnh (Command)&quot; và &quot;Truy vấn (Query)&quot; khi mô tả các yêu cầu và hành động giữa người dùng và hệ thống</li><li>Giám sát và phản ứng: Sử dụng &quot;Sự kiện (Event)&quot; và &quot;Log Entry (Bản ghi log)&quot; để mô tả các hành động và tình huống trong hệ thống.</li><li>Quản lý thông tin: Sử dụng &quot;Siêu dữ liệu (Metadata)&quot; khi cần mô tả và quản lý các đặc tính của dữ liệu khác.</li></ol></li></ul><h2 id="vay-message-la-gi" tabindex="-1">Vậy Message là gì ? <a class="header-anchor" href="#vay-message-la-gi" aria-label="Permalink to &quot;Vậy Message là gì ?&quot;">​</a></h2><p>Message là một trong các định nghĩa chung được nhiều bên, hệ thống sử dụng để <code>chỉ thông tin được truyền tải giữa các đối tượng</code>.</p><ul><li><p><strong>Đầu tiên</strong>, <strong>Message</strong> Có nghĩa là <strong>Tin nhắn</strong> hoặc <strong>Thông điệp</strong> là một thông tin ở dạng dữ liệu được gửi từ A đến B(Từ hệ thống A đến hệ thống B) và được B nhận.</p></li><li><p><strong>Thứ hai</strong>, <strong>Message</strong> thường đi kèm với metadata, tức là các thông tin mô tả về Message. Metadata này có thể gồm nhiều thông tin, người gửi, thời gian gửi, người nhận...</p></li><li><p><strong>Thứ ba</strong>, Format của dữ liệu <strong>Message</strong> sẽ là một hợp đồng giữa A và B. hợp đồng này gồm nhiều thứ ví dụ: Định dạng tin nhắn(Json), thuật toán mã hóa, các dữ liệu require trong Json....</p><ul><li>Thông thường Format này sẽ được hệ thống nhận tin nhắn, tức B xác định. Tuy nhiên nhiều trường hợp có thể A xác định và B sẽ làm theo.</li></ul></li><li><p><strong>Thứ tư</strong>, <strong>Message</strong> được mang ý nghĩa giả định người gửi sẽ gửi dữ liệu và sẽ có một người nhận. Từ dữ liệu của người gửi, người nhận có thể thực hiện các hành động tương ứng hoặc không làm gì cả, điều này phụ thuộc vào nghiệp vụ của người nhận.</p><ul><li>Nếu nghiệp vụ của người nhận(B) có thực hiện một hành động nào đó khi nhận Message, nhưng hành động không được thực hiện tức là đã bị phạm một <strong>business rule</strong> nào đó của bên B. <ul><li>Thông thường, đây là bước đầu tiên của một quá trình dẫn đến, A nhận được dữ liệu của B(HTML), B thay đổi dữ liệu.... <img src="/assets/1.1Jqj7e7N.jpg" alt="Image"></li></ul></li></ul></li></ul><h3 id="tom-gon-message-la-gi" tabindex="-1"><strong>Tóm gọn Message là gì</strong> <a class="header-anchor" href="#tom-gon-message-la-gi" aria-label="Permalink to &quot;**Tóm gọn Message là gì**&quot;">​</a></h3><ul><li>Message là một yêu cầu thường chứa một khối lượng dữ liệu được chuyển từ một hệ thống sang hệ thống khác để thực hiện các hành động xử lý hoặc trao đổi thông tin.</li></ul><h2 id="đac-điem-cua-message" tabindex="-1">Đặc điểm của Message <a class="header-anchor" href="#đac-điem-cua-message" aria-label="Permalink to &quot;Đặc điểm của Message&quot;">​</a></h2><ul><li><p><strong>Đóng gói thông tin</strong>: Message chứ dữ liệu hoặc thông tin được đóng gói. Thông tin này có thể bảo gồm tin nhắn, câu lệnh, dữ liệu cần truyền đạt...</p></li><li><p><strong>Có cấu trúc</strong>: Message thường có một cấu trúc xác định, bảo gồm các metadata và nội dung(body). Metadata có thể chứa các thông tin về loại message, người gửi, người nhận và nhiều thuộc tính khác. Phần body chứa dữ liệu thực sự cần gửi đi.</p></li><li><p><strong>Đồng bộ hoặc không đồng bộ:</strong> :Message có thể được gửi một cách đồng bộ, người gửi sẽ chờ đợi phản hồi của người nhận trước khi tiếp tục, hoặc không đồng bộ nơi người gửi tiếp tục công việc mà không cần chờ phản hồi.</p></li><li><p><strong>Độc lập với giao thức</strong>: Message có thể được gửi qua nhiều giao thức khác nhau, HTTP, HTTPS, FTP, SMTP, MIME...</p></li><li><p><strong>Bảo mật</strong>: Message có thể được mã hóa và ký để đảm bảo tính bảo mật, toàn vẹn và xác thực nguồn gốc giúp bảo vệ khỏi sự nghe, nhìn lén và thay đổi hoặc giả mạo.</p></li><li><p><strong>Giao tiếp giữa các chệ thống</strong>: Message là phương tiện chính hiện tại sử dụng để giao tiếp giữa các hệ thống, dịch vụ và ứng dụng. Message cho phép chúng ta trao đổi dữ liệu linh hoạt và hiệu quả.</p></li><li><p><strong>Định hướng người nhận(Recipient Orientation)</strong>: Mỗi message được tạo ra sẽ gửi đi sẽ xác định đến 1 hoặc N người nhận message để đảm bảo rằng message đến đúng người hoặc hệ thống cần nó.</p></li></ul><h2 id="vi-du-ve-message" tabindex="-1">Ví dụ về Message <a class="header-anchor" href="#vi-du-ve-message" aria-label="Permalink to &quot;Ví dụ về Message&quot;">​</a></h2><h3 id="vi-du-1" tabindex="-1"><strong>Ví dụ 1</strong> <a class="header-anchor" href="#vi-du-1" aria-label="Permalink to &quot;**Ví dụ 1**&quot;">​</a></h3><p>Application <strong>A</strong> gửi Message đến Application <strong>B</strong> yêu cầu lấy về danh sách học sinh(Tin nhắn được gửi thông qua giao thức <strong>HTTP</strong>).</p><ul><li><ol><li><strong>A</strong> gửi <strong>Message</strong> đến B yều cầu lấy danh sách học sinh thông qua giao thức <strong>HTTP</strong></li></ol></li><li><ol start="2"><li><strong>B</strong> nhận được yêu cầu và kiểm tra đúng các định dạng format của <strong>HTTP</strong> và đúng <strong>business rule</strong>(User ở <strong>A</strong> có quyền đọc...)</li></ol><ul><li>Nếu <strong>A</strong> gửi sai format hoặc vi phạm business rule thì B sẽ từ chối yêu cầu.</li></ul></li><li><ol start="3"><li><strong>B</strong> sử lý yêu cầu và gửi trả lại phản hồi cho <strong>A</strong>.</li></ol></li></ul><h3 id="vi-du-2" tabindex="-1"><strong>Ví dụ 2</strong> <a class="header-anchor" href="#vi-du-2" aria-label="Permalink to &quot;**Ví dụ 2**&quot;">​</a></h3><p>Gửi thư từ nhà của <strong>A</strong> ở <strong>Hà Nội</strong> đến <strong>B</strong> ở <strong>Đà Nẵng</strong> yêu cầu vay 500k VND</p><ul><li><ol><li><strong>A</strong> thực hiện viết Message vào giấy để giở thư đến cho <strong>B</strong>.</li></ol></li><li><ol start="2"><li><strong>A</strong> sẽ sử dụng giao thức gửi thư qua bưu điện vì vậy cần sử dụng <strong>format bao thư</strong> kèm theo <strong>Metadata</strong> người gửi và người nhận.</li></ol></li><li><ol start="3"><li>Sau khi hoàn thành đầy đủ, giao thức bưu điện sẽ gửi thư đến <strong>B</strong> ở <strong>Đà Nẵng</strong>.</li></ol></li><li><ol start="4"><li>Sau khi B nhận thư, tiến hành đọc và xử lý các yêu cầu dựa trên <strong>business rule</strong>( Chơi thân không? Mình có 500k VND không ...)</li></ol></li><li><ol start="5"><li>Nếu thỏa mãn <strong>business rule</strong> B sẽ gửi Message lại đến B với 500K VND họặc không thỏa mãn sẽ gửi lại Message từ chối yêu cầu đến A thông qua giao thức bưu điện.</li></ol></li></ul><h2 id="cac-phuong-phap-trao-đoi-message-giua-cac-he-thong" tabindex="-1">Các phương pháp trao đổi Message giữa các hệ thống. <a class="header-anchor" href="#cac-phuong-phap-trao-đoi-message-giua-cac-he-thong" aria-label="Permalink to &quot;Các phương pháp trao đổi Message giữa các hệ thống.&quot;">​</a></h2><ul><li><strong>Remote Procedure Calls (RPC)</strong>: Một kỹ thuật sử dụng để cho phép một ứng dụng thực hiện call một method trên ứng dụng khác thông qua network. RPC ẩn đi chi tiết về giao tiếp mạng, cho phép nhà phát triển tập trung vào logic nghiệp vụ. Ví dụ phổ biến bao gồm gRPC và XML-RPC</li><li><strong>Socket Programming</strong> : Cho phép giao tiếp 2 chiều giữa một kết nối, Socket có thể được sử dụng để nhận dữ liệu thông qua TCP, UDP.</li><li><strong>APIs</strong>: Các API (Application Programming Interfaces) như RESTful APIs hoặc GraphQL APIs cho phép các ứng dụng giao tiếp với nhau và trao đổi thông tin một cách có cấu trúc.</li><li><strong>Message Queuing</strong>: Message Queuing (MQ) cho phép các ứng dụng gửi và nhận Message một cách đáng tin cậy và hiệu quả. Ví dụ phổ biến bao gồm RabbitMQ</li><li>Và rất nhiều phương pháp khác.</li></ul><h2 id="tong-ket" tabindex="-1">Tổng kết <a class="header-anchor" href="#tong-ket" aria-label="Permalink to &quot;Tổng kết&quot;">​</a></h2><ul><li><p><strong>Message</strong> là một yêu cầu chứa dữ liệu được gửi từ hệ thống này sang hệ thống khác, có mục đích xử lý hoặc trao đổi thông tin.</p></li><li><p><strong>Không chỉ là dữ liệu</strong>, Message mang giá trị, ý nghĩa và mục đích, đóng vai trò là cầu nối giữa các thành phần trong hệ thống, tạo luồng thông tin liên tục.</p></li><li><p><strong>Metadata đi kèm với Message</strong> mô tả thông tin về người gửi, thời gian gửi, người nhận, giúp quản lý và xử lý Message hiệu quả.</p></li><li><p><strong>Format dữ liệu của Message</strong> thường được xác định bởi hợp đồng giữa hệ thống gửi và hệ thống nhận, thường là JSON, bao gồm cả thuật toán mã hóa và dữ liệu cần thiết.</p></li><li><p><strong>Mục đích</strong> của việc gửi Message là để truyền đạt thông tin hoặc yêu cầu thực hiện một công việc cụ thể giữa các thành phần trong hệ thống.</p></li><li><p><strong>Có nhiều phương pháp trao đổi message</strong> như Message Queue, APIs, RPC...</p></li></ul>',25)];const c=n(a,[["render",function(n,g,h,a,c,e){return t(),i("div",null,l)}]]);export{h as __pageData,c as default};
