import{_ as n,j as t,o as h,v as i}from"./chunks/framework.9xwLhOZU.js";const g=JSON.parse('{"title":"Event là gì ?","description":"","frontmatter":{"footer":true,"title":"Event là gì ?","authors":["lethanh"],"date":"2024-04-16T00:00:00.000Z","image":"/assets/1.PDIRLfIW.jpg","outline":"deep","draft":false,"group":"2. Message and event stream"},"headers":[{"level":2,"title":"Event là gì ?","slug":"event-la-gi","link":"#event-la-gi","children":[{"level":3,"title":"3 thành phần chính của Event.","slug":"_3-thanh-phan-chinh-cua-event","link":"#_3-thanh-phan-chinh-cua-event","children":[]},{"level":3,"title":"Đặc điểm của event.","slug":"đac-điem-cua-event","link":"#đac-điem-cua-event","children":[]},{"level":3,"title":"Vai trò của Event trong hệ thống:","slug":"vai-tro-cua-event-trong-he-thong","link":"#vai-tro-cua-event-trong-he-thong","children":[]},{"level":3,"title":"Ví dụ về hệ thống đặt hàng.","slug":"vi-du-ve-he-thong-đat-hang","link":"#vi-du-ve-he-thong-đat-hang","children":[{"level":4,"title":"Bối cảnh:","slug":"boi-canh","link":"#boi-canh","children":[]},{"level":4,"title":"Quá trình xử lý event","slug":"qua-trinh-xu-ly-event","link":"#qua-trinh-xu-ly-event","children":[]}]}]},{"level":2,"title":"Tổng kết","slug":"tong-ket","link":"#tong-ket","children":[]}],"relativePath":"study/thanhlv-study-2024/2024-04-16-event-la-gi.md","filePath":"study/thanhlv-study-2024/2024-04-16-event-la-gi.md","lastUpdated":1741105661000}'),l={name:"study/thanhlv-study-2024/2024-04-16-event-la-gi.md"},e=[i('<nav class="table-of-contents"><ul><li><a href="#event-la-gi">Event là gì ?</a><ul><li><a href="#_3-thanh-phan-chinh-cua-event">3 thành phần chính của Event.</a></li><li><a href="#đac-điem-cua-event">Đặc điểm của event.</a></li><li><a href="#vai-tro-cua-event-trong-he-thong">Vai trò của Event trong hệ thống:</a></li><li><a href="#vi-du-ve-he-thong-đat-hang">Ví dụ về hệ thống đặt hàng.</a><ul><li><a href="#boi-canh">Bối cảnh:</a></li><li><a href="#qua-trinh-xu-ly-event">Quá trình xử lý event</a></li></ul></li></ul></li><li><a href="#tong-ket">Tổng kết</a></li></ul></nav><h2 id="event-la-gi" tabindex="-1">Event là gì ? <a class="header-anchor" href="#event-la-gi" aria-label="Permalink to &quot;Event là gì ?&quot;">​</a></h2><ul><li><p>Event là một trong các định nghĩa chung được nhiều bên, hệ thống sử dụng để <code>chỉ thông tin được truyền tải giữa các đối tượng</code>.</p></li><li><p>Event dịch sang tiếng việt là <strong>Sự Kiện</strong> trong bối cảnh phát triển phần mềm có thể được hiểu là <strong>một thay đổi trạng thái, một hành động cụ thể, một thứ gì đó</strong> ===&gt; <strong>đã xảy ra</strong>. Ví dụ:</p></li></ul><ol><li>Thành đã xuống máy tại ở Sài Gòn lúc 10h sáng ngày 20-11-2024.</li><li>Team phát triển đã Deploy service lúc 23h ngày 20-11-2024</li></ol><ul><li>Thông thường <strong>Event</strong> sẽ được tạo sau khi một <strong>Message</strong> kết thúc, tùy nhiên điều đó không bắt buộc. (Có thể từ một Event khác, hệ thống tự phát ra, từ kết quả của một tính toán định kỳ(Định kỳ 10s đo thân nhiệt).....) <img src="/assets/1.PDIRLfIW.jpg" alt="Image"></li></ul><h3 id="_3-thanh-phan-chinh-cua-event" tabindex="-1">3 thành phần chính của Event. <a class="header-anchor" href="#_3-thanh-phan-chinh-cua-event" aria-label="Permalink to &quot;3 thành phần chính của Event.&quot;">​</a></h3><ul><li><strong>Key</strong> : Định danh của sự kiện. <ul><li><strong>Key</strong> là định danh duy nhất của sự kiện, ví dụ key <strong>&quot;Tăng nhịp tim&quot;</strong> hoặc <strong>&quot;Chớp mắt&quot;</strong></li></ul></li><li><strong>Value</strong> : Data của sự kiện <ul><li>&quot;Value&quot; là data của sự kiện này, ví dụ Key <strong>&quot;Tăng nhịp tim&quot;</strong> lên <strong>120</strong> thì <strong>120</strong> chính là <strong>Value</strong></li></ul></li><li><strong>Timestamp</strong> : Thời gian xảy ra sự kiện. <ul><li>Chính là thời gian sự kiện này xảy ra ví dụ <strong>Tuấn</strong> <strong>&quot;Tăng nhịp tim&quot;</strong> lên <strong>120</strong> lúc <strong>10 sáng ngày 20-11-2024</strong></li></ul></li></ul><h3 id="đac-điem-cua-event" tabindex="-1">Đặc điểm của event. <a class="header-anchor" href="#đac-điem-cua-event" aria-label="Permalink to &quot;Đặc điểm của event.&quot;">​</a></h3><ul><li><p><strong>Thời điểm xác định</strong>: Mỗi Event đều liên quan đến một thời điểm cụ thể Event đó xảy ra.(Ví dụ 12h) <img src="/assets/2.hQTljj11.jpg" alt="Image"></p></li><li><p><strong>Thông tin</strong>: Event mang theo thông tin liên quan đến event đó, giúp các thành phần khác trong hệ thống có thể phản ứng một cách thích hợp. <img src="/assets/3.HToBxnsS.jpg" alt="Image"></p></li><li><p><strong>Không đồng bộ</strong>: Việc xử lý Event <strong>thường</strong> diễn ra không đồng bộ, tức là hệ thống không cần dừng lại để chờ đợi 1 event xử lý thành công.</p></li><li><p><strong>Kích hoạt hành động</strong>: Event có thể có thể được sử dụng để kích hoạt 1 hoặc nhiều hành động, quy trình(job) trong hệ thống tạo điều kiện cho việc phản ứng linh hoạt các thay đổi.</p></li><li><p><strong>Tính phân tán(Broadcast Nature)</strong>: Khác với <a href="./2024-04-15-message-la-gi.html">Message</a> sẽ có người nhận cụ thể vì nó là yêu cầu gửi đến thành phần khác, Event không nhất thiết phải có một người nhận cụ thể.</p><ul><li>Event được phát ra để thông báo một sự kiện thay đổi trạng thái, hành động xảy ra. Ứng dụng tạo ra event và không cần biết ai sẽ nhận event và xử lý nó.</li><li>Các thành phần trong hệ thống có thể lắng nghe tất cả hoặc đăng ký lắng nghe các event mà hệ thống quan tâm sau đó phản ứng với event xảy ra mà không cần Evetn chỉ định trực tiếp người nhận. <img src="/assets/4.6ALJcQOy.webp" alt="Image"></li></ul></li></ul><h3 id="vai-tro-cua-event-trong-he-thong" tabindex="-1">Vai trò của Event trong hệ thống: <a class="header-anchor" href="#vai-tro-cua-event-trong-he-thong" aria-label="Permalink to &quot;Vai trò của Event trong hệ thống:&quot;">​</a></h3><ul><li><p><strong>Tăng cường tính linh hoạt</strong>: Event giúp các thành phần trong hệ thống có thể tương tác với nhau một cách linh hoạt và không cần phải biết trước về sau</p></li><li><p><strong>Phản ứng với thay đổi</strong>: Hệ thống có thể được thiết kế để tự động phản ứng với các Event giúp xử lý các thay đổi một cách nhanh chóng và hiệu quả.</p></li><li><p><strong>Tách biệt nghiệp vụ</strong>: Event giúp tách biệt nghiệp vụ và logic xử lý làm cho hệ thống dễ quản lý và bảo trì.</p><ul><li>Các thành phần không phụ thuộc trực tiếp vào nhau để hoàn thành nghiệm vụ, thay vào đó tương tác thông qua nhận và gửi event</li></ul></li><li></li><li><p><strong>Tối ưu hóa hiệu suất</strong>: Việc sử dụng Event có thể giúp tối ưu hóa hiệu suất hệ thống bằng cách giảm thiểu sự phụ thuộc và chờ đợi giữa các thành phần.</p></li></ul><h3 id="vi-du-ve-he-thong-đat-hang" tabindex="-1">Ví dụ về hệ thống đặt hàng. <a class="header-anchor" href="#vi-du-ve-he-thong-đat-hang" aria-label="Permalink to &quot;Ví dụ về hệ thống đặt hàng.&quot;">​</a></h3><h4 id="boi-canh" tabindex="-1"><strong>Bối cảnh:</strong> <a class="header-anchor" href="#boi-canh" aria-label="Permalink to &quot;**Bối cảnh:**&quot;">​</a></h4><ul><li>Khi một khách hàng hoàn tất quá trình đặt hàng trên web thương mai điện tử, hệ thống sẽ tạo ra một event <strong>&quot;Đặt hàng mới&quot;</strong>. (Ví dụ sẽ là Thanh toán khi nhận hàng)</li></ul><h4 id="qua-trinh-xu-ly-event" tabindex="-1">Quá trình xử lý event <a class="header-anchor" href="#qua-trinh-xu-ly-event" aria-label="Permalink to &quot;Quá trình xử lý event&quot;">​</a></h4><ul><li><ol><li>Hệ thống phát ra Event <strong>&quot;Đặt hàng mới&quot;</strong>.</li></ol><ul><li>Hệ thống đặt hàng tạo và phát ra event <strong>&quot;Đặt hàng mới&quot;</strong>, bao gồm tất cả thông tin cần thiết về đơn hàng, ID đơn hàng, thông tin sản phẩm, thông tin khách hàng, tổng giá trị đơn.</li></ul></li><li><ol start="2"><li><strong>Xử lý kho hàng:</strong></li></ol><ul><li>Hệ thống quản lý kho hàng sẽ lắng nghẹ event <strong>&quot;Đặt hàng mới&quot;</strong>. Khi nhận được event, nó kiểm tra sự có sẵn của sản phẩm trong kho. Nếu thiếu hàng nó sẽ phát ra Event <strong>&quot;Yêu cầu bổ xung hàng&quot;</strong> để thông báo cho bộ phận bán hàng.</li></ul></li><li><ol start="3"><li><strong>Xử lý thanh toán :</strong></li></ol><ul><li>Hệ thống thanh toán sẽ lắng nghe Event <strong>&quot;Đặt hàng mới&quot;</strong>. Dựa trên thông tin đơn hàng, tiến hành xác minh và xử lý thanh từ khách hàng.</li><li>Nếu thanh toán thành công, hệ thống sẽ phát Event <strong>&quot;Thanh toán thành công&quot;</strong>; nếu thất bại hệ thống phát Event <strong>&quot;Thanh toán thất bại&quot;</strong></li><li>Nếu thanh toán khi nhận hàng sẽ không làm gì cả.</li></ul></li><li><ol start="4"><li><strong>Xử lý thông báo</strong></li></ol><ul><li>Hệ thống thông báo sẽ lắng nghe Events <strong>&quot;Đặt hàng mới&quot;</strong> , <strong>&quot;Thanh toán thành công&quot;</strong>, <strong>&quot;Thanh toán thất bại&quot;</strong>, <strong>&quot;Yêu cầu bổ xung hàng&quot;</strong></li><li>Tuy thuộc vào Event và các cài đặt nhận thông báo của người dùng, hệ thống sẽ gửi thông báo đến người dùng khi nhận được Event (APp, SMS, Email...)</li></ul></li><li><ol start="5"><li><strong>Xử lý vận chuyển</strong>.</li></ol><ul><li>Hệ thống Xử lý vận chuyển sẽ lắng nghe Events <strong>&quot;Đặt hàng mới&quot;</strong> , <strong>&quot;Thanh toán thành công&quot;</strong>.</li><li>Trong trường hợp thanh toán nhận hàng, khi nhận được <strong>&quot;Đặt hàng mới&quot;</strong> thì hệ thống sẽ tự động tiến hành logic xử lý vận chuyển</li><li>Trong trường hợp thanh toán trước sẽ đợi Event <strong>&quot;Thanh toán thành công&quot;</strong></li></ul></li></ul><h2 id="tong-ket" tabindex="-1">Tổng kết <a class="header-anchor" href="#tong-ket" aria-label="Permalink to &quot;Tổng kết&quot;">​</a></h2><ul><li><p>Event là khái niệm cốt lõi trong xây dựng hệ thống phần mềm dựa trên sự kiện. Mỗi Event là một sự kiện đã xảy ra(Thay đổi trạng thái, thiệt độ) hoặc hành động đã xảy ra....</p></li><li><p>Event không chỉ thông báo sự kiện, nó còn có thể kích hoat các sự kiện khác (Lắng nghe sau đó kích hoạt sự kiện mới), tạo ra hệ thống linh hoạt.</p></li><li><p>Event có thời điểm xác định, mang các thông tin liên quan đến Event, không đồng bộ và có tính phân tán không cần người nhận cụ thể.</p></li><li><p>Trong hệ thống phần mềm, Event tăng tính linh hoạt, phản ứng thay đổi, tách biệt nghiệp vụ và tối ưu hiệu suất.</p></li></ul><h1 id="ref" tabindex="-1">REF: <a class="header-anchor" href="#ref" aria-label="Permalink to &quot;REF:&quot;">​</a></h1><p><a href="https://viblo.asia/p/domain-events-la-gi-924lJ8j8KPM" target="_blank" rel="noreferrer">https://viblo.asia/p/domain-events-la-gi-924lJ8j8KPM</a></p>',20)];const a=n(l,[["render",function(n,i,g,l,a,o){return h(),t("div",null,e)}]]);export{g as __pageData,a as default};
