import{_ as n,j as t,o as i,v as c}from"./chunks/framework.OuVixYWh.js";const h=JSON.parse('{"title":"INT11176 - Nhập môn Internet và E-learning","description":"Nhập môn Internet và E-learning là khóa học giúp người học hiểu rõ về E-learning, các đặc điểm, cấu trúc, thành phần của hệ thống E-learning, các phương thức học tập E-learning, và các kỹ năng cần thiết để học E-learning.","frontmatter":{"title":"INT11176 - Nhập môn Internet và E-learning","description":"Nhập môn Internet và E-learning là khóa học giúp người học hiểu rõ về E-learning, các đặc điểm, cấu trúc, thành phần của hệ thống E-learning, các phương thức học tập E-learning, và các kỹ năng cần thiết để học E-learning.","footer":true,"outline":"deep","date":"2025-07-05T00:00:00.000Z","group":"All"},"headers":[{"level":2,"title":"E-learning là gì?","slug":"e-learning-la-gi","link":"#e-learning-la-gi","children":[]},{"level":2,"title":"Đặc điểm của E-learning","slug":"đac-điem-cua-e-learning","link":"#đac-điem-cua-e-learning","children":[{"level":3,"title":"Lớp học truyền thống","slug":"lop-hoc-truyen-thong","link":"#lop-hoc-truyen-thong","children":[]},{"level":3,"title":"Lớp học E-learning","slug":"lop-hoc-e-learning","link":"#lop-hoc-e-learning","children":[]}]},{"level":2,"title":"Cấu trúc của hệ thống E-learning","slug":"cau-truc-cua-he-thong-e-learning","link":"#cau-truc-cua-he-thong-e-learning","children":[{"level":3,"title":"Hạ tầng và kỹ thuật","slug":"ha-tang-va-ky-thuat","link":"#ha-tang-va-ky-thuat","children":[]}]},{"level":2,"title":"Các thành phần chính của hệ thống E-learning","slug":"cac-thanh-phan-chinh-cua-he-thong-e-learning","link":"#cac-thanh-phan-chinh-cua-he-thong-e-learning","children":[]},{"level":2,"title":"Các phương thức học tập E-learning","slug":"cac-phuong-thuc-hoc-tap-e-learning","link":"#cac-phuong-thuc-hoc-tap-e-learning","children":[{"level":3,"title":"Synchronous Learning","slug":"synchronous-learning","link":"#synchronous-learning","children":[{"level":4,"title":"Lợi ích:","slug":"loi-ich","link":"#loi-ich","children":[]}]},{"level":3,"title":"Asynchronous Learning","slug":"asynchronous-learning","link":"#asynchronous-learning","children":[{"level":4,"title":"Ví dụ:","slug":"vi-du","link":"#vi-du","children":[]},{"level":4,"title":"Lợi ích:","slug":"loi-ich-1","link":"#loi-ich-1","children":[]}]}]},{"level":2,"title":"Học tập kết hợp (Blended Learning)","slug":"hoc-tap-ket-hop-blended-learning","link":"#hoc-tap-ket-hop-blended-learning","children":[{"level":3,"title":"Lợi ích:","slug":"loi-ich-2","link":"#loi-ich-2","children":[]}]},{"level":2,"title":"Các kỹ năng cần thiết để học E-learning","slug":"cac-ky-nang-can-thiet-đe-hoc-e-learning","link":"#cac-ky-nang-can-thiet-đe-hoc-e-learning","children":[{"level":3,"title":"Kỹ năng sử dụng công nghệ","slug":"ky-nang-su-dung-cong-nghe","link":"#ky-nang-su-dung-cong-nghe","children":[]},{"level":3,"title":"Kỹ năng tự học","slug":"ky-nang-tu-hoc","link":"#ky-nang-tu-hoc","children":[]}]}],"relativePath":"study/thanhlv-study-2025-ptit-hoc-online/2025-07-05-INT11176-nhap-mon-internet-va-elearning.md","filePath":"study/thanhlv-study-2025-ptit-hoc-online/2025-07-05-INT11176-nhap-mon-internet-va-elearning.md","lastUpdated":1751688731000}'),l={name:"study/thanhlv-study-2025-ptit-hoc-online/2025-07-05-INT11176-nhap-mon-internet-va-elearning.md"},a=[c('<h2 id="e-learning-la-gi" tabindex="-1">E-learning là gì? <a class="header-anchor" href="#e-learning-la-gi" aria-label="Permalink to &quot;E-learning là gì?&quot;">​</a></h2><ul><li><strong>E-learning</strong> là viết tắt của &quot;Electronic Learning&quot;, nghĩa là học tập thông qua các phương tiện điện tử, thường là qua internet. <img src="https://static-cdn.thanhlv.com/study/thanhlv-study-2025-ptit-hoc-online/2025-07-05-INT11176-nhap-mon-internet-va-elearning/bMz3aFhmjrzCjYaA.png" alt=""></li></ul><h2 id="đac-điem-cua-e-learning" tabindex="-1">Đặc điểm của E-learning <a class="header-anchor" href="#đac-điem-cua-e-learning" aria-label="Permalink to &quot;Đặc điểm của E-learning&quot;">​</a></h2><h3 id="lop-hoc-truyen-thong" tabindex="-1">Lớp học truyền thống <a class="header-anchor" href="#lop-hoc-truyen-thong" aria-label="Permalink to &quot;Lớp học truyền thống&quot;">​</a></h3><ul><li><strong>Lớp học truyền thống</strong> thường diễn ra trong một không gian vật lý, nơi học sinh và giáo viên gặp nhau trực tiếp.</li><li><strong>Đặc điểm</strong>: <ul><li><ol><li>Học sinh và giáo viên gặp nhau trực tiếp tại một không gian học tập cụ thể. (Lớp học, trường học, trung tâm đào tạo...)</li></ol></li><li><ol start="2"><li>Sử dụng bảng đen, sách giáo khoa, và các tài liệu học tập truyền thống, đôi khi vẫn có thể sử dụng máy tính hoặc thiết bị điện tử.</li></ol></li><li><ol start="3"><li>Thời gian học cố định, thường theo lịch trình của trường học.</li></ol></li><li><ol start="4"><li>Học sinh có thể tương tác trực tiếp với giáo viên và bạn học.</li></ol></li></ul></li></ul><h3 id="lop-hoc-e-learning" tabindex="-1">Lớp học E-learning <a class="header-anchor" href="#lop-hoc-e-learning" aria-label="Permalink to &quot;Lớp học E-learning&quot;">​</a></h3><ul><li><strong>Lớp học E-learning</strong> là lớp học trực tuyến, nơi học sinh và giáo viên có thể tương tác qua internet.</li><li><strong>Đặc điểm</strong>: <ul><li>Học sinh và giáo viên có thể ở bất cứ đâu, chỉ cần có kết nối internet.</li><li>Sử dụng các công cụ trực tuyến như video call, chat, và các nền tảng học tập trực tuyến.</li><li>Thời gian học linh hoạt, có thể truy cập tài liệu học tập bất cứ lúc nào.</li><li>Học sinh có thể tương tác với giáo viên và bạn học qua các công cụ trực tuyến.</li></ul></li></ul><h2 id="cau-truc-cua-he-thong-e-learning" tabindex="-1">Cấu trúc của hệ thống E-learning <a class="header-anchor" href="#cau-truc-cua-he-thong-e-learning" aria-label="Permalink to &quot;Cấu trúc của hệ thống E-learning&quot;">​</a></h2><h3 id="ha-tang-va-ky-thuat" tabindex="-1">Hạ tầng và kỹ thuật <a class="header-anchor" href="#ha-tang-va-ky-thuat" aria-label="Permalink to &quot;Hạ tầng và kỹ thuật&quot;">​</a></h3><ul><li><strong>Hạ tầng</strong>: Yêu cầu một hệ thống máy chủ mạnh mẽ để lưu trữ và xử lý dữ liệu học tập, bao gồm video, tài liệu, và các bài tập.</li><li><strong>Hệ thống</strong>: Sử dụng các nền tảng học tập trực tuyến (LMS) <ul><li><strong>Kỹ thuật</strong>: Sử dụng các công nghệ web như HTML, CSS, JavaScript để xây dựng giao diện người dùng, và các ngôn ngữ lập trình như Python, Java, hoặc Node.js để phát triển backend.</li><li><strong>Cơ sở dữ liệu</strong>: Sử dụng các hệ quản trị cơ sở dữ liệu như MySQL, PostgreSQL, hoặc MongoDB để lưu trữ thông tin người dùng, khóa học, và kết quả học tập.</li></ul></li><li><strong>Nội dung hoc tập</strong>: Bao gồm video, bài viết, bài tập, và các tài liệu học tập khác. Nội dung này có thể được tạo ra bởi giáo viên hoặc các chuyên gia trong lĩnh vực.</li></ul><h2 id="cac-thanh-phan-chinh-cua-he-thong-e-learning" tabindex="-1">Các thành phần chính của hệ thống E-learning <a class="header-anchor" href="#cac-thanh-phan-chinh-cua-he-thong-e-learning" aria-label="Permalink to &quot;Các thành phần chính của hệ thống E-learning&quot;">​</a></h2><ul><li><strong>Người dùng</strong>: Bao gồm học sinh, giáo viên, và quản trị viên hệ thống.</li><li><strong>Nền tảng học tập trực tuyến (LMS)</strong>: Là nơi lưu trữ và quản lý các khóa học, tài liệu học tập, và kết quả học tập của người dùng.</li><li><strong>Công cụ tương tác</strong>: Bao gồm các công cụ video call, chat, và các công cụ trực tuyến khác để người dùng có thể tương tác với nhau.</li><li><strong>Công cụ đánh giá</strong>: Bao gồm các bài kiểm tra, bài tập, và các công cụ đánh giá khác để đánh giá kết quả học tập của người dùng.</li><li><strong>Công cụ quản lý</strong>: Bao gồm các công cụ để quản lý người dùng, khóa học, và các tài liệu học tập. Quản trị viên có thể sử dụng các công cụ này để theo dõi tiến độ học tập của người dùng, quản lý nội dung học tập, và xử lý các vấn đề kỹ thuật.</li></ul><h2 id="cac-phuong-thuc-hoc-tap-e-learning" tabindex="-1">Các phương thức học tập E-learning <a class="header-anchor" href="#cac-phuong-thuc-hoc-tap-e-learning" aria-label="Permalink to &quot;Các phương thức học tập E-learning&quot;">​</a></h2><ul><li>Có 2 kiểu học tập E-learning chính: <ul><li><strong>Synchronous Learning</strong>: Học trực tuyến theo thời gian thực, có thể tương tác với giảng viên và bạn học.</li><li><strong>Asynchronous Learning</strong>: Học trực tuyến không theo thời gian thực, có thể truy cập tài liệu học tập bất cứ lúc nào.</li><li><strong>Blended Learning</strong>: Kết hợp giữa học trực tuyến và học truyền thống, cho phép linh hoạt trong việc học tập.</li></ul></li></ul><h3 id="synchronous-learning" tabindex="-1">Synchronous Learning <a class="header-anchor" href="#synchronous-learning" aria-label="Permalink to &quot;Synchronous Learning&quot;">​</a></h3><ul><li><strong>Đặc điểm</strong>: <ul><li>Học trực tuyến theo thời gian thực.</li><li>Có thể tương tác với giảng viên và bạn học.</li><li>Thường sử dụng video call, chat, hoặc các công cụ trực tuyến khác.</li></ul></li></ul><h4 id="loi-ich" tabindex="-1">Lợi ích: <a class="header-anchor" href="#loi-ich" aria-label="Permalink to &quot;Lợi ích:&quot;">​</a></h4><ul><li>Có thể tương tác trực tiếp với giảng viên và bạn học.</li><li>Giúp duy trì động lực học tập tốt hơn là học một mình --&gt; Con người là động vật xã hội, cần có sự tương tác với người khác để duy trì động lực học tập.</li><li>Giúp giải quyết các vấn đề khó khăn ngay lập tức. Có thể hỏi giảng viên hoặc bạn học ngay lập tức khi gặp khó khăn trong quá trình học tập.</li></ul><h3 id="asynchronous-learning" tabindex="-1">Asynchronous Learning <a class="header-anchor" href="#asynchronous-learning" aria-label="Permalink to &quot;Asynchronous Learning&quot;">​</a></h3><ul><li><strong>Đặc điểm</strong>: <ul><li>Học trực tuyến không theo thời gian thực.</li><li>Có thể truy cập tài liệu học tập bất cứ lúc nào.</li><li>Thường sử dụng video, bài viết, hoặc các tài liệu học tập khác.</li></ul></li></ul><h4 id="vi-du" tabindex="-1">Ví dụ: <a class="header-anchor" href="#vi-du" aria-label="Permalink to &quot;Ví dụ:&quot;">​</a></h4><ul><li>Bạn ra quán coffee ngồi học thông qua E-learning platform như Coursera, Udemy, hoặc chính LMS của trường.</li></ul><h4 id="loi-ich-1" tabindex="-1">Lợi ích: <a class="header-anchor" href="#loi-ich-1" aria-label="Permalink to &quot;Lợi ích:&quot;">​</a></h4><ul><li>Linh hoạt khi học tập, có thể học bất cứ đầu và bất cứ lúc nào.</li><li>Có thể học theo tốc độ của riêng mình, không bị áp lực về thời gian.</li><li>Có thể truy cập lại tài liệu học tập bất cứ lúc nào để ôn tập hoặc xem lại.</li><li>Không cần phải tham gia vào các buổi học trực tuyến theo thời gian thực, giúp tiết kiệm thời gian và công sức.</li><li>Có thể học tập ở bất cứ đâu, chỉ cần có kết nối internet.</li></ul><h2 id="hoc-tap-ket-hop-blended-learning" tabindex="-1">Học tập kết hợp (Blended Learning) <a class="header-anchor" href="#hoc-tap-ket-hop-blended-learning" aria-label="Permalink to &quot;Học tập kết hợp (Blended Learning)&quot;">​</a></h2><ul><li><strong>Đặc điểm</strong>: <ul><li>Kết hợp giữa học trực tuyến và học truyền thống.</li><li>Cho phép linh hoạt trong việc học tập.</li><li>Có thể học trực tuyến và tham gia các buổi học trực tiếp tại lớp học. Sau đó, có thể truy cập lại tài liệu học tập trực tuyến để ôn tập hoặc xem lại.</li></ul></li></ul><h3 id="loi-ich-2" tabindex="-1">Lợi ích: <a class="header-anchor" href="#loi-ich-2" aria-label="Permalink to &quot;Lợi ích:&quot;">​</a></h3><ul><li>Kết hợp giữa lợi ích của học trực tuyến và học truyền thống.</li><li>Cho phép linh hoạt trong việc học tập, có thể học trực tuyến hoặc tham gia các buổi học trực tiếp tại lớp học.</li><li>Giúp duy trì động lực học tập tốt hơn là học một mình, vì có thể tương tác với giảng viên và bạn học trong các buổi học trực tiếp.</li><li>Giúp giải quyết các vấn đề khó khăn ngay lập tức trong các buổi học trực tiếp, có thể hỏi giảng viên hoặc bạn học ngay lập tức khi gặp khó khăn trong quá trình học tập.</li></ul><h2 id="cac-ky-nang-can-thiet-đe-hoc-e-learning" tabindex="-1">Các kỹ năng cần thiết để học E-learning <a class="header-anchor" href="#cac-ky-nang-can-thiet-đe-hoc-e-learning" aria-label="Permalink to &quot;Các kỹ năng cần thiết để học E-learning&quot;">​</a></h2><h3 id="ky-nang-su-dung-cong-nghe" tabindex="-1">Kỹ năng sử dụng công nghệ <a class="header-anchor" href="#ky-nang-su-dung-cong-nghe" aria-label="Permalink to &quot;Kỹ năng sử dụng công nghệ&quot;">​</a></h3><ul><li><strong>Truy cập internet</strong>: Biết cách sử dụng các thiết bị điện tử như máy tính, laptop, hoặc điện thoại thông minh để truy cập internet.</li><li><strong>Sử dụng các công cụ trực tuyến</strong>: Biết cách sử dụng các công cụ trực tuyến như video call, chat, và các nền tảng học tập trực tuyến.</li><li><strong>Tìm kiếm thông tin</strong>: Biết cách tìm kiếm thông tin trên internet, kho dữ liệu của trừng, sử dụng các công cụ tìm kiếm như Google, và đánh giá độ tin cậy của các nguồn thông tin.</li><li><strong>Giao tiếp trực tuyến</strong>: Biết cách giao tiếp trực tuyến qua email, chat, và các công cụ trực tuyến khác.</li><li><strong>Cộng tác trực tuyến</strong>: Biết cách làm việc nhóm trực tuyến, sử dụng các công cụ như Google Docs, Trello, hoặc Slack để cộng tác với người khác.</li><li><strong>Quản lý thời gian</strong>: Biết cách quản lý thời gian học tập, đặt lịch học, và theo dõi tiến độ học tập của bản thân.</li></ul><h3 id="ky-nang-tu-hoc" tabindex="-1">Kỹ năng tự học <a class="header-anchor" href="#ky-nang-tu-hoc" aria-label="Permalink to &quot;Kỹ năng tự học&quot;">​</a></h3><ul><li><strong>THói quen học tập</strong>: Biết tự học, tự nghiên cứu và tự đánh giá kết quả học tập của mình.</li><li><strong>Tự động viên</strong>: Biết cách tự động viên bản thân để duy trì động lực học tập, đặc biệt là khi học trực tuyến một mình.</li><li><strong>Tính tự giác</strong>: Tự giác trong việc hoàn thành bài tập, tham gia các buổi học trực tuyến, và theo dõi tiến độ học tập của bản thân.</li><li><strong>Độc lập</strong>: Biết cách làm việc độc lập, tự giải quyết các vấn đề khó khăn trong quá trình học tập mà không cần sự giúp đỡ của người khác.</li></ul>',33)];const g=n(l,[["render",function(n,c,h,l,g,e){return i(),t("div",null,a)}]]);export{h as __pageData,g as default};
