import{_ as n,j as a,o as h,v as t}from"./chunks/framework.9xwLhOZU.js";const i=JSON.parse('{"title":"What is Apache Cassandra?","description":"","frontmatter":{"footer":true,"title":"What is Apache Cassandra?","authors":["lethanh"],"date":"2025-03-26T00:00:00.000Z","outline":"deep","image":"https://static-cdn.thanhlv.com/study/thanhlv-study-2025/2025-03-26-what-is-apache-cassandra/cassandra-la-gi-1.webp","draft":false,"group":"1. Cassandra"},"headers":[{"level":2,"title":"Các đặc điểm chính của Apache Cassandra","slug":"cac-đac-điem-chinh-cua-apache-cassandra","link":"#cac-đac-điem-chinh-cua-apache-cassandra","children":[]},{"level":2,"title":"Cảm hứng của Cassandra","slug":"cam-hung-cua-cassandra","link":"#cam-hung-cua-cassandra","children":[{"level":3,"title":"Google BigTable","slug":"google-bigtable","link":"#google-bigtable","children":[]},{"level":3,"title":"Amazon Dynamo","slug":"amazon-dynamo","link":"#amazon-dynamo","children":[]}]},{"level":2,"title":"Kết hợp sức mạnh của BigTable và Dynamo","slug":"ket-hop-suc-manh-cua-bigtable-va-dynamo","link":"#ket-hop-suc-manh-cua-bigtable-va-dynamo","children":[]},{"level":2,"title":"Kiến trúc NoSQL: So sánh với cơ sở dữ liệu quan hệ","slug":"kien-truc-nosql-so-sanh-voi-co-so-du-lieu-quan-he","link":"#kien-truc-nosql-so-sanh-voi-co-so-du-lieu-quan-he","children":[]},{"level":2,"title":"Điểm hạn chế của Cassandra","slug":"điem-han-che-cua-cassandra","link":"#điem-han-che-cua-cassandra","children":[]},{"level":2,"title":"Kết luận","slug":"ket-luan","link":"#ket-luan","children":[]},{"level":2,"title":"Tài liệu tham khảo","slug":"tai-lieu-tham-khao","link":"#tai-lieu-tham-khao","children":[]}],"relativePath":"study/thanhlv-study-2025/2025-03-26-what-is-apache-cassandra.md","filePath":"study/thanhlv-study-2025/2025-03-26-what-is-apache-cassandra.md","lastUpdated":1748187835000}'),c={name:"study/thanhlv-study-2025/2025-03-26-what-is-apache-cassandra.md"},l=[t('<nav class="table-of-contents"><ul><li><a href="#cac-đac-điem-chinh-cua-apache-cassandra">Các đặc điểm chính của Apache Cassandra</a></li><li><a href="#cam-hung-cua-cassandra">Cảm hứng của Cassandra</a><ul><li><a href="#google-bigtable">Google BigTable</a></li><li><a href="#amazon-dynamo">Amazon Dynamo</a></li></ul></li><li><a href="#ket-hop-suc-manh-cua-bigtable-va-dynamo">Kết hợp sức mạnh của BigTable và Dynamo</a></li><li><a href="#kien-truc-nosql-so-sanh-voi-co-so-du-lieu-quan-he">Kiến trúc NoSQL: So sánh với cơ sở dữ liệu quan hệ</a></li><li><a href="#điem-han-che-cua-cassandra">Điểm hạn chế của Cassandra</a></li><li><a href="#ket-luan">Kết luận</a></li><li><a href="#tai-lieu-tham-khao">Tài liệu tham khảo</a></li></ul></nav><h1 id="what-is-apache-cassandra" tabindex="-1">What is Apache Cassandra? <a class="header-anchor" href="#what-is-apache-cassandra" aria-label="Permalink to &quot;What is Apache Cassandra?&quot;">​</a></h1><p>Apache Cassandra là một Open source NoSQl Distributed database được nhiều công ty tin tưởng về khả năng mở rộng và tính khả dụng mà không ảnh hưởng đến hiệu suất.</p><p>Cassandra được phát triển ban đầu bởi Facebook [1], sau đó trở thành một mã nguồn mở và được Apache Software Foundation quản lý[1]. <img src="https://static-cdn.thanhlv.com/study/thanhlv-study-2025/2025-03-26-what-is-apache-cassandra/cassandra-la-gi-1.webp" alt="Image"></p><h2 id="cac-đac-điem-chinh-cua-apache-cassandra" tabindex="-1">Các đặc điểm chính của Apache Cassandra <a class="header-anchor" href="#cac-đac-điem-chinh-cua-apache-cassandra" aria-label="Permalink to &quot;Các đặc điểm chính của Apache Cassandra&quot;">​</a></h2><ol><li><strong>Kiển trúc phân tán</strong></li></ol><p>Không giống như một số loại database có kiến trúc master-slave, Cassandra có kiến trúc phân tán, mỗi node trong Cassandra có thể ghi và đọc dữ liệu. Không có máy chủ trung tâm nào quản lý tất cả các node và dữ liệu.</p><p>Tất cả các node trong cluster đều bình đẳng và chia sẻ dữ liệu với nhau.</p><ol start="2"><li><strong>Khả năng mở rộng tuyến tính(Linear Scalability)</strong></li></ol><p>Khi cần mở rộng, chúng ta có thể tạo mới node và join vào cluster mà không ảnh hưởng đến hiệu suất của hệ thống. Hiệu suất của hệ thống tăng tuyến tính với số lượng node.</p><ol start="3"><li><p><strong>Mô hình dữ liệu Key-Value/Column Family</strong></p><p>Đây là một mô hình dữ liệu của Google BigTable, một cách lưu trữ dữ liệu phân cấp, mỗi key có thể chứa nhiều cột và mỗi cột có thể chứa nhiều giá trị.</p></li><li><p><strong>Replication và Fault Tolerance</strong></p></li></ol><p>Cassandra sử dụng Replication để đảm bảo dữ liệu không bị mất khi một node bị lỗi. Số lượng replica có thể được cấu hình bởi config Replication Factor và Cassandra sẽ tự động đồng bộ dữ liệu giữa các node.</p><ol start="5"><li><strong>Ngôn ngữ truy vấn CQL(Cassandra Query Language)</strong></li></ol><p>Casandra sử dụng CQL để truy vấn dữ liệu, CQL cực giống với SQL nên việc học và sử dụng Cassandra khá dễ dàng.</p><ol start="6"><li><p><strong>Khả năng chịu lỗi và khả dụng cao</strong></p><p>Nhờ cơ chế Replication và kiến trúc phân tán, Cassandra có khả năng chịu lỗi và khả dụng cao. Đảm bảo dữ liệu không bị mất và hệ thống luôn hoạt động khi có một số node bị lỗi.</p></li><li><p><strong>Hỗ trợ cho các loại dữ liệu lớn(Big data)</strong></p></li></ol><p>Casandra được thiết kế để xử lý lượng dữ liệu lớn, phân tán trên nhiều node. Cassandra thường được sử dụng cùng với các hệ thống Big Data như Hadoop, Spark, Kafka, ... để phân tích dữ liệu lớn.</p><h2 id="cam-hung-cua-cassandra" tabindex="-1">Cảm hứng của Cassandra <a class="header-anchor" href="#cam-hung-cua-cassandra" aria-label="Permalink to &quot;Cảm hứng của Cassandra&quot;">​</a></h2><p>Thực tế Cassandra được lấy cảm hứng từ 2 hệ thống lớn là Google BigTable và Amazon Dynamo.</p><h3 id="google-bigtable" tabindex="-1"><strong>Google BigTable</strong> <a class="header-anchor" href="#google-bigtable" aria-label="Permalink to &quot;**Google BigTable**&quot;">​</a></h3><p>Cassandra lấy cảm hứng từ Google BigTable về mô hình dữ liệu Key-Value/Column Family, một cách lưu trữ dữ liệu phân cấp, mỗi key có thể chứa nhiều cột và mỗi cột có thể chứa nhiều giá trị.</p><p>Cassandra sử dụng cơ chế tối ưu hóa cho query theo Primary Key. Cassandra sử dụng một số cơ chế như Partition Key, Clustering Key, Composite Key để tối ưu hóa query.</p><h3 id="amazon-dynamo" tabindex="-1"><strong>Amazon Dynamo</strong> <a class="header-anchor" href="#amazon-dynamo" aria-label="Permalink to &quot;**Amazon Dynamo**&quot;">​</a></h3><p>Cassandra lấy cảm hứng từ Amazon Dynamo về kiến trúc phân tán, mỗi node trong Cassandra có thể ghi và đọc dữ liệu.</p><ol><li>Kiến trúc phi tập trung (Decentralized Architecture):</li></ol><ul><li>Không có máy chủ trung tâm nào quản lý tất cả các node và dữ liệu. Cassandra sử dụng mô hình Peer-to-Peer để giao tiếp giữa các node. Mọi node đều bình đẳng và chia sẻ dữ liệu với nhau.</li><li>Điều này giúp loại bỏ điểm lỗi duy nhất (Single Point of Failure) và tăng khả năng khả dụng của hệ thống.</li></ul><ol start="2"><li>Khả năng mở rộng tuyến tính (Linear Scalability):</li></ol><ul><li>Khi cần mở rộng, chúng ta có thể tạo mới node và join vào cluster mà không ảnh hưởng đến hiệu suất của hệ thống.</li></ul><ol start="3"><li>Consistent Hashing:</li></ol><ul><li>Cassandra sử dụng Consistent Hashing để phân chia dữ liệu giữa các node. Consistent Hashing giúp tối ưu hóa việc phân chia dữ liệu giữa các node và giảm số lượng dữ liệu cần phải di chuyển khi thêm hoặc xóa node.</li><li>Mỗi node trong Cassandra sẽ chịu trách nghiệm lưu trữ một phần của dữ liệu trong không gian Hash Ring.</li></ul><ol start="4"><li>Replication và Quorum:</li></ol><ul><li>Cassandra hỗ trợ cơ chế sao chép dữ liệu (Replication) để đảm bảo dữ liệu không bị mất khi một node bị lỗi tương tự như Dynamo.. <ul><li>Số lượng bản sao của dữ liệu được cấu hình bởi Replication Factor và Consistency Level.</li></ul></li><li>Cassandra sử dụng cơ chế Quorum để đảm bảo đồng nhất dữ liệu giữa các node. Đảm bảo tính cân bằng giữa tính sẵn sàng (availability) và tính nhất quán (consistency).</li></ul><h2 id="ket-hop-suc-manh-cua-bigtable-va-dynamo" tabindex="-1">Kết hợp sức mạnh của BigTable và Dynamo <a class="header-anchor" href="#ket-hop-suc-manh-cua-bigtable-va-dynamo" aria-label="Permalink to &quot;Kết hợp sức mạnh của BigTable và Dynamo&quot;">​</a></h2><p>Cassandra tận dụng:</p><ul><li>Mô hình dữ liệu mạnh mẽ của Bigtable, giúp tổ chức dữ liệu hiệu quả và dễ truy vấn.</li><li>Kiến trúc phân tán của Dynamo, giúp đạt được tính khả dụng cao, khả năng mở rộng vượt trội và độ bền bỉ với lỗi hệ thống.</li><li>Nhờ sự kết hợp này, Cassandra trở thành một hệ quản trị cơ sở dữ liệu lý tưởng cho các ứng dụng đòi hỏi khả năng lưu trữ dữ liệu lớn, truy cập nhanh, và luôn sẵn sàng.</li></ul><h2 id="kien-truc-nosql-so-sanh-voi-co-so-du-lieu-quan-he" tabindex="-1">Kiến trúc NoSQL: So sánh với cơ sở dữ liệu quan hệ <a class="header-anchor" href="#kien-truc-nosql-so-sanh-voi-co-so-du-lieu-quan-he" aria-label="Permalink to &quot;Kiến trúc NoSQL: So sánh với cơ sở dữ liệu quan hệ&quot;">​</a></h2><table><thead><tr><th>Đặc điểm</th><th>Cassandra(NoSQL)</th><th>Cơ sở dữ liệu quan hệ (RDBMS)</th></tr></thead><tbody><tr><td>Cách lưu dữ liệu</td><td>Theo mô hình Key Value hoặc Column Family</td><td>Mô hình bảng (Table base)</td></tr><tr><td>Khả năgng mở rộng</td><td>Mở rộng ngang(horizontal scaling), thêm node để mở rộng</td><td>Mở rộng dọc (vertical scaling), cần tăng cấu hình máy chủ</td></tr><tr><td>Kiểu dữ liệu</td><td>Không có schema cố định (Schema-less)</td><td>Schema cố định với table, column và row</td></tr></tbody></table><h2 id="điem-han-che-cua-cassandra" tabindex="-1">Điểm hạn chế của Cassandra <a class="header-anchor" href="#điem-han-che-cua-cassandra" aria-label="Permalink to &quot;Điểm hạn chế của Cassandra&quot;">​</a></h2><p>Apache Cassandra là một hệ thống quản lý cơ sở dữ liệu phân tán được thiết kế để xử lý lượng lớn dữ liệu trên nhiều máy chủ, đảm bảo không có điểm lỗi đơn và cung cấp khả năng mở rộng cao.</p><p>Tuy nhiên, như mọi công nghệ, Cassandra cũng có những hạn chế nhất định mà bạn cần lưu ý khi đánh giá việc sử dụng nó cho các dự án của mình:</p><ul><li><strong>Độ chễ ghi cao:</strong> Cassandra sử dụng mô hình ghi cuối cùng (last write wins) để giải quyết xung đột dữ liệu. Điều này có thể dẫn đến việc mất dữ liệu nếu xung đột xảy ra. Khi cả 2 cùng khi. Để đảm bảo tính nhất quán, Cassandra cần phải ghi vào nhiều nde trước khi hoàn thành một transaction. Điều này có thể tạo ra độ chễ ghi cao trong một số trường hợp. (Đọc phần tính nhất quán sẽ hiểu lý do)</li><li><strong>Tính nhất quán:</strong> Consistency trong Cassandra có thể được cấu hình theo mức độ từ <code>ONE</code> đến <code>ALL</code>. Tuy nhiên, việc cấu hình Consistency Level cao có thể ảnh hưởng đến hiệu suất của hệ thống. <ul><li>ONE: Đảm bảo dữ liệu được ghi vào ít nhất một node.(Tốc độ nhanh nhất. 1)</li><li>QUORUM: Đảm bảo dữ liệu được ghi vào hơn nửa số node. Tức là 51% số node(Tổng các Node của tất cả datacenter). (Tốc độ chậm)</li><li>Local_QUORUM: Đảm bảo dữ liệu được ghi vào hơn nửa số node trong một datacenter. (Tốc độ trung bình)</li><li>ALL: Mức độ cao nhất, đảm bảo dữ liệu được ghi vào tất cả các node trên toàn hệ thống thì transaction đó mới được coi là hoàn thành. (Tốc độ rất chậm)</li></ul></li><li><strong>Không hỗ trợ các truy vấn phức tạp</strong>: Cassandra không hỗ trợ các truy vấn phức tạp như các hệ thống quan hệ(Join, subquery, having group). Nếu ứng dụng của bạn đòi hỏi các truy vấn phức tạp, Cassandra có thể không phải là lựa chọn tốt nhất.</li><li><strong>Quản lý schema</strong>: Cassandra hỗ trợ cơ chế Schema-on-Read, điều này có nghĩa là bạn có thể lưu trữ dữ liệu mà không cần định nghĩa schema trước. Tuy nhiên, việc quản lý schema có thể trở nên phức tạp khi dữ liệu phát triển và thay đổi. <ul><li>Nhất là thay đổi, việc thêm hoặc sửa một cloumn có thể yêu cầu cập nhât lớn đến tất cả các node vì vậy ảnh hưởng đến hiệu suất.</li></ul></li><li>Độ phức tạp trng quản lý và bảo trì: Cassandra là một hệ thống phân tán, việc quản lý và bảo trì Cassandra có thể phức tạp hơn so với các hệ thống cơ sở dữ liệu quan hệ truyền thống. <ul><li>Việc cân bằng tải, đảm bảo tính sẵn sàng cao đòi hỏi hiểu biết đủ sâu về Cassandra và kiến thức về hệ thống phân tán.</li></ul></li></ul><h2 id="ket-luan" tabindex="-1">Kết luận <a class="header-anchor" href="#ket-luan" aria-label="Permalink to &quot;Kết luận&quot;">​</a></h2><ul><li>Apache Cassandra là một hệ quản trị cơ sở dữ liệu phân tán, mở rộng ngang, và khả dụng cao.</li><li>Cassandra lấy cảm hứng từ Google BigTable và Amazon Dynamo, kết hợp mô hình dữ liệu mạnh mẽ của BigTable và kiến trúc phân tán của Dynamo.</li><li>Cassandra hỗ trợ mô hình dữ liệu Key-Value/Column Family, khả năng mở rộng tuyến tính, và khả năng chịu lỗi cao.</li><li>Cassandra sử dụng ngôn ngữ truy vấn CQL (Cassandra Query Language) giống với SQL, dễ học và sử dụng.</li><li>Cassandra thường được sử dụng trong các ứng dụng đòi hỏi khả năng lưu trữ dữ liệu lớn, truy cập nhanh, và luôn sẵn sàng.</li><li>Cassandra là một lựa chọn lý tưởng cho các ứng dụng Big Data, IoT, và các ứng dụng đòi hỏi khả năng mở rộng và khả dụng cao.</li><li>Lưu ý: Cassandra ưu tiên tính khả dụng (availability) hơn tính nhất quán (consistency), bạn cần cân nhắc khi cấu hình Consistency Level cho ứng dụng của mình. Nếu cấu hình Consistency Level cao, hiệu suất của hệ thống sẽ giảm, nếu cấu hình Consistency Level thấp, có thể dẫn đến mất tính nhất quán.</li></ul><h2 id="tai-lieu-tham-khao" tabindex="-1">Tài liệu tham khảo <a class="header-anchor" href="#tai-lieu-tham-khao" aria-label="Permalink to &quot;Tài liệu tham khảo&quot;">​</a></h2><ol><li><a href="https://en.wikipedia.org/wiki/Apache_Cassandra" target="_blank" rel="noreferrer">https://en.wikipedia.org/wiki/Apache_Cassandra</a></li></ol>',44)];const s=n(c,[["render",function(n,t,i,c,s,e){return h(),a("div",null,l)}]]);export{i as __pageData,s as default};
