import{_ as n,j as t,o as h,v as c}from"./chunks/framework.OuVixYWh.js";const i=JSON.parse('{"title":"Các thành phần chính dev hay tiếp xúc trong kiến trúc Kubernetes.","description":"Các thành phần chính trong kiến trúc Kubernetes bao gồm các thành phần như Node, Pod, Service, Deployment, ConfigMap, Secret, Volume, Namespace và Ingress. Mỗi thành phần đóng vai trò quan trọng trong việc quản lý và triển khai ứng dụng trên Kubernetes.","frontmatter":{"footer":true,"title":"Các thành phần chính dev hay tiếp xúc trong kiến trúc Kubernetes.","authors":["lethanh"],"description":"Các thành phần chính trong kiến trúc Kubernetes bao gồm các thành phần như Node, Pod, Service, Deployment, ConfigMap, Secret, Volume, Namespace và Ingress. Mỗi thành phần đóng vai trò quan trọng trong việc quản lý và triển khai ứng dụng trên Kubernetes.","date":"2025-06-17T00:00:00.000Z","outline":"deep","image":"https://static-cdn.thanhlv.com/study/thanhlv-study-2025/2025-06-16-gioi-thieu-ve-kubernetes/kubernetes2.png","draft":false,"group":"2. Kubernetes"},"headers":[{"level":2,"title":"1. Container:","slug":"_1-container","link":"#_1-container","children":[{"level":3,"title":"Bảng so sánh giữa Container và VM","slug":"bang-so-sanh-giua-container-va-vm","link":"#bang-so-sanh-giua-container-va-vm","children":[]}]},{"level":2,"title":"2. Pod:","slug":"_2-pod","link":"#_2-pod","children":[{"level":3,"title":"Ví dụ điển hình (Sidecar Pattern): Hãy tưởng tượng một \\"trạm nấu ăn\\" (Pod) chứa 2 đầu bếp( Container )","slug":"vi-du-đien-hinh-sidecar-pattern-hay-tuong-tuong-mot-tram-nau-an-pod-chua-2-đau-bep-container","link":"#vi-du-đien-hinh-sidecar-pattern-hay-tuong-tuong-mot-tram-nau-an-pod-chua-2-đau-bep-container","children":[]},{"level":3,"title":"Ví dụ thực tế","slug":"vi-du-thuc-te","link":"#vi-du-thuc-te","children":[]}]},{"level":2,"title":"3. Node ( Worker Node )","slug":"_3-node-worker-node","link":"#_3-node-worker-node","children":[]},{"level":2,"title":"4. Control Plane ( Bếp trưởng ), trước đây gọi là Master Node","slug":"_4-control-plane-bep-truong-truoc-đay-goi-la-master-node","link":"#_4-control-plane-bep-truong-truoc-đay-goi-la-master-node","children":[]},{"level":2,"title":"5. Deployment","slug":"_5-deployment","link":"#_5-deployment","children":[]},{"level":2,"title":"6. Service","slug":"_6-service","link":"#_6-service","children":[]},{"level":2,"title":"7. ConfigMap","slug":"_7-configmap","link":"#_7-configmap","children":[]},{"level":2,"title":"8. Secret","slug":"_8-secret","link":"#_8-secret","children":[]},{"level":2,"title":"9. Volume","slug":"_9-volume","link":"#_9-volume","children":[]}],"relativePath":"study/thanhlv-study-2025/2025-06-17-cac-thanh-phan-chinh-dev-hay-tiep-xuc-trong-kien-truc-k8s.md","filePath":"study/thanhlv-study-2025/2025-06-17-cac-thanh-phan-chinh-dev-hay-tiep-xuc-trong-kien-truc-k8s.md","lastUpdated":1750698303000}'),o={name:"study/thanhlv-study-2025/2025-06-17-cac-thanh-phan-chinh-dev-hay-tiep-xuc-trong-kien-truc-k8s.md"},e=[c('<p>Khiến trúc của Kubernetes bao gồm nhiều thành phần khác nhau và được xây dựng dựa trên nguyên tắc trừu tượng hía và phân tách trách nghiệm.</p><p>Tuy nhiên, nếu bạn là một developer, bạn sẽ thường xuyên tiếp xúc với một số thành phần chính trong kiến trúc Kubernetes. Bài viết này sẽ giúp bạn hiểu rõ hơn về các thành phần này và cách chúng hoạt động cùng nhau để cung cấp một môi trường triển khai ứng dụng hiệu quả.</p><nav class="table-of-contents"><ul><li><a href="#_1-container">1. Container:</a><ul><li><a href="#bang-so-sanh-giua-container-va-vm">Bảng so sánh giữa Container và VM</a></li></ul></li><li><a href="#_2-pod">2. Pod:</a><ul><li><a href="#vi-du-đien-hinh-sidecar-pattern-hay-tuong-tuong-mot-tram-nau-an-pod-chua-2-đau-bep-container">Ví dụ điển hình (Sidecar Pattern): Hãy tưởng tượng một &quot;trạm nấu ăn&quot; (Pod) chứa 2 đầu bếp( Container )</a></li><li><a href="#vi-du-thuc-te">Ví dụ thực tế</a></li></ul></li><li><a href="#_3-node-worker-node">3. Node ( Worker Node )</a></li><li><a href="#_4-control-plane-bep-truong-truoc-đay-goi-la-master-node">4. Control Plane ( Bếp trưởng ), trước đây gọi là Master Node</a></li><li><a href="#_5-deployment">5. Deployment</a></li><li><a href="#_6-service">6. Service</a></li><li><a href="#_7-configmap">7. ConfigMap</a></li><li><a href="#_8-secret">8. Secret</a></li><li><a href="#_9-volume">9. Volume</a></li></ul></nav><h1 id="cac-thanh-phan-chinh-dev-hay-tiep-xuc-trong-kien-truc-kubernetes" tabindex="-1">Các thành phần chính dev hay tiếp xúc trong kiến trúc Kubernetes. <a class="header-anchor" href="#cac-thanh-phan-chinh-dev-hay-tiep-xuc-trong-kien-truc-kubernetes" aria-label="Permalink to &quot;Các thành phần chính dev hay tiếp xúc trong kiến trúc Kubernetes.&quot;">​</a></h1><p><strong>4 thành phần cơ bản.</strong></p><h2 id="_1-container" tabindex="-1">1. Container: <a class="header-anchor" href="#_1-container" aria-label="Permalink to &quot;1. Container:&quot;">​</a></h2><p>Mọi thứ trong Kubernetes đều xoay quanh container.</p><ul><li><strong>Định nghĩa</strong>: Container là một đơn vị phần mềm tiêu chuẩn, nhẹ, độc lập, chứa mã nguồn của ứng dụng và tất cả các thư viện, công cụ cần thiết để chạy ứng dụng đó. <ul><li>Container như là một hộp nguyên liệu được đóng gói sẵn, chứa mọi thứ cần thiết để thực hiện công việc của mình mà không cần lo lắng về môi trường bên ngoài.</li><li>Thông thường container sẽ là một instance của một image docker hoặc một số nền tảng container khác như Podman, rkt, ...</li></ul></li><li><strong>So sánh với máy ảo (VM):</strong> Sự khác biệt cốt lõi nằm ở layer ảo hóa, VM ảo hóa cả phần cứng tạo ra một máy tính hoàn toàn với hệ điều hành riêng, trong khi container sẽ ảo hóa hệ điều hành và chia sẻ kernel với hệ điều hành host.</li></ul><h3 id="bang-so-sanh-giua-container-va-vm" tabindex="-1">Bảng so sánh giữa Container và VM <a class="header-anchor" href="#bang-so-sanh-giua-container-va-vm" aria-label="Permalink to &quot;Bảng so sánh giữa Container và VM&quot;">​</a></h3><table><thead><tr><th>Tiêu chí</th><th>Container</th><th>Máy ảo (VM)</th></tr></thead><tbody><tr><td>Ảo hóa</td><td>Ảo hóa hệ điều hành</td><td>Ảo hóa phần cứng</td></tr><tr><td>Kích thước</td><td>Nhẹ, nhanh chóng(Có thể tình bằng MB)</td><td>Nặng, chậm hơn(Tính bằng GB)</td></tr><tr><td>Khởi động</td><td>Nhanh chóng (Vài giây)</td><td>Chậm hơn (Vài phút)</td></tr><tr><td>Hiệu suất</td><td>Hiệu suất cao hơn, ít overhead, gần tương đương với máy chủ vật lý</td><td>Hiệu suất thấp hơn, nhiều overhead vì có một layer hypervisor</td></tr><tr><td>Quản lý tài nguyên</td><td>Chia sẻ OS kernel, sử dụng tài nguyên hiệu quả hơn</td><td>Tài nguyên riêng biệt cho mỗi VM</td></tr><tr><td>Mức độ cô lập</td><td>Cô lập ở mức process, chia sẻ kernel với hệ điều hành host</td><td>Cô lập ở mức phần cứng, mỗi VM có hệ điều hành riêng</td></tr></tbody></table><h2 id="_2-pod" tabindex="-1">2. Pod: <a class="header-anchor" href="#_2-pod" aria-label="Permalink to &quot;2. Pod:&quot;">​</a></h2><p>Pod là đơn vị cơ bản nhất trong Kubernetes, đại diện cho một hoặc nhiều container chạy trên cùng một node. Mỗi Pod có thể chứa một hoặc nhiều container, và các container trong cùng một Pod chia sẻ cùng một địa chỉ IP, không gian mạng và không gian lưu trữ.</p><ul><li><strong>Định nghĩa</strong>: Là đơn bị nhỏ nhất trong Kubernetes, đại diện cho một hoặc nhiều container chạy trên cùng một node. <ul><li><strong>Mạng ( Network ):</strong> Tất cả các container trong cùng một Pod sẽ chia sẻ cùng một địa chỉ IP và không gian mạng, điều này giúp chúng có thể giao tiếp với nhau một cách dễ dàng. Chúng có thể gọi trực tiếp đến nhau thông qua localhost, giống hệt như các đầu bếp chân phở và nấu nứ có thể nói chuyện trực tiếp với nhau.</li><li><strong>Lưu trữ ( Storage ):</strong> Các container trong cùng một Pod có thể chia sẻ các volume, cho phép chúng truy cập vào cùng một dữ liệu. Điều này giống như việc các đầu bếp có thể sử dụng chung một tủ lạnh để lưu trữ nguyên liệu.</li></ul></li><li><strong>Tại sao cần pod ?:</strong> Đây là một câu hỏi rất quan trọng, thay vì chỉ chạy các container riêng lẻ, Kubernetes sử dụng Pod để nhóm các container có liên quan với nhau. Điều này giúp quản lý và triển khai ứng dụng dễ dàng hơn. <ul><li><strong>Đơn giản hóa việc giao tiếp và chia sẻ dữ liệu</strong><ul><li>Pod sẽ chứa các contianer có liên kết chặt chẽ với nhau, thường là các container cần giao tiếp với nhau hoặc chia sẻ tài nguyên.</li></ul></li><li><strong>Quản lý như một đơn vị thống nhất</strong><ul><li>Pod đóng vai trò là đơn vị để lập lịch (scheduling), mở rộng (scaling) và quản lý vòng đời. Kubernetes sẽ di chuyển, nhân bản hoặc dừng cả Pod (bao gồm tất cả các container bên trong) chứ không phải từng container riêng lẻ. Điều này đảm bảo rằng các container phụ thuộc lẫn nhau luôn được đặt trên cùng một máy (Node) và được quản lý đồng bộ</li></ul></li><li><strong>Mô hình triển khai linh hoạt</strong><ul><li>Mặc dù Pod có thể chứa nhiều container, nhưng đa số một pod chạy một container duy nhất. Ngay cả trong trường hợp này, Pod vẫn đóng vai trò là một lớp trừu tượng (abstraction layer), cho phép Kubernetes quản lý nó một cách nhất quán. Khi cần mở rộng ứng dụng, bạn chỉ cần tạo thêm các bản sao của Pod đó.</li></ul></li></ul></li></ul><h3 id="vi-du-đien-hinh-sidecar-pattern-hay-tuong-tuong-mot-tram-nau-an-pod-chua-2-đau-bep-container" tabindex="-1">Ví dụ điển hình (Sidecar Pattern): Hãy tưởng tượng một &quot;trạm nấu ăn&quot; (Pod) chứa 2 đầu bếp( Container ) <a class="header-anchor" href="#vi-du-đien-hinh-sidecar-pattern-hay-tuong-tuong-mot-tram-nau-an-pod-chua-2-đau-bep-container" aria-label="Permalink to &quot;Ví dụ điển hình (Sidecar Pattern): Hãy tưởng tượng một &quot;trạm nấu ăn&quot; (Pod) chứa 2 đầu bếp( Container )&quot;">​</a></h3><ol><li><strong>Đầu bếp chính</strong>: Chịu trách nhiệm nấu phở (Chạy webserver)</li><li><strong>Đầu bếp phụ</strong>(Sidecar): Chuyên dọn dẹp và ghi chép lại những gì đầu bếp chính đã dùng (thu thập log). Đầu bếp phụ này sẽ xem các &quot;ghi chép&quot; của đầu bếp chính (thông qua một Volume được chia sẻ) và báo cáo lại cho bếp trưởng. Cả hai đầu bếp này sống và chết cùng nhau như một thể thống nhất.</li></ol><p>Mình sẽ có 1 bài viết các k8s tạo pod như nào để chia sẻ cùng network và volume và có một demo sử dụng với docker. Mình sẽ làm nó sau.</p><h3 id="vi-du-thuc-te" tabindex="-1">Ví dụ thực tế <a class="header-anchor" href="#vi-du-thuc-te" aria-label="Permalink to &quot;Ví dụ thực tế&quot;">​</a></h3><p>Một ứng dụng tích hợp SSO của google.</p><ol><li><strong>Container chính chứa các logic nghiệp vụ của chúng ta</strong>: <ul><li>Chạy ứng dụng web, xử lý các yêu cầu từ người dùng.</li><li>Ví dụ: Một ứng dụng web sử dụng Flask hoặc Node.js để xử lý các yêu cầu HTTP.</li></ul></li><li><strong>Container phụ (Sidecar)</strong>: Sử dụng oauth2-proxy <ul><li>Chạy oauth2-proxy để xử lý xác thực người dùng thông qua Google SSO.</li><li>Container này sẽ giao tiếp với google và người dùng để xác nhận người dùng và lấy danh tinh người dùng.</li><li>Sau khi xác thực, nó sẽ chuyển tiếp yêu cầu đến container chính để xử lý yêu cầu.</li></ul></li></ol><h2 id="_3-node-worker-node" tabindex="-1">3. Node ( Worker Node ) <a class="header-anchor" href="#_3-node-worker-node" aria-label="Permalink to &quot;3. Node ( Worker Node )&quot;">​</a></h2><p>Nếu Pod là &quot;trạm nấu ăn&quot;, thì Node là &quot;nhà bếp&quot; nơi trạm nấu ăn được đặt.</p><ul><li><strong>Định nghĩa</strong>: Node là một máy trạm trong cụm k8s, nó thường là một máy chủ vật lý hoặc máy ảo. Đây là nơi các pod thực sự chạy.</li><li><strong>Chức năng</strong>: Node cung cấp tài nguyên tính toán (CPU, RAM, Disk) cho các Pod. Mỗi Node có thể chạy nhiều Pod, và Kubernetes sẽ quản lý việc phân phối Pod trên các Node để đảm bảo hiệu suất và khả năng mở rộng.</li><li><strong>Các thành phần chính trên mỗi Node</strong>: Mỗi &quot;Nhà bếp&quot; (Node) sẽ cần có một số &quot;nhân viên hậu cần&quot; (Kubelet, Kube-proxy, Container Runtime) để đảm bảo mọi thứ hoạt động trơn tru. <ul><li>kubelet: Đây là &quot;Quản lý bếp&quot; (Agent) của k8s trên mỗi Node, nó giao tiếp với bếp trưởng (Kubernetes Control Plane) để nhận lệnh, báo cáo trạng thái của các Pod đang chạy trên Node và đảm bảo rằng các đầu bếp (container) đang làm việc và khỏe mạnh.</li><li>kube-proxy: Đây là &quot;Bộ phận phân phối nguyên liệu&quot; (Network Proxy) của k8s trên mỗi Node, nó đảm bảo rằng các đầu bếp (container) có thể giao tiếp với nhau và với thế giới bên ngoài thông qua mạng.</li><li>Container Runtime: Đây là phần chịu trách nghiệm thực sự chạy các container trên Node. Nó có thể là Docker, containerd, hoặc một runtime khác.</li><li><strong>Các thành phần khác</strong>: Mỗi Node cũng có thể có các thành phần khác như: <ul><li><strong>CNI (Container Network Interface)</strong>: Để quản lý mạng cho các Pod.</li><li><strong>Volume Plugins</strong>: Để quản lý lưu trữ cho các Pod.</li><li><strong>Metrics Server</strong>: Để thu thập và cung cấp thông tin về hiệu suất của Node và Pod.</li><li><strong>Logging Agent</strong>: Để thu thập và gửi log từ các container đến hệ thống log tập trung.</li><li><strong>Node Problem Detector</strong>: Để phát hiện và báo cáo các vấn đề về phần cứng hoặc phần mềm trên Node.</li><li><strong>Node Affinity</strong>: Để xác định các Pod nào có thể chạy trên Node này dựa trên các thuộc tính của Node (ví dụ: loại máy chủ, vùng địa lý, ...).</li><li><strong>Node Taints and Tolerations</strong>: Để kiểm soát việc phân phối Pod trên các Node, cho phép một số Pod chỉ chạy trên các Node nhất định hoặc tránh chạy trên các Node có vấn đề.</li><li>....</li></ul></li></ul></li></ul><h2 id="_4-control-plane-bep-truong-truoc-đay-goi-la-master-node" tabindex="-1">4. Control Plane ( Bếp trưởng ), trước đây gọi là Master Node <a class="header-anchor" href="#_4-control-plane-bep-truong-truoc-đay-goi-la-master-node" aria-label="Permalink to &quot;4. Control Plane ( Bếp trưởng ), trước đây gọi là Master Node&quot;">​</a></h2><p>Dây là &quot;bộ não&quot; của Kubernetes, nơi quản lý và điều phối các Node và Pod và mọi thứ khác trong cụm Kubernetes.</p><ul><li><strong>Định nghĩa</strong>: Control Plane là tập hợp các thành phần chịu trách nhiệm quản lý toàn bộ trạng thái của cluster Kubernetes. <ul><li>Nó đưa ra các quyết định về việc triển khai, mở rộng, và quản lý vòng đời của các Pod và Node.</li><li>Nó cũng đảm bảo rằng trạng thái thực tế của cluster luôn đồng bộ với trạng thái mong muốn được định nghĩa trong các cấu hình.</li><li>Để đảm bảo tính sẵn sàng cao, Control Plane có thể được triển khai trên nhiều máy chủ (Master Node) và sử dụng cơ chế đồng bộ hóa để đảm bảo rằng tất cả các thành phần đều hoạt động nhất quán.</li><li><strong>Các thành phần chính của Control Plane</strong>: <ul><li><strong>Kube-apiserver</strong>: Đây là interface chính để tương tác với Kubernetes. Nó cung cấp API RESTful để quản lý các tài nguyên trong cluster. Nó giống như thư ký của bếp trưởng, tiếp nhận các yêu cầu, kiểm tra tính hợp lệ và gửi lên.</li><li><strong>etcd</strong>: Là cơ sở dữ liệu phân tán có dạng Key Value, lưu trữ tất cả các cấu hình và trạng thái của cluster. Nó đảm bảo tính nhất quán và độ bền của dữ liệu.</li><li><strong>Kube-scheduler</strong>: Nghiệm vụ duy nhất của nó là quyết định Node nào sẽ chạy Pod mới. Nó xem xét các yếu tố như tài nguyên, affinity, anti-affinity, và các ràng buộc khác để đưa ra quyết định tốt nhất.</li><li><strong>Kube-controller-manager</strong>: Chạy các controller để theo dõi trạng thái của cluster và thực hiện các hành động cần thiết để duy trì trạng thái mong muốn. <ul><li>Mỗi controller sẽ theo dõi một loại tài nguyên cụ thể (ví dụ: Node, Pod, Deployment) và thực hiện các hành động cần thiết để đảm bảo rằng trạng thái thực tế của tài nguyên đó luôn đồng bộ với trạng thái mong muốn.</li><li><strong>Ví dụ</strong>: Replication Controller đảm bảo rằng số lượng đầu bếp chần phở luôn đúng như yêu cầu, nếu một người nghỉ, nó sẽ ngay lập tức tìm người thay thế.</li></ul></li></ul></li></ul></li></ul><p><strong>Các đối tượng quản lý bậc cao</strong>:</p><p>Thực tế, dev hiếm khi làm việc trực tiếp với pdo trên k8s. Thay vào đó sẽ sử dụng các dối tượng quản lý bậc cao hơn để quản lý.</p><h2 id="_5-deployment" tabindex="-1">5. Deployment <a class="header-anchor" href="#_5-deployment" aria-label="Permalink to &quot;5. Deployment&quot;">​</a></h2><p>Một đối tượng sử dụng nhiều và thường xuyên nhất trong k8s.</p><ul><li><strong>Định nghĩa</strong>: Deployment là một đối tượng quản lý bậc cao trong Kubernetes, cho phép bạn định nghĩa và quản lý các ứng dụng stateless ( không) trạng thái). <ul><li>Ví dụ như các web server, API server, ... Deployment sẽ tự động quản lý việc tạo, cập nhật và xóa các Pod để đảm bảo rằng số lượng Pod đang chạy luôn đúng như mong muốn.</li></ul></li><li><strong>Vai trò</strong>: <ul><li>Bạn không cần phải tạo từng Pod một cách thủ công, thay vào đó, bạn chỉ cần định nghĩa Deployment và khai báo &quot;Tôi muốn chạy ứng dụng A, sử dụng container B và tôi cần 3 bản sao (replicas)&quot;</li><li>Khi bạn muốn cập nhật ứng dụng lên phiên bản mới, bạn chỉ cần thay đổi image file trong Deployment. Sau đó thực hiện rolling update, Kubernetes sẽ tự động tạo các Pod mới với phiên bản mới và dừng các Pod cũ một cách an toàn.</li></ul></li></ul><h2 id="_6-service" tabindex="-1">6. Service <a class="header-anchor" href="#_6-service" aria-label="Permalink to &quot;6. Service&quot;">​</a></h2><p>Nếu Deployment trả lời cho câu hỏi &quot;Chạy cái gì ? Bao nhiêu bản?&quot;, thì Service trả lời cho câu hỏi &quot;Làm thế nào để truy cập vào ứng dụng đó?&quot;.</p><ul><li><strong>Định nghĩa</strong>: Service là một đối tượng trong Kubernetes, cung cấp một điểm truy cập ổn định cho các Pod. Nó cho phép bạn truy cập vào các Pod thông qua một địa chỉ IP và cổng cố định, bất kể Pod có thể được di chuyển hoặc thay thế. <ul><li>Ví dụ: Nếu bạn có một ứng dụng web chạy trên nhiều Pod, bạn có thể tạo một Service để truy cập vào tất cả các Pod đó thông qua một địa chỉ IP duy nhất.</li></ul></li><li><strong>Vai trò</strong>: <ul><li>Service giúp giải quyết vấn đề về địa chỉ IP của Pod có thể thay đổi khi chúng được tạo mới hoặc di chuyển. Khi bạn tạo một Service, Kubernetes sẽ cung cấp một địa chỉ IP ổn định và một tên DNS cho Service đó.</li><li>Điều này cho phép các ứng dụng khác trong cluster hoặc người dùng cuối có thể truy cập vào ứng dụng của bạn mà không cần phải biết địa chỉ IP cụ thể của từng Pod.</li><li>Service cũng hỗ trợ cân bằng tải (load balancing) giữa các Pod, đảm bảo rằng lưu lượng truy cập được phân phối đều giữa các Pod đang chạy ứng dụng.</li></ul></li></ul><p>Service giải quyết vấn đề này bằng cách:</p><ul><li>Cung cấp một địa chỉ IP ảo và một tên DNS cố định: Service có một địa chỉ IP ảo (gọi là ClusterIP) và một tên DNS duy nhất trong cụm. Các ứng dụng khác chỉ cần &quot;nói chuyện&quot; với Service này mà không cần quan tâm đến địa chỉ IP của từng Pod riêng lẻ.</li><li>Tự động khám phá Pod (Pod Discovery): Service sử dụng một cơ chế gọi là &quot;selectors&quot; (bộ chọn) dựa trên các &quot;labels&quot; (nhãn) để tự động tìm và nhóm các Pod mà nó cần quản lý. Bất kỳ Pod nào có nhãn khớp với bộ chọn của Service sẽ tự động được đưa vào danh sách đích.</li><li>Cân bằng tải (Load Balancing): Khi có nhiều Pod cùng chạy một ứng dụng, Service sẽ tự động phân phối các yêu cầu mạng đến các Pod đó một cách đồng đều (thường theo thuật toán round-robin). Điều này giúp tăng khả năng chịu tải và tính sẵn sàng cao cho ứng dụng.</li><li><strong>Các loại Service trong Kubernetes</strong>: <ul><li><strong>ClusterIP</strong>: Đây là loại Service mặc định, cung cấp một địa chỉ IP nội bộ trong cluster để các Pod có thể giao tiếp với nhau. Nó không thể truy cập từ bên ngoài cluster.</li><li><strong>NodePort</strong>: Mở một cổng trên mỗi Node trong cluster, cho phép truy cập vào Service từ bên ngoài thông qua địa chỉ IP của Node và cổng đã mở.</li><li><strong>LoadBalancer</strong>: Tạo một Load Balancer bên ngoài (nếu được hỗ trợ bởi nhà cung cấp dịch vụ đám mây) để phân phối lưu lượng truy cập đến Service.</li><li><strong>ExternalName</strong>: Cung cấp một tên DNS bên ngoài cho Service, cho phép bạn ánh xạ tên DNS đến một địa chỉ IP hoặc tên miền bên ngoài.</li></ul></li></ul><h2 id="_7-configmap" tabindex="-1">7. ConfigMap <a class="header-anchor" href="#_7-configmap" aria-label="Permalink to &quot;7. ConfigMap&quot;">​</a></h2><p>ConfigMap là một đối tượng trong Kubernetes, cho phép bạn lưu trữ các cấu hình và thông tin không nhạy cảm dưới dạng key-value pairs. Điều này giúp tách biệt cấu hình khỏi mã nguồn của ứng dụng, cho phép bạn thay đổi cấu hình mà không cần phải tái biên dịch hoặc tái triển khai ứng dụng.</p><ul><li><strong>Định nghĩa</strong>: ConfigMap là một đối tượng trong Kubernetes, cho phép bạn lưu trữ các cấu hình và thông tin không nhạy cảm dưới dạng key-value pairs. <ul><li>Ví dụ: Bạn có thể lưu trữ các biến môi trường, đường dẫn đến tệp cấu hình, hoặc bất kỳ thông tin nào mà ứng dụng của bạn cần để hoạt động.</li></ul></li><li><strong>Vai trò</strong>: <ul><li>ConfigMap giúp tách biệt cấu hình khỏi mã nguồn của ứng dụng, cho phép bạn thay đổi cấu hình mà không cần phải tái biên dịch hoặc tái triển khai ứng dụng.</li><li>Bạn có thể sử dụng ConfigMap để cung cấp các biến môi trường cho các Pod, hoặc để mount các tệp cấu hình vào trong Pod.</li><li>Điều này giúp quản lý cấu hình dễ dàng hơn và giảm thiểu rủi ro khi thay đổi cấu hình.</li><li>ConfigMap cũng hỗ trợ việc quản lý cấu hình theo môi trường (development, staging, production) một cách dễ dàng. Bạn có thể tạo nhiều ConfigMap khác nhau cho từng môi trường và sử dụng chúng trong các Pod tương ứng.</li></ul></li></ul><h2 id="_8-secret" tabindex="-1">8. Secret <a class="header-anchor" href="#_8-secret" aria-label="Permalink to &quot;8. Secret&quot;">​</a></h2><p>Secret là một đối tượng trong Kubernetes, cho phép bạn lưu trữ các thông tin nhạy cảm như mật khẩu, token, hoặc chứng chỉ một cách an toàn.</p><p>Secret giúp bảo vệ thông tin nhạy cảm khỏi việc bị lộ ra ngoài và cung cấp một cách an toàn để truyền tải thông tin này đến các Pod.</p><ul><li><strong>Định nghĩa</strong>: Secret là một đối tượng trong Kubernetes, cho phép bạn lưu trữ các thông tin nhạy cảm như mật khẩu, token, hoặc chứng chỉ một cách an toàn. <ul><li>Ví dụ: Bạn có thể lưu trữ mật khẩu cơ sở dữ liệu, token API, hoặc chứng chỉ SSL trong Secret.</li><li>Secret được mã hóa và chỉ có thể truy cập bởi các Pod hoặc người dùng có quyền truy cập thích hợp.</li></ul></li><li><strong>Vai trò</strong>: <ul><li>Secret giúp bảo vệ thông tin nhạy cảm khỏi việc bị lộ ra ngoài và cung cấp một cách an toàn để truyền tải thông tin này đến các Pod.</li><li>Bạn có thể sử dụng Secret để cung cấp các biến môi trường cho các Pod, hoặc để mount các tệp cấu hình vào trong Pod.</li><li>Điều này giúp quản lý thông tin nhạy cảm dễ dàng hơn và giảm thiểu rủi ro khi thay đổi thông tin nhạy cảm.</li><li>Secret cũng hỗ trợ việc quản lý thông tin nhạy cảm theo môi trường (development, staging, production) một cách dễ dàng. Bạn có thể tạo nhiều Secret khác nhau cho từng môi trường và sử dụng chúng trong các Pod tương ứng.</li></ul></li></ul><h2 id="_9-volume" tabindex="-1">9. Volume <a class="header-anchor" href="#_9-volume" aria-label="Permalink to &quot;9. Volume&quot;">​</a></h2><p>Volume là một đối tượng trong Kubernetes, cho phép bạn lưu trữ dữ liệu bền vững cho các Pod. Volume giúp đảm bảo rằng dữ liệu không bị mất khi Pod bị xóa hoặc di chuyển.</p><ul><li><strong>Định nghĩa</strong>: Volume là một đối tượng trong Kubernetes, cho phép bạn lưu trữ dữ liệu bền vững cho các Pod. <ul><li>Ví dụ: Bạn có thể sử dụng Volume để lưu trữ dữ liệu của cơ sở dữ liệu, tệp cấu hình, hoặc bất kỳ dữ liệu nào mà ứng dụng của bạn cần để hoạt động.</li></ul></li><li><strong>Vai trò</strong>: <ul><li>Volume giúp đảm bảo rằng dữ liệu không bị mất khi Pod bị xóa hoặc di chuyển. Khi một Pod bị xóa, dữ liệu trong Volume vẫn được giữ nguyên và có thể được sử dụng lại trong các Pod khác.</li><li>Bạn có thể sử dụng Volume để chia sẻ dữ liệu giữa các Pod, hoặc để lưu trữ dữ liệu bền vững cho các ứng dụng cần lưu trữ lâu dài.</li><li>Volume cũng hỗ trợ việc quản lý dữ liệu theo môi trường (development, staging, production) một cách dễ dàng. Bạn có thể tạo nhiều Volume khác nhau cho từng môi trường và sử dụng chúng trong các Pod tương ứng.</li></ul></li></ul>',45)];const r=n(o,[["render",function(n,c,i,o,r,g){return h(),t("div",null,e)}]]);export{i as __pageData,r as default};
